float cot(float x) { return 1.0/tan(x); }
float sqr(float x) { return x*x; }

vec2 werner(float x, float y) {
  float r = length(vec2(x,y)); // Concentric circles
  float theta = atan(x,-y);
  float phi = 0.5*PI - r; // Colatitude is distance from pole
  float lambda = r * theta/cos(phi);
  return vec2(phi,lambda);
}

vec2 bonne(float x, float y) {
  float p = 0.3*PI, cotp = 1.0/tan(p);
  float rho = sqrt(sqr(x) + sqr(cotp - y));
  float phi,lambda;
  if (p > 0.0) {
    phi = cotp + p - rho;
    lambda = rho * atan(x, cotp - y)/cos(phi);
  } else if (p < 0.0) {
    phi = cotp + p + rho;
    lambda = rho * atan(x, y - cotp)/cos(phi);
  } else {
    // Degenerate case - the sinusoidal projection
    phi = y;
    lambda = x/cos(phi);
  }
  return vec2(phi,lambda);
}

vec3 sphere(float phi, float lambda) {
  float y = sin(phi);
  float x = cos(phi)*cos(lambda);
  float z = cos(phi)*sin(lambda);
  return vec3(x,y,z);
}

