vec2 casa01(vec3 p)
{	vec2 res= vec2(9999.0, -1.0);  vec3 p0=p;
     p.y=p.y-12.0;
    float d1a= sdBox( p, vec3(12.0,10.0,5.0) );
    float d1b= sdBox( p-vec3(0.0,-5.0,-5.0), vec3(3.5,5.0,1.0) ); //puerta-marco
    float d1c= sdBox( p-vec3(0.0,-5.25,-5.0), vec3(3.0,5.0,1.5) ); //puerta
    float d1d= sdBox( p-vec3(0.0,-5.25,-5.0), vec3(3.0,5.0,0.5) ); //fondo puerta
    d1a= Difference(d1a, d1c);//hueco casa
    d1b= Difference(d1b, d1c); //puerta
    vec2 ti1 =techoInclinadoA(p);
