vec2 RepetirCasa(vec3 p)
{  vec2 res= vec2(9999.0, -1.0);  vec3 p0=p;
   vec3 pnew;
   vec2 c1,c2,c3;
   for(float i=0.0;i<3.0;i++) 
   {   pnew=p-vec3(0.0,(i-1.0)*25.0,i*15.0);
       c1= opScaleCasa01(pnew,1.0);
