vec2 GetDist(vec3 p  ) 
{	vec2 res= vec2(9999.0, -1.0);  vec3 p0=p;
    //p.x= opRep1D(p.x, 30.0 );
   vec2 r1= RepetirCasa(p);
   res =opU2(res, r1);
