void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.5*4.*8.*8.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    float scale = c1.z;
    float offset = 0.;
    float offset1 = iTime/1000.;
    float t4 = floor((uv.x+uv.y+iTime)/16.);

    for(int i=1;i<4;i++)
    {
        vec2 t2 = vec2(0.);
        vec2 t3 = vec2(0.);
        for(int k = 1; k < 5; k++){

            uv += (t2.yx);
            t2 = triangle_wave(uv.yx-1.5+float(i*k),scale);
            //t2.y -= float(i)/32.;
            #ifdef animate
            float num = 3.+float(k);
            t2 += t4/num;
            #endif
            t3 = triangle_wave(uv,scale);
            //t3 -= float(k)/3.;
            uv.yx = (t2-t3)/(scale);
            col.x = min(((uv.y-uv.x)+col.x),col.x)/sqrt(3.);
            col = abs(col.yzx+vec3(col.x))/sqrt(3.);
            //col = max(col.yzx,abs(col+vec3(col.x))).yzx/sqrt(3.); //another nice color palette
        }

    }
    fragColor = vec4((col*9.),1.0);
}

