// Complex Atanh Made Simple, Matthew Arcus, mla, 2022
// Draw a grid, rotate and transform with atanh.
//
// Complex log, and functions derived from it, like atanh,
// produce results that have discontinuities in the imaginary
// part, jumping between -PI and PI for clog, and -PI/2
// and PI/2 for atanh. To hide these discontinuities, we
// need to ensure that the colouring function produces the
// same value for both these values, which we can do this
// by making it periodic.

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  int A = 2, B = 7; // Rotation angle is atan(B,A)
  float alpha = 1.0;
  if (!key(CHAR_A)) alpha += 0.618*sin(iTime);
  vec2 ar = vec2(alpha,1.0/alpha);
  if (iMouse.x > 0.0) {
    // Get angle from mouse position
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
    m = floor(20.0*m);
    // Ensure A is positive
    if (m.x < 0.0) m = -m;
    A = max(1,int(m.x)), B = int(m.y);
  }
  vec2 rot = 0.5*ar.yx*vec2(float(A),-B);
  float scale = 2.0;
  vec2 z = (2.0*fragCoord-iResolution.xy)/iResolution.y;
  z *= scale;
  if (!key(CHAR_X)) {
    //z = 2.0*catanh(z);
    z = clog(z) + 2.0*catanh(z+vec2(0,1)) + 2.0*catanh(-z+vec2(0,1));
    z /= PI;
  }
  vec2 z0 = z;
  z.y += 0.2*iTime;
  //z.y = mod(z.y,2.0); // Check periodicity - this should have no effect
  float px = fwidth(z.x);
  z = cmul(rot,z); // Transform z to skewed square grid
  px *= length(rot); // Adjust pixel width
  z /= ar;
  ivec2 index = ivec2(floor(z)); // Index of grid square

  // Want color(rot*(x-i)) = color(rot*(x+i)) so:
  // color(rot*x-rot*i) = color(rot*x+rot*i) so:
  // color(z) = color(z+(B+iA)) ie.
  // coloring function should be periodic, period B+iA.
  // Note that we only need to repeat in one direction.
  int k = idiv(index.y,A); // Integer divide, but fixed for -ve values
  index -= k*ivec2(B,A);

  float h = float(ihash(uint(index.x)^ihash(uint(index.y))))/pow(2.0,32.0);
  vec3 col = getcol(h);
  z = fract(z);
  z *= ar;
  z = min(z,ar-z);
  float d = min(z.x,z.y);
  if (!key(CHAR_L)) {
    // Draw a pattern in each cell - just a line for now.
    z -= 0.5*ar;
    d = min(d,abs(z[int(h<0.5)])); // Iverson brackets are cool.
  }
  col *= smoothstep(-px,px,d-0.02);
  float px0 = fwidth(length(z0));
  if (key(CHAR_Z)) {
    // Show borders of repeating area.
    col = mix(vec3(1,0,0),col,smoothstep(-px0,px0,abs(abs(z0.y)-1.0)-0.01));
  }
  fragColor = vec4(col,1);
}

