const int CHAR_A = 65;
const int CHAR_B = 66;
const int CHAR_L = 76;
const int CHAR_X = 88;
const int CHAR_Y = 89;
const int CHAR_Z = 90;

// Macros for use in "common" blocks.
#define key(code) (texelFetch(iChannel3, ivec2((code),2),0).x != 0.0)

const float PI = 3.14159;

vec2 cmul(vec2 z, vec2 w) {
  return mat2(z,-z.y,z.x)*w;
}

vec2 cinv(vec2 z) {
  float t = dot(z,z);
  return vec2(z.x,-z.y)/t;
}

vec2 cdiv(vec2 z, vec2 w) {
  return cmul(z,cinv(w));
}

vec2 clog(vec2 z) {
  float r = length(z);
  return vec2(log(r),atan(z.y,z.x));
}

// Inverse hyperbolic tangent 
vec2 catanh(vec2 z) {
  return 0.5*clog(cdiv(vec2(1,0)+z,vec2(1,0)-z));
}

// From Chris Wellons: https://nullprogram.com/blog/2018/07/31/
uint ihash(uint x) {
    x ^= x >> 16;
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    x ^= x >> 16;
    return x;
}

vec3 h2rgb(float h) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return rgb;
}

int idiv(int a,int b) {
  if (b < 0) { b = -b; a = -a; }
  if (a < 0) return -((-a+b-1)/b); // Careful with bracket here!
  else return a/b;
}

int imod(int a, int b) {
  int d = idiv(a,b);
  return a-d*b;
}

// iq's palette function
vec3 pal( float t, vec3 a, vec3 b, vec3 c, vec3 d ) {
    return a + b*cos( 6.28318*(c*t+d) );
}

vec3 getcol(float t) {
  vec3 rgb = pal(t,vec3(0.8), vec3(0.2), vec3(1), vec3(0,0.1,0.2));
  return rgb;
}

