        for(int k = 0; k < 3; k++){
            //t2.yx *= sign(uv.x-uv.y);

            uv += (t2.yx);
            t2 = triangle_wave(uv.yx-.5,scale);
            //t2.y -= float(i)/4.;
            #ifdef animate
            float num = 3.+float(k);
            t2 += t4/num;
            #endif
