#define PI 3.14159265359
#define E 2.7182818284
#define GR 1.61803398875
#define FAR (10.)
#define TAO (2.*PI)

vec2 Rotate(in vec2 v, float angle) {return v*mat2(cos(angle),sin(angle),-sin(angle),cos(angle));}
vec2 Kaleido(in vec2 v,float power){return Rotate(v,floor(.5+atan(v.x,-v.y)*power/TAO)*TAO/power);}

#define flux(x) (vec3(cos(x),cos(4.0*PI/3.0+x),cos(2.0*PI/3.0+x))*.5+.5)
#define saw(x) fract( sign( 1.- mod( abs(x), 2.) ) * abs(x) )

mat2 rot(float x) {
    return mat2(cos(x), sin(x), -sin(x), cos(x));
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
float smooth_floor(float x) {
    return smoothstep(0.5, 1., fract(x))+floor(x);
}

float dfScene(in vec3 z0){
    float mind = 1E32;
    const float detail = 4.;
    for(float vert = 0.; vert < detail; vert += 1.) {
        for(float horiz = 0.; horiz < detail; horiz += 1.) {
            z0.xy=Kaleido(z0.xy, 2.+saw(iTime/PI/E)*4.);
            z0.yz=Rotate(z0.yz, (.25+1.5*saw(iTime/GR/PI))*PI);
            float d = sdBox(z0, vec3(.125));
            z0.y -= .125;
            if(d < mind) mind = d;
        }   
    }

	return mind;
}

vec3 surfaceNormal(vec3 p) { 
    vec2 e = vec2(5.0 / 1024., 0);
	float d1 = dfScene(p + e.xyy), d2 = dfScene(p - e.xyy);
	float d3 = dfScene(p + e.yxy), d4 = dfScene(p - e.yxy);
	float d5 = dfScene(p + e.yyx), d6 = dfScene(p - e.yyx);
	float d = dfScene(p) * 2.0;	
    return normalize(vec3(d1 - d2, d3 - d4, d5 - d6));
}

//main march
vec4 marchScene(vec2 uv) {

    float movement = iTime/PI;
    //camera
    vec3 rd = normalize(vec3(uv, -1./GR));
    vec3 ro = vec3(0.0, 0., 2.);

    //rotate camera
    rd.xz *= rot(-movement);
    ro.xz *= rot(-movement);
    ro += rd/512.;

    float d = 0.0; //distance marched
    vec3 rp = vec3(0.0); //ray position
   	float ns;
    vec3 p;
    for (int i = 0; i <16; i++) {
        rp = ro + rd * d;
        p = rp;
        ns = dfScene(p);
        d += ns;
        if(d > FAR)
        {
            break;
        }

    }
    float fog = smoothstep(.75, .9, d/FAR);
    vec3 n = surfaceNormal(p);
    return vec4(n, 1.)*(1.-fog);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //coordinate system
    vec2 uv = fragCoord.xy / iResolution.xy;

    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec4 t1 = marchScene(uv);
    fragColor = t1;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = vec2(
        fragCoord.x * cos(0.3) - fragCoord.y * sin(0.3),
        fragCoord.x * sin(0.3) + fragCoord.y * cos(0.3)
    );

    float x = mod(uv.x * 0.14, 2.0) / 2.0;
    float y = mod(uv.y * 0.14, 2.0) / 2.0;


    fragColor = texture(iChannel0, fragCoord/iResolution.xy) *
        (1.0 - abs(0.5 - x) * abs(0.5 - y));
}

/*settings*/
#define AA 1
