#define pi 3.14159265
#define tau pi * 2.0

//random
float hash(float v){return fract(sin(v*dot(vec2(737.873,777.873),vec2(173.873,515.526)))*82723.782);}
float hash(vec2 uv){return fract(sin(dot(uv,vec2(173.873,515.526)))*82723.782);}
float hash(vec3 uv){return fract(sin(dot(uv,vec3(173.873,515.526,726.873)))*82723.782);}

//noise 1d
float noise(float a){
	float f = fract(a);
	float h = f*f*(3.0-2.0*f);
	return mix(hash(a),hash(a + 1.0),h);
}

//noise 2d
float noise(vec2 uv){
	vec2 e = vec2(0.0,1.0);
	vec2 p = floor(uv);
	vec2 f = smoothstep(0.0,1.0,fract(uv));
	return mix(
		mix(hash(p + e.xx),hash(p + e.yx),f.x),
		mix(hash(p + e.xy),hash(p + e.yy),f.x),
		f.y
	);
}

//noise 3d
float noise(vec3 uv3d){
	vec2 e = vec2(0.0,1.0);
	vec3 s = vec3(110.0,241.0,171.0);
	vec3 i = floor(uv3d);
	vec3 f = fract(uv3d);
	float n = dot(i,s);
	vec3 u = f * f * (3.0 - 2.0 * f);
	float x1 = mix(hash(n + dot(s,e.xxx)),hash(n + dot(s,e.yxx)),u.x);
	float x2 = mix(hash(n + dot(s,e.xyx)),hash(n + dot(s,e.yyx)),u.x);
  float y1 = mix(hash(n + dot(s,e.xxy)),hash(n + dot(s,e.yxy)),u.x);
  float y2 = mix(hash(n + dot(s,e.xyy)),hash(n + dot(s,e.yyy)),u.x);
	return mix(mix(x1,x2,u.y),mix(y1,y2,u.y),u.z);
}

//clamp zero one
#define czo(a) clamp(a,0.0,1.0)

//integer mix
#define imix(a,b,k) mix(float(a),float(b),k)

//vector min
float vmin(vec2 a){return min(a.x,a.y);}
float vmin(vec3 a){return min(min(a.x,a.y),a.z);}
float vmin(vec4 a){return min(min(a.x,a.y),min(a.z,a.w));}

//vector max
float vmax(vec2 a){return max(a.x,a.y);}
float vmax(vec3 a){return max(max(a.x,a.y),a.z);}
float vmax(vec4 a){return max(max(a.x,a.y),max(a.z,a.w));}

//square
float sq(float a){return pow(a,2.0);}
vec2 sq(vec2 a){return pow(a,vec2(2.0));}
vec3 sq(vec3 a){return pow(a,vec3(2.0));}
vec4 sq(vec4 a){return pow(a,vec4(2.0));}

//length square
//length square
#define lsq(a) dot(a,a)

//smooth min
float smin(float a,float b,float k){
	float h = czo(0.5 + 0.5*(b-a)/k);
	return mix(b,a,h) - k*h*(1.0-h);
}

//smooth max
#define smax(a,b,k) -smin(-a,-b,k)

struct Object{
  float f;
  vec3 p;
	float id;
	vec3 col;
};

//3d sdf primitives
Object fSphere(float id,vec3 uv3d,float rad,vec3 col){
	float sphere = length(uv3d) - rad;
	return Object(sphere,uv3d,id,col);
}
Object fEllipsoid(float id,vec3 uv3d,vec3 rad,vec3 col){
  float p1 = length(uv3d/rad);
  float p2 = length(uv3d/sq(rad));
  float elli = p1*(p1-1.0)/p2;
	return Object(elli,uv3d,id,col);
}
Object fCube(float id,vec3 uv3d,float size,vec3 col){
	vec3 p = abs(uv3d) - size;
	float cube = length(max(p,0.0)) + min(vmax(p),0.0);
	return Object(cube,uv3d,id,col);
}
Object fBox(float id,vec3 uv3d,vec3 size,vec3 col){
	vec3 p = abs(uv3d) - size;
	float box = length(max(p,0.0)) + min(vmax(p),0.0);
	return Object(box,uv3d,id,col);
}
Object fBoxInfinity(float id,vec3 uv3d,vec2 size,vec3 col){
	vec2 p = abs(uv3d.xy) - size;
	float box = length(max(p,0.0)) + min(vmax(p),0.0);
	return Object(box,uv3d,id,col);
}
Object fTorus(float id,vec3 uv3d,vec2 t,vec3 col){
	float torus = length(vec2(length(uv3d.xz)-t.x,uv3d.y))-t.y;;
	return Object(torus,uv3d,id,col);
}
Object fCylinder(float id,vec3 uv3d,vec2 h,vec3 col){
  vec2 p = abs(vec2(length(uv3d.xz),uv3d.y)) - h;
  float cylin = min(max(p.x,p.y),0.0) + length(max(p,0.0));
	return Object(cylin,uv3d,id,col);
}
Object fLine(float id,vec3 uv3d,float rad,vec3 v1,vec3 v2,vec3 col){
	vec3 a = uv3d-v1,b = v2-v1;
	float line = length(a - b*czo(dot(a,b)/lsq(b))) - rad;
	return Object(line,uv3d,id,col);
}
Object fConeRound(float id,vec3 uv3d,float d,float r1,float r2,vec3 col){
  vec2 p = vec2(length(uv3d.xz),uv3d.y);

  float b = (r1-r2)/d;
  float a = sqrt(1.0-sq(b));
  float k = dot(p,vec2(-b,a));

  float cone;
  if(k < 0.0) cone = length(p) - r1;
  else if(k > a*d) cone = length(p-vec2(0.0,d)) - r2;
  else cone = dot(p,vec2(a,b)) - r1;

	return Object(cone,uv3d,id,col);
}
Object fPlane(float id,vec3 uv3d,float d,vec3 n,vec3 col){
	float plane = dot(uv3d,n) - (-d);
	return Object(plane,uv3d,id,col);
}

//3d sdf operations
//union
Object fOpUnion(Object a,Object b){
  if(a.f < b.f) return a;
  else return b;
}
Object fOpUnionSmooth(Object a,Object b,float k,float div){
  Object res;
  float h = czo(0.5 + 0.5*(a.f-b.f)/k);
  res.f = smin(a.f,b.f,k);
  res.col = mix(a.col,b.col,czo(0.5+0.5*(a.f-b.f)/k));
  res.id = mix(a.id,b.id,h) < div ? a.id : b.id;
  res.p = mix(a.p,b.p,h) == a.p ? a.p : b.p;
	return res;
}
//intersection
Object fOpIntersection(Object a,Object b){
  if(a.f > b.f) return a;
  else return b;
}
Object fOpIntersectionSmooth(Object a,Object b,float k,float div){
  Object res;
  float h = czo(0.5 - 0.5*(b.f-a.f)/k);
  res.f = smax(a.f,b.f,k);
  res.col = mix(a.col,b.col,czo(0.5+0.5*(a.f-b.f)/k));
  res.id = mix(a.id,b.id,h) < div ? a.id : b.id;
  res.p = mix(a.p,b.p,h) == a.p ? a.p : b.p;
	return res;
}
//difference
Object fOpDifference(Object a,Object b){
	a.f = -a.f;
	return fOpIntersection(a,b);
}
Object fOpDifferenceSmooth(Object a,Object b,float k,float div){
  Object res;
  float h = czo(0.5 - 0.5*(a.f+b.f)/k);
  res.f = smax(-a.f,b.f,k);
  res.col = mix(a.col,b.col,czo(0.5+0.5*(a.f-b.f)/k));
  res.id = mix(b.id,a.id,h) < div ? a.id : b.id;
  res.p = mix(b.p,a.p,h) == a.p ? a.p : b.p;
	return res;
}

//rotation matrices
mat3 rotateX(float theta){
	float s = sin(theta);
	float c = cos(theta);
	return mat3(
		vec3(1,0,0),
		vec3(0,c,-s),
		vec3(0,s,c)
	);
}
mat3 rotateY(float theta){
	float s = sin(theta);
	float c = cos(theta);
	return mat3(
		vec3(c,0,s),
		vec3(0,1,0),
		vec3(-s,0,c)
	);
}
mat3 rotateZ(float theta){
	float s = sin(theta);
	float c = cos(theta);
	return mat3(
		vec3(c,-s,0),
		vec3(s,c,0),
		vec3(0,0,1)
	);
}

/*texture mapping algorithms*/
//triplanar mapping
vec3 fTextureTP(sampler2D tex,vec3 uv3d,vec3 normal){
  normal = uv3d;
  normal = abs(normal);
  normal = pow(normal,vec3(5.0));
  normal /= normal.x + normal.y + normal.z;
  return
  texture(tex,uv3d.xy + 0.5).rgb * normal.z +
  texture(tex,uv3d.xz + 0.5).rgb * normal.y +
  texture(tex,uv3d.yz + 0.5).rgb * normal.x;
}
