}

//background
vec3 bgCol(){return vec3(0.2,0.6,1.0);}
vec3 mist(){return vec3(0.85,0.9,1.0);}
vec3 background(vec3 rd){
	vec3 bg = vec3(0.0);

  bg += bgCol() - 0.7*rd.y;
