Object raymarch(vec3 ro,vec3 rd){
	float dist = MIN_DIST;
	Object hit;

	for(int i=0;i<STEPS;i++){
		vec3 uv3d = ro + dist*rd;
		hit = scene(uv3d);
		if(abs(hit.f) < EPSILON || dist > MAX_DIST) break;
		dist += hit.f;
	}

	return Object(dist,hit.p,hit.id,hit.col);
}

float getShadow(vec3 ro,vec3 rd){
	float start = 0.0, end = 3.0;
	float k = 10.0;
	float shadow = 1.0;

	for(float d=start;d<end;){
		float hit = scene(ro + d*rd).f;

		if(hit < 0.001) return 0.0;
		shadow = min(shadow,k*hit/d);

		d += hit;
	}

	return shadow;
}

float getAmbOcc(vec3 ro,vec3 rd){
  float occ = 0.0;
  float w = 1.0;

  for(float i=0.0;i<8.0;i++){
  	float len = 0.1 + 0.01*sq(i);
  	float dist = scene(ro + len*rd).f;
  	occ += (len - dist) * w;
  	w *= 0.85;
  }

  return 1.0 - czo(0.5 * occ);
}

vec3 getNormal(vec3 uv3d){
	vec2 e = vec2(1.0,-1.0) * EPSILON;
	return normalize(vec3(
		e.xyy * scene(uv3d + e.xyy).f +
		e.yyx * scene(uv3d + e.yyx).f +
		e.yxy * scene(uv3d + e.yxy).f +
		e.xxx * scene(uv3d + e.xxx).f
	));
}

vec3 getMaterial(Object obj,vec3 normal){
	vec3 m = vec3(0.0);

  switch(int(obj.id)){
  	default:
  	  m += vec3(1.0);
  	  break;

  	case -1:
  	  m += obj.col;
  	  break;

  	case 0:
  	  m += fTextureTP(iChannel1,obj.p,normal);
  	  break;
  	case 1:
  	  m += texture(iChannel0,obj.p.zx).rgb;
  	  break;
  }

	return m;
}

struct Light{
	vec3 pos;
	vec3 col;
	vec3 add;
};
Light light(vec3 uv3d,vec3 ro,vec3 rd,Object obj,vec3 pos,vec3 col){
  Light res;
	res.pos = pos;
	res.col = col;

  vec3 L = normalize(pos - uv3d);
  vec3 N = getNormal(uv3d);
  vec3 R = reflect(-L,N);
  vec3 V = -rd;

  vec3 color = getMaterial(obj,N);
	vec3 bg = background(rd);

  //phong
  vec3 speCol = vec3(1.0,1.0,1.0) * 0.5;
  vec3 ambient = color * 0.05;
  vec3 diffuse = color * czo(dot(L,N));
  vec3 specular = speCol * pow(czo(dot(R,V)),10.0);

  vec3 fresnel = 0.25 * color * pow(1.0 + dot(rd,N),3.0);

  //shadow
  float shadow = getShadow(uv3d+N*0.02,normalize(pos));
  //float shadow = raymarch(uv3d+N*0.02,normalize(pos)).f;
  //length(L)
  //if(shadow < MAX_DIST && obj.id != Light_ID)
  //  return Light(pos,col,ambient + fresnel);

  //ambient occlusion
  float occ = getAmbOcc(uv3d,N);

  //back
  vec3 back = 0.05 * color * czo(dot(N,-L));

  //res.add = pow(abs(N),vec3(5.0));
  //res.add = back;
  //res.add = 0.9 * vec3(1.0) * occ;
  res.add = (back + ambient + fresnel) * occ + (diffuse * occ + specular) * shadow;
  //res.add = (ambient + fresnel) * occ + (diffuse * occ + specular) * shadow;
  //res.add = ambient + (diffuse + specular) * shadow;
  //res.add = ambient + diffuse + specular;

	return res;
}

mat3 lookAt(vec3 ro,vec3 la){
	vec3 cf = normalize(ro - la);
	vec3 cr = normalize(cross(vec3(0,1,0),cf));
	vec3 cu = normalize(cross(cf,cr));

	return mat3(cr,cu,cf);
}

vec2 getUV(vec2 offset,vec2 fragCoord){
	return (2.0 * (fragCoord - offset) - iResolution.xy)/iResolution.y;
}

vec3 render(vec2 uv){
  vec2 mouse = (iMouse.xy-0.5*iResolution.xy)/iResolution.y;
  vec3 col = vec3(0.0);

  //camera
  float fov = 1.0;
  vec3 la = vec3(0.0,0.0,0.0);
  vec3 ro = vec3(0.0,0.5,3.0);

  ro *= rotateX(mouse.y * pi * 0.5 - 0.5);
  ro *= rotateY(-mouse.x * tau * 2.0 + 1.0);

  vec3 rd = lookAt(ro,la) * normalize(vec3(uv,-fov));

  Object obj = raymarch(ro,rd);
  {
    vec3 uv3d = ro + obj.f*rd;
    vec3 normal = getNormal(uv3d);

    vec3 bg = background(rd);
    vec3 material = vec3(0.0);

	  Light sun = light(uv3d,ro,rd,obj,
  	  vec3(30.0,40.0,-30.0),
	    vec3(1.0,1.0,1.0)
  	);
    material  += sun.add;

    if(obj.f < MAX_DIST){
      col += material;

      //gamma correction
      col = pow(col,vec3(1.0/2.2));
      //mist
      col = mix(col,mist(),1.0-exp(-1e-5*pow(obj.f,3.0)));
    }else{
    	col = bg;
    }
  }

  return col;
}

void renderAAx4(inout vec3 color,vec2 c){
	vec4 e = vec4(0.125,-0.125,0.375,-0.375);
	vec3 colAA = render(getUV(e.xz,c)) + render(getUV(e.yw,c)) + render(getUV(e.wx,c)) + render(getUV(e.zy,c));
	color += colAA / 4.0;
}
void renderAAx1(inout vec3 color,vec2 c){
	color += render(getUV(vec2(0.0),c));
}

void mainImage(out vec4 fragColor,in vec2 fragCoord){
  vec3 col = vec3(0.0);

  if(AA == 1) renderAAx1(col,fragCoord);
  else if(AA == 4) renderAAx4(col,fragCoord);

  fragColor = vec4(col,1.0);
}

