	d2 = h.x - 0.06;
	res.x = smin( res.x, d2, 0.51 );
	}

    // feet
	if( p.y<-0.5 )
	{
	h = sdSegment( p, vec3(0.5,-1.8,0.0), vec3(0.6,-1.8,0.4) );
	d2 = h.x - 0.09 + 0.02*h.y;
	res.x = smin( res.x, d2, 0.06 );
	h = sdSegment( p, vec3(0.5,-1.8,0.0), vec3(0.77,-1.8,0.35) );
	d2 = h.x - 0.1 + 0.1*h.y;
	res.x = smin( res.x, d2, 0.06 );
	h = sdSegment( p, vec3(0.5,-1.8,0.0), vec3(0.9,-1.8,0.2) );
	d2 = h.x - 0.07 + 0.02*h.y;
	res.x = smin( res.x, d2, 0.06 );
	}

	// horns
	vec3 hp = p - vec3(1,0.8,0.0);
    hp.xy = mat2(0.6,0.8,-0.8,0.6)*hp.xy;
    hp.x += 0.8*hp.y*hp.y;
    float d4 = sdEllipsoid( hp, vec3(0.13,0.5,0.16) );
	if( d4<res.x ) res = vec2( d4, 3.0 );

	// eyes
	float d3 = length( (p - vec3(0.0,0.5,0.8))*vec3(1.0,0.8,1.0) ) - 0.5;
	if( d3<res.x ) res = vec2( d3, 2.0 );

    //float d03 = length( (p - vec3(0.0,0.1,0.5))*vec3(1.0,0.8,1.0) ) - 0.5;
	//if( d03<res.x ) res = vec2( d03, 2.0 );

	// mouth
	float mo = length( (q-vec3(0.0,-0.5,1.4))*vec3(1.0,0.9,-0.01)/1.2 ) -0.3/1.2;
	float of = 0.1*pow(smoothstep( 0.0, 0.8, abs(p.x-0.3) ),0.5);
	mo = max( mo, -q.y-0.35-of );

	float li = smoothstep( 0.0, 0.05, mo+0.02 ) - smoothstep( 0.05, 0.10, mo+0.02 );
	res.x -= 0.03*li*clamp( (-q.y-0.4)*10.0, 0.0, 1.0 );

	if( -mo > res.x )
		res = vec2( -mo, 4.0 );

    res.x += 0.01*(smoothstep( 0.0, 0.05, mo+0.062 ) - smoothstep( 0.05, 0.10, mo+0.062 ));

    // teeth	
    if( p.x<0.3 )
	{
    p.x = mod( p.x, 0.16 )-0.08;	
    float d5 = length( (p-vec3(0.0,-0.45,0.65))*vec3(1.5,0.7,1.5))-0.07;
	if( d5<res.x )
		res = vec2( d5, 4.0 );
	}

    if( p.x<0.3 )
	{
    p.x = mod( p.x, 0.16 )-0.08;	
    float d5 = length( (p-vec3(0.0,-0.23,0.79))*vec3(1.5,0.75,1.5))-0.08;
	if( d5<res.x )
		res = vec2( d5, 4.0 );
	}

	return vec2(res.x*0.8,res.y);
}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal( in vec3 pos )
{
#if 0    
    vec3 eps = vec3(0.002,0.0,0.0);
	return normalize( vec3(
           map(pos+eps.xyy).x - map(pos-eps.xyy).x,
           map(pos+eps.yxy).x - map(pos-eps.yxy).x,
           map(pos+eps.yyx).x - map(pos-eps.yyx).x ) );
#else
    // inspired by tdhooper and klems - a way to prevent the compiler from inlining map() 4 times
    vec3 n = vec3(0.0);
    for( int i=ZERO; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*map(pos+0.002*e).x;
    }
    return normalize(n);
