    c.y=float(e.x^e.y)/512.;

    // Pattern smoothing - this isnt exactly correct, slight change of brightness on every loop, but it works
    for(int j=0;j<9;++j,i*=2.)
        c.y*=.8+.2*s i.x))*s-i.x))*s i.y))*s-i.y));

    // Swap lines if you have problems viewing this shader:

    // Multiply with time & square view
    o+=c.y*c.x*step(abs(u.x-.5),.5);

    // ---

    //o=vec4(c.y*c.x*step(abs(u.x-.5),.5));
}

*/

Main {
    U = 2.*(U-.5*R)/R.y;
    Q = vec4(0);
    float d = 1e9; 
    U.y -= -1.;
    float r = 0.;
    for (float i = 0.; i < 15.; i++) {

        float s = segment(U,vec2(0),vec2(0,.6));
        d = min(d,s);
        U.y -= .6;
        float h = -.5+hash(vec2(i,1+iFrame)).x;
        h = sign(h);
        r += h*exp2(i);
        vec3 f = hash(vec2(r));
        U *= 1.1+.5*f.y;
        if (h!=0.)
        U.x = h*U.x;
        U.xy *= ei(-1.+.5*f.x);
    }
    vec3 hh = hash(vec2(r));
    Q = step(d,.05)*.15*vec4(4,3,2,1);
    U.y -= -.5;
    U *= .3;
    U = vec2(atan(U.y,U.x),length(U));
    U.x += .1*fbm(vec2(10.*U.y,round(U.x/5.))).x;
    U.y += (.5+.5*abs(sin(5.*U.x)))*-sin(U.x);
    U.y += .5*fbm(r+10.*U.xx).x;
    if (U.y<.5) Q = sin(.5*(hh.x*2.-1.)+.5+U.y+vec4(1,2,3,4));
    if (length(Q)==0.) discard;
}

