#define FREQ 30.0
#define SPD 0.05

vec2 skew(float x, float y){
    return vec2(1.1547 * x, 0.5 * x + y);
}

vec2 unskew(float x, float y){
    return vec2(0.86602 * x , y - 0.43302 * x);
}

bool inWave(vec2 p){
    float d = length(p);
    return sin((d - iTime * SPD) * FREQ) > 0.7;
}

float paint(vec2 pos){
    // if(inWave(pos)) return 1.0;

    float sidelen = 1.0 / (10.0 + iTime * (3.0 + iTime / 100.0) * 2.0);
    vec2 pos2 = skew(pos.x, pos.y) / sidelen;

    float ix = floor(pos2.x) * sidelen;
    float iy = floor(pos2.y) * sidelen;
    float cx = ceil(pos2.x) * sidelen;
    float cy = ceil(pos2.y) * sidelen;
    float fx = fract(pos2.x);
    float fy = fract(pos2.y);

    vec2 p1 = unskew(ix, iy);
    vec2 p2 = unskew(cx, cy);

    //NOTE: This is probably a bad idea.
    //      But it'll all be sorted out by the compiler and hardware optimizations, so don't worry.
    float s = step(fy - fx, 0.0);
    vec2 p3 = unskew(ix * (1.0 - s) + cx * s,
                     cy * (1.0 - s) + iy * s);

    return pow((inWave(p1) ? 0.5 : 0.0)
               + (inWave(p2) ? 0.5 : 0.0)
               + (inWave(p3) ? 0.5 : 0.0),
               1.7);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 norm = (fragCoord - iResolution.xy * 0.5) / min(iResolution.x, iResolution.y);
    fragColor.rgb = vec3(1.0 - paint(norm));
}

