// "Random Slabs" by dr2 - 2022
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

/*
  Based on "Lattice Percolation", with the random bonds in a cubic lattice
  replaced by thin perpendicular slabs, all bounded by a sphere; pattern
  changes periodically.
*/

#if 1
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

float PrRoundBoxDf (vec3 p, vec3 b, float r);
float Minv3 (vec3 p);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
float Hashfv3 (vec3 p);

vec3 ltDir[4], ltCol[4], cId;
float tCur, dstFar, pVal, cMax, rSeed;
bool cOcc, cOccN[6];
const float pi = 3.1415927;

bool IsOcc (vec3 c)
{
  return (length (c + 0.5) < cMax && pVal + 0.5 * (Hashfv3 (c + rSeed + vec3 (0.21, 0.31, 0.41)) +
     Hashfv3 (c.yzx + rSeed + vec3 (0.21, 0.31, 0.41))) > 1.);
}

void SetConf ()
{
  vec2 dc;
  cOcc = IsOcc (cId);
  dc = vec2 (1., 0.);
  for (int k = 0; k < 2; k ++) {
    dc.x = - dc.x;
    cOccN[3 * k + 0] = IsOcc (cId + dc.xyy);
    cOccN[3 * k + 1] = IsOcc (cId + dc.yxy);
    cOccN[3 * k + 2] = IsOcc (cId + dc.yyx);
  }
}

float ObjDf (vec3 p)
{
  vec2 sz, dc;
  float d, bl;
  d = dstFar;
  bl = 0.5;
  sz = vec2 (0.05, 0.99) * bl;
  if (cOcc) {
    p -= cId + 0.5;
    dc = vec2 (1., 0.);
    for (int k = 0; k < 2; k ++) {
      dc.x = - dc.x;
      if (cOccN[3 * k + 0]) d = min (d, PrRoundBoxDf (p - dc.xyy * bl, sz.xyy, 0.01 * bl));
      if (cOccN[3 * k + 1]) d = min (d, PrRoundBoxDf (p - dc.yxy * bl, sz.yxy, 0.01 * bl));
      if (cOccN[3 * k + 2]) d = min (d, PrRoundBoxDf (p - dc.yyx * bl, sz.yyx, 0.01 * bl));
    }
  }
  return d;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p, cIdP, s, rdi;
  float dHit, d, eps;
  eps = 0.0005;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  rdi = 1. / rd;
  cIdP = vec3 (-999.);
  dHit = eps;
  for (int j = VAR_ZERO; j < 160; j ++) {
    p = ro + dHit * rd;
    cId = floor (p);
    if (cId != cIdP) {
      SetConf ();
      cIdP = cId;
    }
    d = ObjDf (p);
    s = (cId + step (0., rd) - p) * rdi;
    d = min (d, abs (Minv3 (s)) + eps);
    dHit += d;
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec4 ObjNfL (vec3 p)
{
  vec4 v;
  vec2 e;
  float s;
  e = vec2 (0.005, -0.005);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  s = dot (v, vec4 (1.));
  return vec4 (normalize (2. * v.yzw - s), (s - 2. * v.x - 4. * ObjDf (p)) / (e.x * e.x));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, vn4;
  vec3 col, vn, sumD, sumS;
  float dstObj, nDotL, att;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn4 = ObjNfL (ro);
    vn = vn4.xyz;
    col4 = vec4 (1., 1., 1., 0.2) * (1. - 0.5 * smoothstep (4., 8., abs (vn4.w))) *
       (0.1 + 0.9 * length (cId + 0.5) / cMax);
    sumD = vec3 (0.);
    sumS = vec3 (0.);
    for (int k = VAR_ZERO; k < 4; k ++) {
      nDotL = max (dot (vn, ltDir[k]), 0.);
      att = smoothstep (0.85, 0.95, dot (normalize (50. * ltDir[k] - ro), ltDir[k]));
      sumD += ltCol[k] * att * col4.rgb * nDotL * nDotL;
      sumS += ltCol[k] * att * col4.a * pow (max (0., dot (ltDir[k], reflect (rd, vn))), 32.);
    }
    col = sumD + sumS;
  } else col = vec3 (0.1);
  return clamp (col, 0., 1.);
}

#define AA  1

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, e;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  cMax = 16.;
  pVal = 0.45;
  rSeed = 11.111 * floor (tCur / 30.);
  az = 0.15 * pi;
  el = -0.2 * pi;
  zmFac = 2.4;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
    zmFac += 2. * abs (az - 0.15 * pi) * smoothstep (0.2, 0.5, abs (az - 0.15 * pi));
  } else {
    az += 0.2 * pi * sin (0.01 * pi * tCur);
    el += 0.1 * pi * sin (0.007 * pi * tCur);
  }
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -45.);
  e = vec2 (1., -1.);
  for (int k = VAR_ZERO; k < 4; k ++) {
    ltDir[k] = normalize ((k < 2) ? ((k == 0) ? e.xxx : e.xyy) : ((k == 2) ? e.yxy : e.yyx));
    ltDir[k].xy = Rot2D (ltDir[k].xy, 0.13 * pi * tCur);
    ltDir[k].xz = Rot2D (ltDir[k].xz, 0.17 * pi * tCur);
  }
  ltCol[0] = vec3 (1., 0.1, 0.1);
  ltCol[1] = ltCol[0].gbr;
  ltCol[2] = ltCol[0].brg;
  ltCol[3] = 0.8 * ltCol[0].rrg;
  dstFar = 80.;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

float Hashfv3 (vec3 p)
{
  return fract (sin (dot (p, vec3 (37., 39., 41.))) * cHashM);
}

////////////////////////////////////////////////////////////////////////////////
//
// Parabolic Torus
// Another nice quartic solid of revolution - a parabolic torus.
//
// <mouse>: change view direction
// <up/down>: zoom
// <left/right>: torus radius
// 'a': antialiasing
// 'r': autorotation
//
////////////////////////////////////////////////////////////////////////////////

const float PI =  3.141592654;

// Lighting
vec3 light = vec3(1,1,-1);
float ambient = 0.4;
float diffuse = 0.6;
float specular = 0.8;
float specularpow = 10.0;
vec3 specularcolor = vec3(1);

float torusr = 1.0;

int paratorus (vec3 p, vec3 d, out vec4 roots) {
  float r = torusr;
  float r2 = r*r;
  float x0 = p.x, y0 = p.y, z0 = p.z;
  //if (abs(z0) < 1e-2) z0 = sgn(z0)*1e-2;
  float x02 = x0*x0, x03 = x02*x0, x04 = x02*x02;
  float y02 = y0*y0, y03 = y02*y0, y04 = y02*y02;
  float z02 = z0*z0, z03 = z02*z0, z04 = z02*z02;
  float x1 = d.x, y1 = d.y, z1 = d.z;
  float x12 = x1*x1, x13 = x12*x1, x14 = x12*x12;
  float y12 = y1*y1, y13 = y12*y1, y14 = y12*y12;
  float z12 = z1*z1, z13 = z12*z1, z14 = z12*z12;
  float A = z14;
  float B = 4.0*z0*z13;
  float C = 6.0*z02*z12 + 2.0*r*z12 - x12 - y12;
  float D = 2.0*(2.0*z03*z1 + 2.0*r*z0*z1 - x0*x1 - y0*y1);
  float E = z04 + 2.0*r*z02 + r2 - x02 - y02;
  return quartic(A,B,C,D,E,roots);
}

vec3 paratorusnormal(vec3 p) {
  //return z4 + 2.0*r*z2 + r2 - x2 - y2;
  float r = torusr; //sin(iTime);
  float x = p.x, y = p.y, z = p.z;
  return vec3(-2.0*x,-2.0*y,4.0*z*(z*z+r));
}

int dosurface(vec3 p0, vec3 r, out vec4 roots) {
  return paratorus(p0,r,roots);
}

vec3 donormal(vec3 p) {
  return paratorusnormal(p);
}

int solve(vec3 p0, vec3 r, out vec4 roots) {
  int nroots = dosurface(p0,r,roots);
  // Sort the roots
  if (nroots >= 2) {
    if (roots.y < roots.x) roots.xy = roots.yx;
  }
  if (nroots >= 3) {
    if (roots.z < roots.y) roots.yz = roots.zy;
    if (roots.y < roots.x) roots.xy = roots.yx;
  }
  if (nroots >= 4) {
    if (roots.w < roots.z) roots.zw = roots.wz;
    if (roots.z < roots.y) roots.yz = roots.zy;
    if (roots.y < roots.x) roots.xy = roots.yx;
  }
  return nroots;
}

vec3 applylighting(vec3 baseColor, vec3 p, vec3 n, vec3 r) {
  if (dot(r,n) > 0.0) n = -n; // Face forwards
  vec3 c = baseColor*ambient;
  c += baseColor*diffuse*(max(0.0,dot(light,n)));
  float s = pow(max(0.0,dot(reflect(light,n),r)),specularpow);
  c += specular*s*specularcolor;
  return c;
}

float gridline(vec3 p) {
  // Draw some gridlines on surface
  vec3 t = fract(p*4.0);
  t = min(t,1.0-t);
  float d = min(t.x,min(t.y,t.z));
  return smoothstep(0.02,0.025,d);
}

vec3 scene(vec3 p0, vec3 r) {
  float time = iTime;
  float a = 0.3333+ max(0.0,3.0*(1.0-cos(0.1*time)));
  float b = (3.0*a-1.0)/(3.0-a);
  vec4 params = vec4(a,b,0,0);
  vec4 roots;
  vec3 color = vec3(0);
  int nroots = solve(p0,r,roots);
  float rwidth = length(fwidth(r));
  for (int i = nroots-1; i >= 0; i--) {
    float t = roots[i];
    if (t < 0.0) break;
    vec3 p = p0+t*r;
    vec3 n = donormal(p);
    if (dot(n,r) > 0.0) n = -n;
    n = normalize(n);
    if (isnan(length(n))) continue;
    float k = 0.0;
    k += 2.0*iTime;
    k += 0.5*atan(p.x,p.y)/PI; // Hum!
    //if (length(p.xy) > torusr) k = -k;
    k += 10.0*p.z;
    vec3 basecolor = pow(abs(n.bgr),vec3(2.0));
    basecolor = h2rgb(1.5*atan(p.z*p.z)/PI);
    float dk = fwidth(k);
    //assert(fwidth(k) < 0.5);
    //if (abs(k-round(k)) < 0.1) basecolor = vec3(0);
    basecolor = mix(vec3(0),basecolor,smoothstep(0.0,5.0*rwidth*t/abs(dot(n,r)),abs(k-round(k)) - 0.1));
    //if (abs(length(p)-sqrt(a)) < 0.01) basecolor = vec3(1,0,0);
    //if (min(abs(p.x),min(abs(p.y),abs(p.z))) < 0.01) basecolor = vec3(0.5);
    vec3 c = applylighting(basecolor,p,n,r);
    float fog = 1.0-smoothstep(0.0,200.0,t);
    c *= fog;
    color = mix(color,c,0.5);
  }
  return color;
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  if (!key(CHAR_R)) {
    float t = iTime;
    p.yz = rotate(p.yz, 0.1*t);
    p.zx = rotate(p.zx, 0.222*t);
  }
  return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  float scale = 1.0;
  float camera = 4.0;
  camera *= 0.1*float(10+keycount(KEY_DOWN)-keycount(KEY_UP));
  torusr = 0.1*float(keycount(KEY_RIGHT)-keycount(KEY_LEFT));

  vec3 p = vec3(0,0,-camera);
  p = transform(p);
  light = transform(light);
  light = normalize(light);
  int AA = 1;
  if (key(CHAR_A)) AA = 2;
  vec3 color = vec3(0);
  for (int i = 0; i < AA; i++) {
    for (int j = 0; j < AA; j++) {
      vec2 uv = scale*(2.0*(fragCoord.xy+vec2(i,j)/float(AA)) - iResolution.xy)/iResolution.y;
      vec3 r = vec3(uv, 2.0);
      r = transform(r);
      r = normalize(r);
      color += scene(p,r);
    }
  }
  color /= float(AA*AA);
  color /= 1.0+color;
  color = pow(color,vec3(0.4545));
  if (alert) color.x = 1.0;
  fragColor = vec4(color,1);
}

