void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    vec2  p  = 4.0*fragCoord/iResolution.x;
    float px = 4.0/iResolution.x;

    float al = smoothstep(-0.1,0.1,sin(6.283185*iTime));

    vec3 col = vec3( 0.1 + 0.05*mod(floor(p.x)+floor(p.y),2.0) );

    // hardware pow()
    {
    float y = pow( p.x, 1.0/3.0 );
    float d = abs(p.y-y)/sqrt(1.0+pow(p.x,-4.0/3.0)/9.0 ); // https://iquilezles.org/articles/distance/
    col = mix( col, vec3(0.5,0.5,0.5), 1.0-smoothstep(0.0,1.5*px,d) );
    }
    // my implementation
    {
    float y = my_cbrt( p.x );
    float d = abs(p.y-y)/sqrt( 1.0+pow(p.x,-4.0/3.0)/9.0 ); // https://iquilezles.org/articles/distance/
    col = mix( col, vec3(1.0,1.0,0.0), al*(1.0-smoothstep(0.0,1.5*px,d)) );
    }    
    // hardware sqrt()
    {
    float y = sqrt( p.x );
    float d = abs(p.y-y)/sqrt(1.0+1.0/(4.0*p.x) ); // https://iquilezles.org/articles/distance/
    col = mix( col, vec3(0.5,0.5,0.5), 1.0-smoothstep(0.0,1.5*px,d) );
    }
    // my implementation
    {
    float y = my_sqrt( p.x );
    float d = abs(p.y-y)/sqrt(1.0+1.0/(4.0*p.x) ); // https://iquilezles.org/articles/distance/
    col = mix( col, vec3(0.0,1.0,1.0), al*(1.0-smoothstep(0.0,1.5*px,d)) );
    }    

    fragColor = vec4( col, 1.0 );
}

#define HALF_SQRT3 0.66025403
#define INV_SQRT3 0.5773502691
#define SIDELEN (iResolution.y / (iTime * 5.0))

vec2 getHexagonCenter(in vec2 pos){
    // Label
    int x = int(mod(floor(pos.x) - floor(pos.y), 3.0));

    switch(x){
        case 0:
            return vec2(ceil(pos.x), floor(pos.y));
        case 2:
            return vec2(floor(pos.x), ceil(pos.y));
        case 1:
            if(fract(pos.x) + fract(pos.y) < 1.0){
                return vec2(floor(pos.x), floor(pos.y));
            } else {
                return vec2(ceil(pos.x), ceil(pos.y));
            }
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    mat2 toHexagons = mat2(HALF_SQRT3, HALF_SQRT3,
                           -0.5      , 0.5      );
    mat2 fromHexagons = mat2(INV_SQRT3, INV_SQRT3,
                             -1.0     , 1.0);
    vec2 center = fromHexagons * fragCoord / SIDELEN;
    center = getHexagonCenter(center);
    center = toHexagons * center * SIDELEN;

    // normalize
    center /= iResolution.xy;

    float invcol = distance(center, vec2(-0.5, 0.5));
    fragColor.r = 1.0 - invcol;
    fragColor.g = 1.0 - 2.0 * invcol;
}

