// sdf and noise from shaders by inigo quilez, https://iquilezles.org/articles/distfunctions/

#define PI 3.14159265359
#define MAX_MARCHING_STEPS 255
#define MIN_DIST 0.0
#define MAX_DIST 100.0
#define PRECISION  0.001


float hash( float n )
{
    return fract(sin(n)*43758.5453);
}


float noise( in vec3 x ){
	vec3 p = floor(x);
	vec3 f = fract(x);
	f = f*f*(3.0 - 2.0*f);
    float n = p.x + p.y*157.0 + 113.0*p.z;
    return mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                   mix( hash(n+157.0), hash(n+158.0),f.x),f.y),
               mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                   mix( hash(n+270.0), hash(n+271.0),f.x),f.y),f.z);
}



float fbm (vec3 p)
{
    float f = 0.0;
    float freq = 4.0;
    for (int i = 0; i < 5; i++)
    {
        float n = 4. * noise(p * freq) / freq;
        f += n;
        freq *= 1.5;
    }
    return f;
}



float sdSphere( vec3 p, float r)
{
    return length(p)-(r+fbm(p));
}




float map( vec3 p )
{
    float d = sdSphere(p, 1.0);
    return d;
}

vec3 calcNormal( in vec3 pos )
{

    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ) + 
					  e.yyx*map( pos + e.yyx ) + 
					  e.yxy*map( pos + e.yxy ) + 
					  e.xxx*map( pos + e.xxx ) );
}


float calcAO( vec3 pos, vec3 nor)
{
    float occ = 0.0;
    float sca = 1.0;
    for(int i = 0; i < 4; i++)
    {
        float h = .001 + 0.015 * float(i);
        float d = map(pos + nor * h);
        occ = (h - d) * sca;
        sca *= .95;
        if( occ > 0.35 ) break;
    }

    return clamp( 1.0 - 1.0*occ, 0.0, 1.0 );
}

float raymarch( vec3 ro, vec3 rd)
{
    float dist = 0.0;

    for(int i = 0; i < MAX_MARCHING_STEPS; i++)
    {
        vec3 p = ro + rd * dist;
        float d = map(p);
        if(abs(d) < 0.001 || d > MAX_DIST) break;
        dist += d*0.75;
    }


    return dist;
}


vec3 render( in vec3 ro, in vec3 rd )
{

    vec3 col;
    vec3 background = vec3(.01, .01, .01);
    // raymarch
    float d = raymarch(ro, rd);

    if(d > MAX_DIST)
    {

    }
    else // if hit something
    {
        vec3 pos = ro + rd * d;
        vec3 nor = calcNormal(pos);

        // shadow
            pos += nor * PRECISION * 2.0; 
            float ao = calcAO(pos, nor);

        // sun
        {
            vec3 sunPos = vec3(13.*sin(iTime*.5), 7., 5.*cos(iTime*.5));
            vec3 ld = normalize(sunPos - pos);
            float dif = clamp( dot( nor, ld ), 0.0, 1.0 );


            float spe = pow( clamp( dot( nor, ld ), 0.0, 1.0 ),16.0);
            spe *= dif;
            spe *= 0.04+0.96*pow(clamp(1.0-dot(ld, sunPos),0.0,1.0),5.0);

            col += 1.2*dif * vec3(.6, .6, 1.);
        }
        col *= ao;

    }

    // fog
    col = mix(col, background, 1.0 - exp(-0.0001 * d * d * d));

    return col;
}

mat3 camera(vec3 campos, vec3 camtar)
{
    float roll = 0.0;
    vec3 cw = normalize(camtar - campos); // camera watch
    vec3 cr = normalize(cross(cw, vec3(sin(roll), cos(roll), 0.0))); // camera right
    vec3 cu = normalize(cross(cr, cw));   // camera up

    return mat3(cr, cu, -cw);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.x;

    float time = iTime*.2;
    vec2 mouse = PI * (-1.0 + 2.0 * iMouse.xy / iResolution.xy);

    // camera
    vec3 ro = vec3(0.0+sin(mouse.x)*15., 2.0, 15.0-15.*(cos(mouse.x)+1.));
    vec3 camtar = vec3(0.0, 0.0, 0.0);
    mat3 cam = camera(ro, camtar);
    vec3 rd = normalize(cam*vec3(uv, -1.3)); // ray direction

    vec3 col = render(ro, rd);

    // gamma
    col = pow(col, vec3(1.0/2.2));

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define SHADOW_ANGLE -50.0
#define SHADOW_COLOR vec3(1, 0, 0);
#define LIGHT_DIR vec2(1, 1)
#define LIGHT_INTENSITY 0.3

#define EPS 0.01
#define ITER 30

#define T iTime
#define S(a,b,t) smoothstep(a,b,t)
#define pi 3.1415926535898

vec2 rot(vec2 uv, float t) {
    float c = cos(t), s = sin(t);
    return mat2(c, -s, s, c) * uv;
}
vec3 RotateY(vec3 v, float degree)
{
    float rad = 0.0174532925*degree;
    float cosY = cos(rad);
    float sinY = sin(rad);
    return vec3(cosY*v.x - sinY * v.z, v.y, sinY*v.x + cosY * v.z);
}

float sdCircle(vec2 p, float radius) {
    return length(p) - radius;
}
// Copied from iq's article
float sdStar5(in vec2 p, in float r, in float rf)
{
    const vec2 k1 = vec2(0.809016994375, -0.587785252292);
    const vec2 k2 = vec2(-k1.x,k1.y);
    p.x = abs(p.x);
    p -= 2.0*max(dot(k1,p),0.0)*k1;
    p -= 2.0*max(dot(k2,p),0.0)*k2;
    p.x = abs(p.x);
    p.y -= r;
    vec2 ba = rf*vec2(-k1.y,k1.x) - vec2(0,1);
    float h = clamp( dot(p,ba)/dot(ba,ba), 0.0, r );
    return length(p-ba*h) * sign(p.y*ba.x-p.x*ba.y);
}
float triangleDist(vec2 p, float radius)
{
	return max(	abs(p).x * 0.866025 + 
			   	p.y * 0.5, -p.y) 
				-radius * 0.5;
}
float substract(float d1, float d2)
{
	return max(-d1, d2);
}
float pie(vec2 p, float angle)
{
	angle = radians(angle) / 2.0;
	vec2 n = vec2(cos(angle), sin(angle));
	return abs(p).x * n.x + p.y*n.y;
}
float circleDist(vec2 p, float radius)
{
	return length(p) - radius;
}

float semiCircleDist(vec2 p, float radius, float angle, float width)
{
	width /= 2.0;
	radius -= width;
	return substract(pie(p, angle), 
					 abs(circleDist(p, radius)) - width);
}
float boxDist(vec2 p, vec2 size, float radius)
{
	size -= vec2(radius);
	vec2 d = abs(p) - size;
  	return min(max(d.x, d.y), 0.0) + length(max(d, 0.0)) - radius;
}
float lineDist(vec2 p, vec2 start, vec2 end, float width)
{
	vec2 dir = start - end;
	float lngth = length(dir);
	dir /= lngth;
	vec2 proj = max(0.0, min(lngth, dot((start - p), dir))) * dir;
	return length( (start - p) - proj ) - (width / 2.0);
}

float radius =.5;
float angle =.1;
float width =.5;
vec2 size = vec2(0.4,.24);
vec2 start = vec2(0,0);
vec2 end = vec2(1,0);
float map(vec2 p) {
    //p = rot(p, iTime);
    p += vec2(sin(iTime), cos(iTime)*.5);
    float a1 = 5., a2 = a1 + 5., a3 = a2 + 5., a4 = a3 + 5., a5 = a4 + 5., a6 = a5 + 5., a7 = a6 + 5., a8 = a7 + 5.;    
    float gTime = mod(iTime,  a8 + 1.);//time after animations

    float Circ = sdCircle(p, 0.3);
    float tria = triangleDist(p, radius);
    float box = boxDist(p, size, radius-.45);
    float star = sdStar5(p, 0.4, 0.5);
    float scir = semiCircleDist(p, radius, angle, width);
    float lin = lineDist(p, start, end, width-.45);
        for (int i = 0; i < 4; i++)
        {
            vec3 p3;
            p3=RotateY(p3, -22.5 * float(i)) + vec3(float(i)*sin(T)*.75, float(i)*cos(T)*.75, float(i)*pi);
            float circAdd = sdCircle(p-p3.xy, 0.3);
            float triaAdd = triangleDist(p-p3.xy, radius);
            Circ = min(Circ, circAdd);
            tria = min(tria, triaAdd);
            float boxAdd = boxDist(p-p3.xy, size, radius-.45);
            box = min(box, boxAdd);
            float starAdd = sdStar5(p-p3.xy,0.4, 0.5);
            star = min(star, starAdd);
            float scirAdd = semiCircleDist(p-p3.xy, radius, angle, width);
            scir = min(scir, scirAdd);
            float linAdd = lineDist(p-p3.xy, start, end, width-.45);
            lin = min(lin, linAdd);
        }

    if (gTime < a1){
        float t0 = gTime-2.;

		return Circ;//sdCircle(p, 0.3);
    } else if (gTime < a2) {
        float t0 = gTime-a1;

        return tria; 
    } else if (gTime < a3 ){
     	float t0 = gTime - a2;

        return box;
    } else if (gTime < a4) { 
        float t0 = gTime-a3;

        return star;
    } else if (gTime < a5) {

       return Circ;     
    } else if (gTime < a5) {

       return tria;       

    } else if (gTime < a6) {

       return scir;       
    } else if (gTime < a7){

       return box;
    } else if  (gTime < a8) {

       return  lin;
    }



}

vec2 getNormal(vec2 p) {
    vec2 d = vec2(1, 0) * EPS;
    return normalize(vec2(
        map(p + d.xy) - map(p - d.xy),
        map(p + d.yx) - map(p - d.yx)
    ));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = uv * 2. - 1.;
    p.x *= iResolution.x / iResolution.y; // fix aspect ratio

    vec3 col;

    // Draw BG
    float a = floor((uv.x - uv.y * 0.5 - iTime * .08) * 8.) - iTime * 2.;
    col = vec3(sin(a), sin(a + 2.), sin(a + 3.)) * 0.2 + 0.7;

    // Draw infinite shadow by raymarching
    float angle = radians(SHADOW_ANGLE);
    vec2 dir = -vec2(cos(angle), sin(angle));
    float d;
    float t = 0.;
    vec2 p2;

    for (int i = 0; i < ITER; i++) {
        p2 = p + dir * t;
        d = map(p2);
        if (d < EPS) {
            vec2 n = getNormal(p2);
            float shade = dot(n, normalize(LIGHT_DIR));
            col = SHADOW_COLOR;
            col *= 1. + shade * LIGHT_INTENSITY;
        }
        t += d;
    }    

    // Draw foreground
    d = map(p);
    float inside = step(d, EPS);
    if (d < EPS) {
        col = vec3(step(d, -EPS));
        //uv = rot(uv, iTime);


        uv/=vec2(.75);
        uv+=vec2(.75);
        uv += vec2(sin(iTime), cos(iTime));
        col=min(col, texture(iChannel0,uv).rgb);
    }    
    //col*=rectangle(uv, vec2(0.5, 0.51));//
    // vignette
    col -= smoothstep(0.5, 3.0, length(p));

    fragColor = vec4(col, 1.0);
}

const vec4 waterTint = vec4(0.5, 0.5, 0.5, 1.0);
const float waterRippleStrength = 10.25;
const float waterRippleSpeed = 0.015;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float waterLineOffset = iMouse.y / iResolution.y;

    if (uv.y >= waterLineOffset)
    {
        fragColor = texture(iChannel0, uv);
    }
    else
    {
        float fieldOfViewScalar = (iMouse.x / iResolution.x) * 10.0;
        vec2 vanishingPoint = vec2(0.5, waterLineOffset + fieldOfViewScalar);
        float distanceToVanishingPoint = length(uv - vanishingPoint);
        float distanceToWaterLine = waterLineOffset - uv.y + 0.2;
        float fadedWaterRippleStrength = waterRippleStrength * distanceToWaterLine;
        vec4 noiseColor = texture(
            iChannel1,
            vec2(
                uv.x + iTime * waterRippleSpeed, 
                uv.y - iTime * waterRippleSpeed
            ) * distanceToVanishingPoint
        );
        noiseColor *= texture(
            iChannel2,
            vec2(
                uv.x - iTime * waterRippleSpeed,
                uv.y + iTime * waterRippleSpeed
            ) * distanceToVanishingPoint
        );
        noiseColor *= fadedWaterRippleStrength * distanceToWaterLine;
        fragColor = texture(
            iChannel0, 
            vec2(
                uv.x,
                -uv.y + waterLineOffset
            ) + 
            vec2(
                0.0,
                waterLineOffset
            ) +
            vec2(
                noiseColor.g * noiseColor.b * noiseColor.r,
                noiseColor.r * noiseColor.g * noiseColor.b
            )
        ) * waterTint;
    }
}

// License CC0: Face in the clouds
//  Symmetry around y-axis can often create an illusion of a face
//  or a human body. I was playing around with smeared FBM
//  and added some glowing points to mislead the brain to think it's eyes
//  of a malevolent cloud being

#define PI          3.141592654
#define TAU         (2.0*PI)
#define TIME        iTime
#define TTIME       (TIME*TAU)
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))
#define DOT2(x)     dot(x, x)
#define RESOLUTION  iResolution

const mat2 frot = ROT(PI/3.33);

vec2 g_vx = vec2(0.0, 0.0);
vec2 g_vy = vec2(3.2, 1.3);

vec2 g_wx = vec2(1.7, 9.2);
vec2 g_wy = vec2(8.3, 2.8);

// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}
// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
//  Macro version of above to enable compile-time constants
#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))

// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
float sRGB(float t) { return mix(1.055*pow(t, 1./2.4) - 0.055, 12.92*t, step(t, 0.0031308)); }
// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
vec3 sRGB(in vec3 c) { return vec3 (sRGB(c.x), sRGB(c.y), sRGB(c.z)); }

// License: Unknown, author: Matt Taylor (https://github.com/64), found: https://64.github.io/tonemapping/
vec3 aces_approx(vec3 v) {
  v = max(v, 0.0);
  v *= 0.6f;
  float a = 2.51f;
  float b = 0.03f;
  float c = 2.43f;
  float d = 0.59f;
  float e = 0.14f;
  return clamp((v*(a*v+b))/(v*(c*v+d)+e), 0.0f, 1.0f);
}

// License: MIT, author: Inigo Quilez, found: https://www.iquilezles.org/www/articles/smin/smin.htm
float pmin(float a, float b, float k) {
  float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
  return mix(b, a, h) - k*h*(1.0-h);
}

float vesica(vec2 p, vec2 sz) {
  if (sz.x < sz.y) {
    sz = sz.yx;
  } else {
    p  = p.yx; 
  }
  vec2 sz2 = sz*sz;
  float d  = (sz2.x-sz2.y)/(2.0*sz.y);
  float r  = sqrt(sz2.x+d*d);
  float b  = sz.x;
  p = abs(p);
  return ((p.y-b)*d>p.x*b) ? length(p-vec2(0.0,b))
                           : length(p-vec2(-d,0.0))-r;
}

float pabs(float a, float k) {
  return -pmin(a, -a, k);
}

float noise(vec2 p) {
  float a = sin(p.x);
  float b = sin(p.y);
  float c = 0.5 + 0.5*cos(p.x + p.y);
  float d = mix(a, b, c);
  return d;
}

float fbm(vec2 p) {    
  float f = 0.0;
  float a = 1.0;
  float s = 0.0;
  float m = 2.0;
  for (int x = 0; x < 4; ++x) {
    f += a*noise(p); p = frot*p*m;
    m += 0.01;
    s += a;
    a *= 0.5;
  }
  return f/s;
}

vec2 df(vec2 p) {
  vec2 p0 = p;
  p0.x = abs(p0.x);
  p0 -= vec2(0.75, 0.4);
  p0 *= ROT(PI/9.0);
  float d0 = vesica(p0, vec2(0.45, 0.2));
  float g0 = length(p0);

  float d = d0;
  float g = g0;

  return vec2(d, g);
}

float warp(vec2 p, out vec2 v, out vec2 w) {
  float d = df(p).x-0.1;
  p.x = -pabs(p.x, 0.125);

  v = vec2(fbm(p + g_vx), fbm(p + g_vy));
  w = vec2(fbm(p + 3.0*v + g_wx), fbm(p + 3.0*v + g_wy));

  float h = fbm(p + 2.25*w);

  float aa = 0.2;
  h *= mix(1.0, 0.05, smoothstep(aa, -aa, d));

  return h*smoothstep(2.5, 0.15, abs(p.x+0.5*p.y));
}

vec3 normal(vec2 p) {
  vec2 v;
  vec2 w;
  float eps = 2.0/RESOLUTION.y;
  vec2 e = vec2(eps, 0);

  vec3 n;
  n.x = warp(p + e.xy, v, w) - warp(p - e.xy, v, w);
  n.y = 2.0*e.x;
  n.z = warp(p + e.yx, v, w) - warp(p - e.yx, v, w);

  return normalize(n);
}

vec3 smear(vec2 p, vec2 q) {
  float aa = 2.0/RESOLUTION.y;
  g_vx *= ROT(TTIME/1000.0);
  g_vy *= ROT(TTIME/900.0);
  g_wx *= ROT(TTIME/800.0);
  g_wy *= ROT(TTIME/700.0);

  vec2 v;
  vec2 w;

  vec2 d2 = df(p);
  float d = abs(d2.x) - 2.0*aa;
  float g = d2.y;
  float h = warp(p, v, w);
  vec3 n  = normal(p);

  vec3 ld1  = normalize(vec3(0.5, 0.2, 0.4));
  vec3 ld2  = normalize(vec3(-0.5, 0.2, -0.4));
  vec3 lcol1= HSV2RGB(vec3(0.9, 0.333, 1.0));
  vec3 lcol2= HSV2RGB(vec3(0.6, 0.125, 2.0));
  vec3 acol = HSV2RGB(vec3(0.6, 0.0, 0.3));

  float dif1 = pow(max(dot(ld1, n), .0), 1.0);
  float dif2 = pow(max(dot(ld2, n), .0), 2.0);

  const vec3 col11 = HSV2RGB(vec3(0.9, 0.9, 0.5));
  const vec3 col21 = HSV2RGB(vec3(0.4, 0.9, 0.5));
  const vec3 col12 = HSV2RGB(vec3(0.6, 0.9, 1.5));
  const vec3 col22 = HSV2RGB(vec3(0.0, 0.9, 1.5));

  vec3 col1 = mix(col11, col12, q.x);
  vec3 col2 = mix(col21, col22, q.y);

  vec3 col = vec3(0.0);
  float lv = length(v);
  float lw = length(w);
  col += lv*col1*dif1*lcol1;
  col += lw*col2*dif1*lcol1;
  col += lv*col1*dif2*lcol2;
  col += lw*col2*dif2*lcol2;
  col += lv*col1*acol;
  col += lw*col2*acol;
  col *= smoothstep(0.0, 1., (h*h+0.05+0.75*0.125*(1.0+p.y)));
//  col *= smoothstep(0.0, 1., (h*h+0.125));
  col *= smoothstep(2.0, 8.0, TIME-length(p));
  col += mix(5.0, 1.0, 0.5+0.5*sin(TTIME/8.0))*HSV2RGB(vec3(0.6, 0.8, 1.0))*exp(-40.0*g);
  col *= smoothstep(0.0, 4.0, TIME);
//  col = mix(col, vec3(1.0), smoothstep(aa, -aa, d));
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;

  vec3 col = smear(p, q);
  col = aces_approx(col);
  col = sRGB(col);

  fragColor = vec4(col, 1.0);
}

#define myPi 3.141592654f
#define Eps 1e-8
#define maxZ 100.0

const int n = 3;
vec3 colVec[n] = vec3[n](vec3(0.6, 0.6, 0.9), vec3(0.0, 0.8, 0.7), vec3(0.3, 0.1, 0.6));
