int sizes[n] = int[n](3, 3, 4), inds[n] = int[n](0, 3, 6); const int m = 10;
vec2 polygonsXY[m]; float polygonsZ[m];

void initPolygons(out vec2 polygonsXY[m], out float  polygonsZ[m], in float time){
// 0 -- triangle
    polygonsXY[0] = vec2(0.0, 0.0); polygonsZ[0] = 0.1; 
    polygonsXY[1] = vec2(1.0, 0.0); polygonsZ[1] = 0.1;
    polygonsXY[2] = vec2(1.0, 1.0); polygonsZ[2] = 0.1;
// 1 -- triangle
    polygonsXY[3] = vec2(0.3, 0.2); polygonsZ[3] = 0.09;
    polygonsXY[4] = vec2(0.7, 0.5); polygonsZ[4] = 0.11;
    polygonsXY[5] = vec2(0.8, 0.9); polygonsZ[5] = 0.09;
// 2 -- rotated square
    float w = 2.0;
    vec3 center = vec3(0.55, 0.5, 0.1 + 0.05*sin(time*w)); float r = 0.4;
    int i; float j =0.0, phi = myPi/6.0;
    float ang = 2.0*myPi/4.0; 
    vec3 tmpVec;
    mat3 rot = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(phi), sin(phi)), vec3(0.0, -sin(phi), cos(phi)) );
    for (i = 6; i < 9; i++){
        tmpVec = rot*(r*vec3(cos(j*ang + time), sin(j*ang + time), 0.0)); 
        polygonsXY[i] = tmpVec.xy + center.xy;
        polygonsZ[i] = tmpVec.z + center.z;
        j = j + 1.0;
    }

}



int leftTurn(in vec2 a, in vec2 b, in vec2 c)
{
    float tmp = (c.x - a.x)*(b.y - a.y) - (c.y - a.y)*(b.x - a.x);
    if (abs(tmp) < Eps) 
        return 0;
    else 
        return tmp > 0.0 ? 1: -1;
}


bool inPolygon(in int ind0, in int size, in vec2 vert[m], in vec2 p) // vertices in counter clockwise order
{
    bool res = leftTurn(vert[ind0 + size-1], vert[ind0], p) <= 0; int i = ind0;
    while(res && (i < ind0 + size -1))
    {
        res = res && (leftTurn(vert[i], vert[i+1], p) <= 0); i++;
    }
    return res;
}

float zInPlane(in vec3 a, in vec3 b, in vec3 c, in vec2 p) // z coordinate for (x,y) point of a plane (a-b, c-b)
{
    vec3 nrm = cross(a-b, c-b);
    //float D = - dot(nrm, b);
    if (abs(nrm.z) < Eps) return a.z;
    else return (dot(nrm, b) - nrm.x*p.x - nrm.y*p.y)/nrm.z;
}


