vec3 hue(float val) {
    vec3 col;
    float h = mod(val, 1.0);
    float X = -abs(6.0 * mod(h, 1.0 / 3.0) - 1.0) + 1.0;

    if(h < 1.0 / 6.0)
        col = vec3(1.0,X,0.0);
    else if(h < 2.0 / 6.0)
        col = vec3(X,1.0,0.0);
    else if(h < 3.0 / 6.0)
        col = vec3(0.0,1.0,X);
    else if(h < 4.0 / 6.0)
        col = vec3(0.0,X,1.0);
    else if(h < 5.0 / 6.0)
        col = vec3(X,0.0,1.0);
    else
        col = vec3(1.0,0.0,X);

    return col;
}

