float distToBox(vec2 pos, float rotation, vec2 size, vec2 uv) {
    float cr = cos(rotation);
    float sr = sin(rotation);

    uv -= pos;
    uv = vec2(uv.x * cr - uv.y * sr, uv.x * sr + uv.y * cr);

    vec2 q = abs(uv) - size;
    return length(max(q,0.0f));
}

