////////////////////////////////////////////////////////////////////////////////
//
// Elliptical Torus
//
// Another nice quartic surface - a surface of revolution generated by
// an ellipse, ie. an elliptical torus.
//
// Analytic solution to intersection & gradient. Calculation of
// coefficients done mostly by Sage algebra.
//
// <mouse>: change view direction
// <up/down>: zoom in/out
// <left/right>: change torus radius
// 'a': antialiasing
// 'r': autorotation
//
////////////////////////////////////////////////////////////////////////////////

const float PI =  3.141592654;

// Lighting
vec3 light = vec3(1,1,-1);
float ambient = 0.4;
float diffuse = 0.6;
float specular = 0.8;
float specularpow = 10.0;
vec3 specularcolor = vec3(1);

float torusr = 1.0;

int elliptotorus (vec3 p, vec3 d, out vec4 roots) {
  // A = (b4*z14 + 2*b2*x12*z12 + 2*b2*y12*z12 + x14 + 2*x12*y12 + y14)
  // B = 4*(b4*z0*z13 + b2*x12*z0*z1 + b2*y12*z0*z1 + b2*x0*x1*z12 + b2*y0*y1*z12 + x0*x13 + x12*y0*y1 + x0*x1*y12 + y0*y13)
  // C = 2*(3*b4*z02*z12 + b2*x12*z02 + b2*y12*z02 + 4*b2*x0*x1*z0*z1 + 4*b2*y0*y1*z0*z1 - b4*z12 + b2*r2*z12 + b2*x02*z12 + b2*y02*z12 - b2*x12 - r2*x12 + 3*x02*x12 + x12*y02 + 4*x0*x1*y0*y1 - b2*y12 - r2*y12 + x02*y12 + 3*y02*y12)
  // D = 4*(b4*z03*z1 + b2*x0*x1*z02 + b2*y0*y1*z02 - b4*z0*z1 + b2*r2*z0*z1 + b2*x02*z0*z1 + b2*y02*z0*z1 - b2*x0*x1 - r2*x0*x1 + x03*x1 + x0*x1*y02 - b2*y0*y1 - r2*y0*y1 + x02*y0*y1 + y03*y1)
  // E = b4*z04 - 2*b4*z02 + 2*b2*r2*z02 + 2*b2*x02*z02 + 2*b2*y02*z02 + b4 - 2*b2*r2 + r4 + x04 - 2*b2*y02 - 2*r2*y02 + 2*x02*y02 + y04 - 2*(b2 + r2)*x02
  float x0 = p.x, y0 = p.y, z0 = p.z;
  float x02 = x0*x0, x03 = x02*x0, x04 = x02*x02;
  float y02 = y0*y0, y03 = y02*y0, y04 = y02*y02;
  float z02 = z0*z0, z03 = z02*z0, z04 = z02*z02;
  float x1 = d.x, y1 = d.y, z1 = d.z;
  float x12 = x1*x1, x13 = x12*x1, x14 = x12*x12;
  float y12 = y1*y1, y13 = y12*y1, y14 = y12*y12;
  float z12 = z1*z1, z13 = z12*z1, z14 = z12*z12;
  float b2 = 0.25, b4 = b2*b2;
  float r = torusr;
  float r2 = r*r, r4 = r2*r2;
  float A = x14 + y14 + b4*z14 + 2.0*(x12*y12 + b2*x12*z12 + b2*y12*z12);
  float B = 4.0*(x0*x13 + y0*y13 + x0*x1*y12 + x12*y0*y1 + b4*z0*z13 + b2*x12*z0*z1 + b2*y12*z0*z1 + b2*x0*x1*z12 + b2*y0*y1*z12);
  float C = 2.0*(3.0*b4*z02*z12 + b2*x12*z02 + b2*y12*z02 + 4.0*b2*x0*x1*z0*z1 + 4.0*b2*y0*y1*z0*z1 - b4*z12 + b2*r2*z12 + b2*x02*z12 + b2*y02*z12 - b2*x12 - r2*x12 + 3.0*x02*x12 + x12*y02 + 4.0*x0*x1*y0*y1 - b2*y12 - r2*y12 + x02*y12 + 3.0*y02*y12);
  float D = 4.0*(b4*z03*z1 + b2*x0*x1*z02 + b2*y0*y1*z02 - b4*z0*z1 + b2*r2*z0*z1 + b2*x02*z0*z1 + b2*y02*z0*z1 - b2*x0*x1 - r2*x0*x1 + x03*x1 + x0*x1*y02 - b2*y0*y1 - r2*y0*y1 + x02*y0*y1 + y03*y1);
  float E = b4*z04 - 2.0*b4*z02 + 2.0*b2*r2*z02 + 2.0*b2*x02*z02 + 2.0*b2*y02*z02 + b4 - 2.0*b2*r2 + r4 + x04 - 2.0*b2*y02 - 2.0*r2*y02 + 2.0*x02*y02 + y04 - 2.0*(b2 + r2)*x02;
  return quartic(A,B,C,D,E,roots);
}

vec3 elliptotorusnormal (vec3 p) {
  // b4*z4 + b4 - 2*b2*r2 + r4 + x4 + y4 - 2*(b2 + r2)*x2 - 2*(b2 + r2 - x2)*y2 - 2*(b4 - b2*r2 - b2*x2 - b2*y2)*z2
  float x = p.x, y = p.y, z = p.z;
  float x2 = x*x, x3 = x2*x;
  float y2 = y*y, y3 = y2*y;
  float z2 = z*z, z3 = z2*z;
  float b2 = 0.25, b4 = b2*b2;
  float r = torusr;
  float r2 = r*r;
  // b4*z4 + x4 + y4 - 2*(b2 + r2)*x2 - 2*(b2 + r2 - x2)*y2 - 2*(b4 - b2*r2 - b2*x2 - b2*y2)*z2
  float dx = 4.0*x3 - 4.0*(b2 + r2)*x + 4.0*x*y2 + 4.0*b2*x*z2;
  float dy = 4.0*y3 - 4.0*(b2 + r2 - x2)*y + 4.0*b2*y*z2;
  float dz = 4.0*b4*z3 - 4.0*(b4 - b2*r2 - b2*x2 - b2*y2)*z;
  return vec3(dx,dy,dz);
}

int dosurface(vec3 p0, vec3 r, out vec4 roots) {
  return elliptotorus(p0,r,roots);
}

vec3 donormal(vec3 p) {
  return elliptotorusnormal(p);
}

int solve(vec3 p0, vec3 r, float tmin, out vec4 roots) {
  int nroots = dosurface(p0,r,roots);
  // Sort the roots
  if (nroots >= 2) {
    if (roots.y < roots.x) roots.xy = roots.yx;
  }
  if (nroots >= 3) {
    if (roots.z < roots.y) roots.yz = roots.zy;
    if (roots.y < roots.x) roots.xy = roots.yx;
  }
  if (nroots >= 4) {
    if (roots.w < roots.z) roots.zw = roots.wz;
    if (roots.z < roots.y) roots.yz = roots.zy;
    if (roots.y < roots.x) roots.xy = roots.yx;
  }
  return nroots;
}

vec3 applylighting(vec3 baseColor, vec3 p, vec3 n, vec3 r) {
  if (dot(r,n) > 0.0) n = -n; // Face forwards
  vec3 c = baseColor*ambient;
  c += baseColor*diffuse*(max(0.0,dot(light,n)));
  float s = pow(max(0.0,dot(reflect(light,n),r)),specularpow);
  c += specular*s*specularcolor;
  return c;
}

vec3 scene(vec3 p0, vec3 r) {
  float tmin = 0.0;
  tmin = -dot(p0,r);
  p0 += tmin*r;
  vec4 roots;
  vec3 color = vec3(0,0,0.1);
  int nroots = solve(p0,r,-tmin,roots);
  for (int i = nroots-1; i >= 0; i--) {
    float t = roots[i];
    if (t < -tmin) break;
    vec3 p = p0+t*r;
    vec3 n = donormal(p);
    if (isnan(length(n))) continue;
    if (dot(n,r) > 0.0) n = -n;
    n = normalize(n);
    vec3 basecolor = pow(abs(n),vec3(2.0));
    float k = iTime;
    //k += 10.0*atan(p.x,p.y)/PI; // Hum!
    if (length(p.xy) > torusr) k = -k;
    k += 10.0*p.z;
    if (abs(k-round(k)) < 0.1) basecolor = vec3(0);
    //if (abs(length(p)-sqrt(a)) < 0.01) basecolor = vec3(1,0,0);
    //if (min(abs(p.x),min(abs(p.y),abs(p.z))) < 0.01) basecolor = vec3(0.5);
    vec3 c = applylighting(basecolor,p,n,r);
    float dist = t + tmin;
    float fog = 1.0-smoothstep(0.0,100.0,dist);
    //c *= fog;
    color = mix(color,c,0.5);
  }
  return color;
}

// GUI related code starts here
vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  if (!key(CHAR_R)) {
    float t = iTime;
    p.yz = rotate(p.yz, 0.1*t);
    p.zx = rotate(p.zx, 0.222*t);
  }
  return p;
}

