      vec2 uv = scale*(2.0*(fragCoord.xy+vec2(i,j)/float(AA)) - iResolution.xy)/iResolution.y;
      vec3 r = vec3(uv, 2.0);
      r = transform(r);
      r = normalize(r);
      color += scene(p,r);
    }
  }
  color /= float(AA*AA);
  color = pow(color,vec3(0.4545));
  if (alert) color.x = 1.0;
  fragColor = vec4(color,1);
}

