float sdSquare(in vec2 p)
{
    vec2 d = abs(p)-vec2(0.5,0.5);
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}


float map(in vec2 p)
{
    float sd = sdSquare(p);
    for (float it = 1.; it < its; it++)
    {
        vec2 mod_d = mod(p*pow(3.,it)+1.5,3.)-1.5;
        float d = sdSquare(mod_d);
        sd = max(sd, -d);
