// Webcam Glitch
// By Leon Denise 08-06-2022
// Simple and handy glitch effect for digital screen

vec3 hash33(vec3 p3) // Dave Hoskins https://www.shadertoy.com/view/4djSRW
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}
vec2 hash21(float p)
{
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // zoom out
    uv = (uv-.5)*1.1+.5;

    // animation
    float speed = 10.;
    float t = floor(iTime*speed);

    // randomness
    vec2 lod = iResolution.xy/hash21(t)/200.;
    vec2 p = floor(uv*lod);
    vec3 rng = hash33(vec3(p,t));

    // displace uv
    vec2 offset = vec2(cos(rng.x*6.283),sin(rng.x*6.283))*rng.y;
    float fade = sin(fract(iTime*speed)*3.14);
    vec2 scale = 50. / iResolution.xy;
    float threshold = step(0.9, rng.z) ;
    uv += offset * threshold * fade * scale;

    // chromatic abberation
    vec2 rgb = 10./iResolution.xy * fade * threshold;
    fragColor.r = texture(iChannel0, uv+rgb).r;
    fragColor.g = texture(iChannel0, uv).g;
    fragColor.b = texture(iChannel0, uv-rgb).b;
    fragColor.a = 1.0;

    // crop
    fragColor.rgb *= step(0.,uv.x) * step(uv.x,1.) * step(0.,uv.y) * step(uv.y,1.);

}

// 
// Created by fl0a1e 06/08/2022
// 
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
// sdf from shaders by inigo quilez, https://iquilezles.org/articles/distfunctions/

#define PI 3.14159265359
#define MAX_MARCHING_STEPS 255
#define MIN_DIST 0.0
#define MAX_DIST 100.0
#define PRECISION  0.001



float sdPlane( vec3 p )
{
	return p.y + 0.3;
}

float sdSphere( vec3 p, float r, vec3 offset)
{
    return length(p - offset)-r;
}

float sdTorus( vec3 p, vec2 t )
{
    vec2 q = vec2(length(p.xz)-t.x,p.y);
    return length(q)-t.y;
}

float sdBoxFrame( vec3 p, vec3 b, float e )
{
       p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

// min
float opU( float a, float b )
{
    return (a < b) ? a : b;
}

float sdSmoothUnion( float d1, float d2, float k ) 
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

float map( vec3 p )
{
    float d = sdSphere(p, .3, vec3(2.*sin(iTime),0.,0.));
    d = sdSmoothUnion( d, sdTorus(p + vec3(0., .1, 0. ), vec2(0.8,0.2)), 0.2);
    d = sdSmoothUnion(d, sdSphere(p, .3, vec3(0. , 0., 2.*cos(iTime))), 0.2);

    d = sdSmoothUnion(d, sdBoxFrame(p - vec3(2., 0., 0.), vec3(0.3,0.3,0.3), 0.03), .1);
    d = sdSmoothUnion(d, sdBoxFrame(p - vec3(0., 0., -2.), vec3(0.3,0.3,0.3), 0.03), .1);
    d = sdSmoothUnion(d, sdBoxFrame(p - vec3(-2., 0., 0.), vec3(0.3,0.3,0.3), 0.03), .1);
    d = sdSmoothUnion(d, sdBoxFrame(p - vec3(0., 0., 2.), vec3(0.3,0.3,0.3), 0.03), .1);
    d = opU(d, sdPlane(p));
    return d;
}


vec3 calcNormal( in vec3 pos )
{

    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ) + 
					  e.yyx*map( pos + e.yyx ) + 
					  e.yxy*map( pos + e.yxy ) + 
					  e.xxx*map( pos + e.xxx ) );
}

float raymarch( vec3 ro, vec3 rd)
{
    float dist = 0.0;

    for(int i = 0; i < MAX_MARCHING_STEPS; i++)
    {
        vec3 p = ro + rd * dist;
        float d = map(p);
        if(d < 0.001 || d > MAX_DIST) break;
        dist += d;
    }


    return dist;
}


float calcAO( vec3 pos, vec3 nor)
{
    float occ = 0.0;
    float sca = 1.0;
    for(int i = 0; i < 5; i++)
    {
        float h = .01 + 0.015 * float(i);
        float d = map(pos + nor * h);
        occ = (h - d) * sca;
        sca *= .95;
        if( occ > 0.35 ) break;
    }

    return clamp( 1.0 - 5.0*occ, 0.1, 1.0 );
}


float calcSoftshadow( vec3 ro, vec3 rd, float k )
{
    float res = 1.0;
    float t = .02;
    for( int i = 0; i < 24; i++ )
    {
		float h = map( ro + rd*t );
        float s = smoothstep(0.0, 1.0, clamp(k*h/t,0.0,1.0));
        res = min( res, s );
        t += clamp( h, 0.02, 0.2 );
        if( res < 0.004 || t > 2.5 ) break;
    }
    return clamp( res, 0.2, 1.0 );
}


mat3 camera(vec3 campos, vec3 camtar)
{
    float roll = 0.0;
    vec3 cw = normalize(camtar - campos); // camera watch
    vec3 cr = normalize(cross(cw, vec3(sin(roll), cos(roll), 0.0))); // camera right
    vec3 cu = normalize(cross(cr, cw));   // camera up

    return mat3(cr, cu, -cw);
}

vec3 render( in vec3 ro, in vec3 rd )
{
    // background
    vec3 col;

    // raymarch
    float d = raymarch(ro, rd);

    if(d > MAX_DIST)
    {
        col = vec3(.4, .5, .9);
    }
    else // if hit something
    {
        vec3 pos = ro + rd * d;
        vec3 nor = calcNormal(pos);

        float ao = calcAO(pos, nor);

        // sun
        {
            vec3 sunPos = vec3(13., 7., 5.);
            vec3 ld = normalize(sunPos - pos);
            float dif = clamp( dot( nor, ld ), 0.0, 1.0 );
            // shadow
            pos += nor * PRECISION * 2.0; // add offset
            dif *= calcSoftshadow(pos, ld, 8.0);


            float spe = pow( clamp( dot( nor, ld ), 0.0, 1.0 ),16.0);
            spe *= dif;
            spe *= 0.04+0.96*pow(clamp(1.0-dot(ld, sunPos),0.0,1.0),5.0);

            col += dif * vec3(.6, .6, 1.);
        }


        col *= ao;


    }

    // fog
    col = mix(col, vec3(.4, .5, .9), 1.0 - exp(-0.0001 * d * d * d));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.x;

    float time = iTime*.2;
    vec3 ro = vec3(0.0, 2.0, 10.0);
    vec3 camtar = vec3(0.0, 0.0, 0.0);
    float cameraRadius = 10.;
    ro.x = cameraRadius * cos(time) + camtar.x; // convert x-component to polar and add offset
    // camera
    ro.z = cameraRadius * sin(time) + camtar.z; // convert z-component to polar and add offset
    mat3 cam = camera(ro, camtar);
    vec3 rd = normalize(cam*vec3(uv, -1.5)); // ray directionvec3 ro = vec3(0.0, 2.0, 10.0);



    vec3 col = render(ro, rd);

    // gamma
    col = pow(col, vec3(1.0/2.2));


    // Output to screen
    fragColor = vec4(col,1.0);
}

