    vec2 center = vec2(0.5, 0.5);
    vec2 v2 = (fragCoord/iResolution.xy) - center;
    v2 *= 0.99;
    offsetPos = v2 + center;

    vec3 image = texture(iChannel1,offsetPos).xyz;

    float camBrightness = rgbToGray(cam);
    float imageBrightness = rgbToGray(image);

    //vec3 shifted = hueShift2(cam, iTime); 
