#define BACKGROUND_COLOR vec3(0.5f, 0.5f, 1.0f)

// The amount of metaballs
// The more there are, the quicker the same one will reach the same position again
// This is because the noise texture is finite and sampled based on the ball and time
// Doesn't actally control the amount of blobs (unrolled loop) but still used in calculations
#define METABALLS 8

#define METABALL_MIN_RADIUS 0.0625f
#define METABALL_MAX_RADIUS 0.125f

#define FLOW_TIME_SCALE 0.015625f
#define UP_FLOW_TIME_SCALE 0.0625f
#define ROTATION_TIME_SCALE 0.0981747705f

#define CAMERA_OFFSET -2.0f
#define SPHERE_RADIUS 0.375f

#define MAX_ITERATIONS 8
#define SDF_EPSILON 1e-6

#define SPECULAR_HIGHLIGHT 1
#define POINT_LIGHT_POSITION vec3(1.0f, 0.5f, 0.0f)
#define SPECULAR_DEGREE 6


// Cubic interpolation http://paulbourke.net/miscellaneous/interpolation/
float mixCubic(float y0, float y1, float y2, float y3, float mu)
{
   float a0, a1, a2, a3, mu2;

   mu2 = mu * mu;
   a0 = y3 - y2 - y0 + y1;
   a1 = y0 - y1 - a0;
   a2 = y2 - y0;
   a3 = y1;

   return a0 * mu * mu2 + a1 * mu2 + a2 * mu + a3;
}

vec4 mixCubic(vec4 z, vec4 a, vec4 b, vec4 c, float t){
    return vec4(
        mixCubic(z.x, a.x, b.x, c.x, t),
        mixCubic(z.y, a.y, b.y, c.y, t),
        mixCubic(z.z, a.z, b.z, c.z, t),
        mixCubic(z.w, a.w, b.w, c.w, t)
    );
}


// https://gist.github.com/h3r/3a92295517b2bee8a82c1de1456431dc
float hash(float x, float y){
    return fract(1e4 * sin(17.0 * x + y * 0.1) * (0.1 + abs(sin(y * 13.0 + x))));
}

vec4 hash4(float x, float y){
    return vec4(
        -0.25f + 0.5f * hash(x + 0.75f, y),
        -0.25f + 0.5f * hash(x + 0.5f, y),
        -0.25f + 0.5f * hash(x + 0.25f, y),
        hash(x, y));
}


// Inigo Quilez MIT licence https://www.shadertoy.com/view/lt3BW2
float opSmoothUnion( float d1, float d2, float k )
{
    float h = max(k-abs(d1-d2),0.0);
    return min(d1, d2) - h*h*0.25/k;
}


// Samples the color from a gradient
vec3 colorGradient(float x){
    return vec3(smoothstep(0.25f, 0.4f, x), smoothstep(0.4f, 0.6f, x), smoothstep(0.65f, 0.875f, x));
}


// Samples the noise texture at the given index
vec4 noiseAtId(int i){
    return texture(iChannel0, vec3(float(i >> 10) / 32.0f, float(i << 5 >> 10) / 32.0f, float(i << 10 >> 10) / 32.0f));
}


// Samples the noise texture at the given time for the given metaball
vec4 noise(int ballId, float time){

    float flooredTime = floor(time);    
    // int stride = 32768 / METABALLS;
    //int t = int(flooredTime);

    // vec4 z = noiseAtId(stride * ballId + (t + stride - 1) % stride);
    // vec4 a = noiseAtId(stride * ballId + t % stride);
    // vec4 b = noiseAtId(stride * ballId + (t + 1) % stride);
    // vec4 c = noiseAtId(stride * ballId + (t + 2) % stride);

    vec4 z = hash4(float(ballId), flooredTime - 1.0f);
    vec4 a = hash4(float(ballId), flooredTime);
    vec4 b = hash4(float(ballId), flooredTime + 1.0f);
    vec4 c = hash4(float(ballId), flooredTime + 2.0f);

    return mixCubic(z, a, b, c, time - flooredTime);
}


// Gets the position and radius at a given time for the given metaball
vec4 getBallInfo(int ballId, float time){

    vec4 res = noise(ballId, time);

    // Scale [0;1] -> [-1;1]
    res.xyz *= 2.0f;
    res.xyz += vec3(-1.0f, -1.0f, -1.0f);

    // Fit noise better
    // For some reason it's actually on [-0.5,0.5] by now, so rescale
    // with also some squeezing towards the center for a better look
    res.x = res.x * res.x + res.x;
    res.y *= 2.0f;
    res.z = res.z * res.z + res.z;

    // Updraft
    res.y += 1.0f + iTime * UP_FLOW_TIME_SCALE;
    res.y = fract(res.y);
    res.y = -2.0f + 4.0f * res.y;

    // Density
    res.w = mix(METABALL_MIN_RADIUS, METABALL_MAX_RADIUS, res.w);
    res.w *= min(mix(1.0f, 0.0f, res.y), mix(1.0f, 0.0f, -1.0f + 2.0f * length(res.xyz)));

    // Scale to sphere
    res.xyz *= SPHERE_RADIUS;

    // Rotate sphere
    float angle = iTime * ROTATION_TIME_SCALE;
    float c = cos(angle);
    float s = sin(angle);
    res.xyz = mat3(c, 0.0f, -s, 0.0f, 1.0f, 0.0f, s, 0.0f, c) * res.xyz;

    return res;
}


// Samples the SDF at the given coordinates at the given time
float sdfAt(vec3 coords, float time){

    float value = abs(CAMERA_OFFSET) + 2.0 * SPHERE_RADIUS;

    vec4 ballInfoA, ballInfoB;

    ballInfoA = getBallInfo(0, time);
    ballInfoB = getBallInfo(1, time);
    float s0 = opSmoothUnion(
        length(ballInfoA.xyz - coords) - ballInfoA.w,
        length(ballInfoB.xyz - coords) - ballInfoB.w,
        0.25f);

    ballInfoA = getBallInfo(2, time);
    ballInfoB = getBallInfo(3, time);
    float s1 = opSmoothUnion(
        length(ballInfoA.xyz - coords) - ballInfoA.w,
        length(ballInfoB.xyz - coords) - ballInfoB.w,
        0.25f);

    ballInfoA = getBallInfo(4, time);
    ballInfoB = getBallInfo(5, time);
    float s2 = opSmoothUnion(
        length(ballInfoA.xyz - coords) - ballInfoA.w,
        length(ballInfoB.xyz - coords) - ballInfoB.w,
        0.25f);

    ballInfoA = getBallInfo(6, time);
    ballInfoB = getBallInfo(7, time);
    float s3 = opSmoothUnion(
        length(ballInfoA.xyz - coords) - ballInfoA.w,
        length(ballInfoB.xyz - coords) - ballInfoB.w,
        0.25f);

    float t0 = opSmoothUnion(s0, s1, 0.25f);
    float t1 = opSmoothUnion(s2, s3, 0.25f);

    value = opSmoothUnion(t0, t1, 0.25f);

    // For some reason I have to unroll this loop (see above)
    // because (at least on my system) I get artifacts otherwise
    // The smoothing factor is different for cosmetic reasons
    // for (int i = 0; i < METABALLS; ++i){
    // 
    //     vec4 ballInfo = getBallInfo(i, time);
    //     vec3 delta = ballInfo.xyz - coords;
    //     
    //     value = opSmoothUnion(value, length(delta) - ballInfo.w, 0.25f);
    // }

    return value;
}


// Gets the full-alpha color of the sphere by sphere-marching
vec3 getSphereColor(vec3 start, vec3 end, vec3 backgroundColor){

    vec3 diff = end - start;
    float maxDist = length(diff);
    vec3 dir = diff / maxDist;

    float sdf;
    float dist = 0.0f;
    vec3 color = backgroundColor;

    for (int i = 0; dist < maxDist && i < MAX_ITERATIONS; ++i){

        sdf = sdfAt(start + dist * dir, iTime * FLOW_TIME_SCALE);

        if (abs(sdf) <= SDF_EPSILON){
            color = mix(color, colorGradient(0.75f), 1.0f - dist / SPHERE_RADIUS);
            break;
        }

        color = mix(color, colorGradient(0.5f - 2.0f * sdf), 1.0f - dist);

        dist += sdf;
    }

    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float resolution = min(iResolution.x, iResolution.y);

    // The location of the pixel on the XY plane
    vec2 viewPlaneLocation = (fragCoord - iResolution.xy* 0.5f) / vec2(resolution, resolution);

    vec3 cameraPosition = vec3(0.0f, 0.0f, CAMERA_OFFSET);

    // The direction of the pixel from the camera
    vec3 dir = normalize(vec3(viewPlaneLocation, 0.0f) - cameraPosition);

    // The vector from the camera to the closest point on the ray from the origin
    vec3 toClosest = dir * dot(dir, -cameraPosition);

    // The distance from the camera to the closest point on the ray from the origin
    float tcd = sqrt(dot(toClosest, toClosest));

    // The vector from the origin to the closest point on the ray
    vec3 closest = cameraPosition + toClosest;

    // The square distance from the origin to the closest point on the ray
    float csd = dot(closest, closest);

    // The distance from the origin to the closest point on the ray
    float cd = sqrt(csd);

    // The distance travelled by the ray inside of the sphere to the point
    // closest to the origin, which is half the distance it travels there total
    float ttcd = sqrt(abs(SPHERE_RADIUS * SPHERE_RADIUS - csd));

    // The start and end of the ray through the sphere
    vec3 start = cameraPosition + dir * (tcd - ttcd);
    vec3 end   = cameraPosition + dir * (tcd + ttcd);

    vec3 backgroundColor = BACKGROUND_COLOR;
    vec3 color = cd <= SPHERE_RADIUS ? getSphereColor(start, end, backgroundColor) : backgroundColor;

    // Previous version, marching with a fixed stride
    // vec3 color = backgroundColor;
    // for (float depth = 3.0f; depth > 1.0f; depth -= 0.0625)
    //     color = march(color, cameraPosition + depth * dir);

    // Take account of sphere depth
    color = mix(backgroundColor, color, ttcd / SPHERE_RADIUS);

#if SPECULAR_HIGHLIGHT != 0
    float plAngle = -iTime * ROTATION_TIME_SCALE;
    float plc = cos(plAngle);
    float pls = sin(plAngle);

    vec3 pointLightPosition = mat3(plc, 0.0f, -pls, 0.0f, 1.0f, 0.0f, pls, 0.0f, plc) * POINT_LIGHT_POSITION;
    vec3 pointLightDirection = -normalize(pointLightPosition - start);
    vec3 normal = normalize(start);
    float nDotPLD = dot(normal, pointLightDirection);
    vec3 reflection = nDotPLD * 2.0f * normal - pointLightDirection;

    float specularAmount = nDotPLD <= 0.0f ? dot(dir, reflection) : 0.0f;
    for (int i = 0; i < SPECULAR_DEGREE - 1; ++i)
        specularAmount *= specularAmount;
    color = cd <= SPHERE_RADIUS ? mix(color, vec3(1.0f, 1.0f, 1.0f), specularAmount) : color;
#endif

    // Output to screen
    fragColor = vec4(color, 1.0);
}

