//画线函数,p为uv,即屏幕上任意一点,ab为线段两端点,size是线段宽,
//size-1.5/iResolution.y是线段边缘模糊百分比,color是线段颜色
vec3 DrawLine(vec2 uv,vec2 a,vec2 b,float size,vec3 color)
{
    vec2 ap = uv-a;
    vec2 ab = b-a;

    float t = dot(ap,ab)/dot(ab,ab);//点p在线段上投影占ab的百分比
    t = clamp(t,0.0,1.0);

    //ap-ab*t表示点p到线段ab的垂点,即d是点p到线段的距离
    float d = length(ap-ab*t);
    float s = S(size,d);

    return s*color;
}
vec3 DrawPoint(vec2 uv,vec2 p,float size,vec3 color)
{
    float d = length(uv-p);
    d = S(size,d);

    return d*color;
}
//画Beizer曲线,abc为二次Bezier曲线的三个控制点，通过一个个点表示曲线,
//size为距离点的大小以及模糊程度,color为每个点的颜色
vec3 Bezier(vec2 uv,vec2 a,vec2 b,vec2 c,float size,vec3 color)
{
    vec2 p1 = vec2(0.0);
    vec2 p2 = vec2(0.0);
    vec2 p = vec2(0.0);
    vec3 col = vec3(0.0);
    for(float t = 0.0;t<=1.0;t+=0.003)
    {
        p1 = mix(a,b,t);
        p2 = mix(b,c,t);
        p = mix(p1,p2,t);
        col += DrawPoint(uv,p,size,color);
    }
    return col;
}
//画Beizer曲线,abcd为三次Bezier曲线的三个控制点，通过一个个点表示曲线,
//size为距离点的大小,color为每个点的颜色
vec3 Bezier(vec2 uv,vec2 a,vec2 b,vec2 c,vec2 d,float size,vec3 color)
{
    vec2 p1 = vec2(0.0);
    vec2 p2 = vec2(0.0);
    vec2 p3 = vec2(0.0);

    vec2 p11 = vec2(0.0);
    vec2 p12 = vec2(0.0);

    vec2 p = vec2(0.0);
    vec3 col = vec3(0.0);

    for(float t = 0.0;t<=1.0;t+=0.003)
    {
        p1 = mix(a,b,t);
        p2 = mix(b,c,t);
        p3 = mix(c,d,t);

        p11 = mix(p1,p2,t);
        p12 = mix(p2,p3,t);

        p = mix(p11,p12,t);
        col += DrawPoint(uv,p,size,color);
    }
    return col;
}

