        col += DrawPoint(uv,a,size,vec3(1.0));
        col += DrawPoint(uv,b,size,vec3(1.0));
        col += DrawPoint(uv,c,size,vec3(1.0));

        //Draw Two Line,三个控制的形成的两条直线
        col += DrawLine(uv,a,b,size/3.0,vec3(1.0,0.0,1.0));
        col += DrawLine(uv,b,c,size/3.0,vec3(1.0,0.0,1.0));

        float t = fract(iTime/5.0);//Time,每五秒一个循环

        //两条直线上的某一个点,随t的变化而变化(用t值进行插值得到)
        vec2 p1 = mix(a,b,t);
        col += DrawPoint(uv,p1,size,vec3(1.0));
        vec2 p2 = mix(b,c,t);
        col += DrawPoint(uv,p2,size,vec3(1.0));

        //画出p1与p2的连线
        col += DrawLine(uv,p1,p2,size/3.0,vec3(1.0,1.0,0.0));

        //同样用t在直线p1与p2直接进行插值得到的点就是Bezier曲线上的点
        vec2 p = mix(p1,p2,t);
        col += DrawPoint(uv,p,size,vec3(1.0));

        //用相同的方法画出Bezier曲线用于对比
        col += Bezier(uv,a,b,c,size/3.0,vec3(1.0));
    }
    else  //否则画三次Bezier曲线
    {
        //Draw Four Point,四个控制点
        vec2 a = vec2(0.1*w,0.15);
        vec2 b = vec2(0.3*w,0.85);
        vec2 c = vec2(0.65*w,0.8);
        vec2 d = vec2(0.8*w,0.25);

        col += DrawPoint(uv,a,size,vec3(1.0));
        col += DrawPoint(uv,b,size,vec3(1.0));
        col += DrawPoint(uv,c,size,vec3(1.0));
        col += DrawPoint(uv,d,size,vec3(1.0));

        //Draw Three Line,四个控制的形成的三条直线
        col += DrawLine(uv,a,b,size/3.0,vec3(1.0,0.0,1.0));
        col += DrawLine(uv,b,c,size/3.0,vec3(1.0,0.0,1.0));
        col += DrawLine(uv,c,d,size/3.0,vec3(1.0,0.0,1.0));

        float t = fract(iTime/5.0);//Time,每五秒一个循环

        //三条直线上的某一个点,随t的变化而变化(用t值进行插值得到)
        vec2 p1 = mix(a,b,t);
        col += DrawPoint(uv,p1,size,vec3(1.0));
        vec2 p2 = mix(b,c,t);
        col += DrawPoint(uv,p2,size,vec3(1.0));
        vec2 p3 = mix(c,d,t);
        col += DrawPoint(uv,p3,size,vec3(1.0));

        //画出p1,p2,p3的连线
        col += DrawLine(uv,p1,p2,size/3.0,vec3(1.0,1.0,0.0));
        col += DrawLine(uv,p2,p3,size/3.0,vec3(1.0,1.0,0.0));

        //同样用t在直线p1,p2,p3直接进行插值得到两个点
        vec2 p11 = mix(p1,p2,t);
        col += DrawPoint(uv,p11,size,vec3(1.0));
        vec2 p12 = mix(p2,p3,t);
        col += DrawPoint(uv,p12,size,vec3(1.0));
        //画得到的这两个点的曲线
        col += DrawLine(uv,p11,p12,size/3.0,vec3(0.0,1.0,1.0));

        //同样用t在直线p11与p12直接进行插值得到的点就是Bezier曲线上的点
        vec2 p = mix(p11,p12,t);
        col += DrawPoint(uv,p,size,vec3(1.0));

        //用相同的方法画出Bezier曲线用于对比
        col += Bezier(uv,a,b,c,d,size/3.0,vec3(1.0));
    }

    col = pow(col,vec3(1.0/2.2));

    fragColor = vec4(col,1.0);
}

