const int raymarchsteps = 150;
float sdSphere(vec3 p, float r){
    return length(p) - r;
}
float sdHoles(vec3 p){
    const float c = .17;
    p += vec3(.4163,1.62,1.46);
    p = mod(p+c, 2.*c)-c;
    return sdSphere(p,.14);
}
float sdCheeze(vec3 p, out float h){
    p.z = -p.z;
    p -= vec3(-0.4,-0.65,0.);
    p.xz *= rot(0.65);
    float base;
    {
        float d = sdTri(p.xz, vec2(.5, 1.3));
        vec2 w = vec2( d, abs(p.y) - .35 );
        base =  min(max(w.x,w.y),0.0) + length(max(w,0.0))-0.02;
    }
    h = sdHoles(p);
    float d = ssub(h, base,0.02);
    return d;
}

float sdOlive(vec3 p){
    p -= vec3(.4,-0.82,0.);
    p.x = abs(p.x)-0.12;
    float l = length(p.xy);
    return sdEllipse(vec2(l,p.z), vec2(.13,0.2));
}

float map(vec3 p){
    float _;
    float d = sdCheeze(p,_);
    d = smin(d, p.y+1.,0.02);
    d = min(d,sdOlive(p));
    return d;
}

float intersection(vec3 ro, vec3 rd){
    float T=0.;
    for(int i=0;i<raymarchsteps;i++){
        float d = map(ro+rd*T);
        T += d;
        if (abs(d) < 0.001*T  || T > 20.){
            break;
        }
    }
    return T;
}

float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k ){
    float res = 1.0;
    for( float t=mint; t<maxt; )
    {
        float h = map(ro + rd*t);
        if( h<0.001 )
            return 0.0;
        res = min( res, k*h/t );
        t += h;
    }
    return res;
}
vec3 calcNormal( in vec3 p , in float T){
    float h = 0.01*T;
    vec3 n = vec3(0.0);
    for( int i=0; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*map(p+e*h);
    }
    return normalize(n);
}
float calcAO(vec3 pos, vec3 nor){
	float occ = 0.0;
    float sca = .4;
    for( int i=0; i<5; i++ )
    {
            float h = 0.01 + 0.25*float(i)/4.0;
        float d = map( pos+h*nor);
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );
}

vec3 triTex(vec3 p, vec3 nor, sampler2D tex){
    vec3 x = texture(tex, p.yz).xyz;
    vec3 y = texture(tex, p.xz).xyz;
    vec3 z = texture(tex, p.xy).xyz;

    return x*nor.x + y*nor.y + z*nor.z;
}

vec3 render(vec3 ro, vec3 rd){
    float T = intersection(ro, rd);
    vec3 p = ro+rd*T;
    float dist = map(p);
    float holes;
    vec3 col = vec3(0.);
    float chez = sdCheeze(p, holes);
    float olive = sdOlive(p);
    vec3 sun = normalize(vec3(5,5.,-1.4));
    vec3 scl = vec3(0.925,0.906,0.792);
    vec3 ccl = vec3(0.949,0.831,0.251);
    vec3 gcl = vec3(0.867,0.851,0.851);
    vec3 ocl = vec3(0.463,0.867,0.192);
    bool isCheeze = chez < p.y+1.005;
    float fisCheeze = float (isCheeze);
    vec3 bcl = isCheeze ? ccl : gcl;
    bool isOlive = false;
    if (olive < chez && p.y > -.9){
        bcl = ocl;
        isOlive = true;
    }
    if (dist < 1.2){

        vec3 nor = calcNormal(p, T);
        float occ = calcAO(p, nor);
        float sha = softshadow(p, sun, 0.1, 5., 10.);
        float nds = sqrt(max(0., dot(nor, sun)));
        vec3 hvc = normalize(sun - rd);
        vec3 ref = reflect(rd, nor);
        float frez = .5+.5*dot(rd, ref);
        float inchez = clamp(1.-15.*chez, 0., 1.);

        vec3 tex = triTex(p, nor, iChannel1);

        bcl += isCheeze ? tex*0.3 : tex*-0.1;
        col += 6.*bcl*scl*nds*sha*scl;
        col += 2.*max(0.00001, pow(dot(nor, hvc),3.))*sha;
        col += 3.*max(0.5,dot(nor, -sun))*gcl*bcl*scl*occ;
        col += 3.*clamp(getSky(ref,sun),0.,1.)*occ*0.75*(.5+.5*dot(nor,vec3(0,1,0)))*frez;
        col += frez*( !isOlive ? vec3(0.902,0.176,0.176): vec3(0.000,0.000,0.000) )*sha*3.;
        col += 3.5*vec3(0.878,0.278,0.024)*(max(0.5,1.-20.*holes))*inchez;
        col += scl*ocl*smoothstep(.5, 0., olive);
    } else {
        col = getSky(rd, sun)*5.;
    }
    return col;
}

