// Hash by Dave_Hoskins
// Filmic tonemapping curve by milesWaugh

vec3 Texture0(vec2 uv) {
    // Multiplying the texture by various values to maintain consistency. Also there's a hand-picked constant.
    return (lightIntensity * 100. * texture(iChannel0, ((uv/scale)/2. + 0.5)).rgb)/float(attempts);
}

vec3 lighting(vec3 ray, vec3 rayDir, vec3 rayOrigin, vec3 surfNormal, float f) {
    diffuse = 0.;
    specular = 0.;

    sceneLights(ray, rayDir, surfNormal);

    vec3 finalCol = vec3(0.);

    if (abs(ray.y - (-1. - epsilon)) < 0.05) {
        diffuse += float(Texture0(ray.xz));
    }

    if (surfType == 0) {
        finalCol = mix(surfCol * (diffuse + ambient), vec3(specular), f);
    }
    else if (surfType == 1) {
        finalCol = mix(surfCol * specular, vec3(specular), f);
    }
    else if (surfType == 2) {
        finalCol = vec3(specular) * f;
    }

    return finalCol;
}

vec3 renderFromBackwardRay(vec3 rayDir, vec3 rayOrigin) {
    vec3 contribution = vec3(1.0);
    vec3 combinedCol;
    float totalDepth = 0.;
    float foga;
    bool newMedium = false;
    for (int i = 0; i < bounces; i++) {
        vec4 rayData = shootRay( rayDir, rayOrigin, renderDist, true );

        if (rayData.x == 1.) {
            totalDepth += distance(rayData.yzw, rayOrigin);
            float f = fresnel(1.5, surfRoughness, surfNormal, -rayDir);
            combinedCol += contribution * lighting(rayData.yzw, rayDir, rayOrigin, surfNormal, f);
            if (surfType == 0) {
                break;
                //contribution *= f;
            }
            else if (surfType == 1) {
                vec4 rayData1 = shootRay( reflect(rayDir, surfNormal), rayData.yzw + epsilon * surfNormal, renderDist, true );
                combinedCol += f * lighting(rayData1.yzw, rayDir, rayData.yzw + epsilon * surfNormal, surfNormal, f);
                break;
                //contribution *= mix(surfCol, vec3(1.), vec3(f));
            }
            if (surfType == 2) {
                vec3 rayDir0 = rayDir;
                vec3 surfNormal0 = surfNormal;
                contribution *= surfCol * (1. - f);
                // One bounce of glossy reflections
                vec4 rayData1 = shootRay( reflect(rayDir, surfNormal), rayData.yzw + epsilon * surfNormal, renderDist, true );
                combinedCol += f * lighting(rayData1.yzw, rayDir, rayData.yzw + epsilon * surfNormal, surfNormal, f);
                rayDir = rayDir0;
                surfNormal = surfNormal0;

                rayDir = refract(rayDir, surfNormal, mix(1./1.5, 1.5, newMedium));
                newMedium = !newMedium;

                rayOrigin = rayData.yzw - epsilon * surfNormal;
            }
            else {
                break;
            }
        }
        else {
            combinedCol += contribution * ambient;
            break;
        }
    }
    return combinedCol;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float cosTime = cos(iTime/2.);
    cosTime = 6.*-cosTime+6.;
    camera = vec3(0, 0, -7);

    vec2 mouse = iMouse.xy/iResolution.xy - vec2(0.5);

    if (mouse == vec2(-0.5)) {
        mouse = vec2(-0.15, -0.12);
    }

    mouse.y = min(0.03, mouse.y); // Constrain camera rotation

    camera.yz *= rotate(pi * -mouse.y);
    camera.xz *= rotate(2. * pi * -mouse.x);

    camera.y = max(-1., camera.y);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - iResolution.xy/2.)/iResolution.x;
    vec2 UV = fragCoord/iResolution.xy;
    vec3 rayDir = rayDirection( focalLength, uv );

    rayDir.yz *= rotate(pi * -mouse.y);
    rayDir.xz *= rotate(2. * pi * -mouse.x);

    vec3 col = renderFromBackwardRay(rayDir, camera);

    //col = Texture0(10. * (UV - 0.5)); // The caustics texture

    // Output to screen, filmic tonemapping, and gamma correction.
    col = filmic(col);
    fragColor = vec4(pow(col,vec3(1./2.2)),1.0);
}

