void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int id = int(fragCoord.x) + int(iResolution.x) * int(fragCoord.y) + 50;
    if (id > samples && id > 0) {
        fragColor = vec4(0.0,0.0,0.0,1.0);
        return;
    }
    else {
        vec2 uv = fragCoord/iResolution.xy;
        mat3 render;
        for (int i = 0; i < attempts; i++) { // Rejection sampling, converges much faster but even more bias
            float seed = 10.23 * (float(iFrame) + 10.4341 * float(i));
            vec3 rayDir = randInSphere(vec3(uv * 10. * seed, seed/1.));
            render = renderFromForwardRay(rayDir, lightPos);
            if (render[1].x > 0.) {
                break;
            }
        }
        vec3 hit = vec3(render[1].x, render[2].xz); // vec3(brightness, hit position x, hit position z)
        fragColor = vec4(hit,render[0].x); // vec4(brightness, hit position x, hit position z, hit glass?)
    }
}

