float diffuse = 0.;
float specular;
vec3 ambient = vec3(0.1);

mat2 rotate(float a) {
    float sinA = sin(a);
    float cosA = cos(a);
    return mat2(cosA, -sinA, sinA, cosA);
}

vec3 rand(vec3 p3) // Hash by Dave_Hoskins
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);

}

// Smooth tricubic interpolated 3D noise
vec3 noise(vec3 p3) {

    vec3 p31 = floor(p3);
    p3 = smoothstep(0., 1., fract(p3));

    // Corner points
    vec3 p000 = p31 + vec3(0);
    vec3 p001 = p31 + vec3(0, 0, 1);
    vec3 p010 = p31 + vec3(0, 1, 0);
    vec3 p011 = p31 + vec3(0, 1, 1);
    vec3 p100 = p31 + vec3(1, 0, 0);
    vec3 p101 = p31 + vec3(1, 0, 1);
    vec3 p110 = p31 + vec3(1, 1, 0);
    vec3 p111 = p31 + vec3(1);


    // Tricubic interpolation
    vec3 v000 = rand(p000);
    vec3 v001 = rand(p001);
    vec3 v010 = rand(p010);
    vec3 v011 = rand(p011);
    vec3 v100 = rand(p100);
    vec3 v101 = rand(p101);
    vec3 v110 = rand(p110);
    vec3 v111 = rand(p111);

    vec3 vX00 = mix(v000, v100, p3.x);
    vec3 vX01 = mix(v001, v101, p3.x);
    vec3 vX10 = mix(v010, v110, p3.x);
    vec3 vX11 = mix(v011, v111, p3.x);

    vec3 vXY0 = mix(vX00, vX10, p3.y);
    vec3 vXY1 = mix(vX01, vX11, p3.y);

    return mix(vXY0, vXY1, p3.z);
}

vec3 randInSphere(vec3 p3) {
    for (int i = 0; i < 50; i++) {
        vec3 rand1 = 2. * (rand(p3) - 0.5);
        if (length(rand1) <= 1.) {
            return normalize(rand1);
        }
    }
    return vec3(0, -1, 0);
}

vec3 rayDirection( float focal, vec2 xy ) {
    return (normalize(vec3(xy, focal)));
}


// Intersectors

vec4 Sphere( vec3 rayOrigin, vec3 rayDir, vec3 pos, float radius, bool negative) {
    vec3 offset = pos - rayOrigin;
    float dot1 = dot(offset, rayDir);
    float dot2 = dot(offset, offset);

    if (dot2 - dot1 * dot1 < radius * radius) {
        float near = sqrt(radius * radius - (dot2 - (dot1 * dot1)));
        float intersectSign = mix(-1., 1., float(negative));
        return vec4(dot1 + intersectSign * near, intersectSign * (pos - (rayOrigin + (rayDir * (dot1 + intersectSign * near)))));
    }
    return vec4(-1., 0, 0, 0);
}

vec4 Plane( vec3 rayOrigin, vec3 rayDir, vec3 normal, float dist ) {
    float dot1 = dot(normal, rayDir);
    float near = (dot(normal, rayOrigin) + dist)/dot1;
    if (!(dot1 == 0.) && near < 0.)
    {
        return vec4(-near, normal);
    }
    return vec4(-1., 0, 0, 0);
}

vec4 Plane2( vec3 rayOrigin, vec3 rayDir, vec3 normal, float dist ) {
    float dot1 = dot(normal, rayDir);
    float near = (dot(normal, rayOrigin) + dist)/dot1;
    if (!(dot1 == 0.) && near < 0. && length((rayOrigin + rayDir * -near).xz + vec2(2., 0.)) < 1.5)
    {
        return vec4(-near, normal);
    }
    return vec4(-1., 0, 0, 0);
}

void addIntersector( vec4 surface, vec3 col, float roughness, int type /* 0=dielectric, 1=metallic, 2=glass */, int textureType, vec3 textureScale, vec3 rayDir, vec3 start) {
    float dist = surface.x;
    if ( dist < nearestDist && dist > 0. ) {
        nearestDist = dist;
        if (updateMat) {
            surfCol = col;
            if (textureType == 0) {

            }
            else if (textureType == 1) {
                vec3 uvw = rayDir * dist + start;
                vec3 rUVW = round(textureScale * uvw);
                float checker = mod(rUVW.x + rUVW.y + rUVW.z, 2.);
                surfCol = col * mix(0.4, 1., checker);
            }
            surfRoughness = roughness*roughness;
            surfNormal = normalize(surface.yzw);
            surfType = type;
        }
    }
}

float scene( vec3 rayOrigin, vec3 rayDir, vec3 start)
{

    addIntersector( Sphere( rayOrigin, rayDir, vec3(1, 0, 0), 1., false), vec3(1.,1., 1.), .05, 2, 0, vec3(1.), rayDir, start);
    addIntersector( Sphere( rayOrigin, rayDir, vec3(1, 0, 0), 1., true), vec3(1.,1., 1.), .05, 2, 0, vec3(1.), rayDir, start);

    //addIntersector( Sphere( rayOrigin, rayDir, vec3(-3, 0.2, 0), 1., false), vec3(1.,1., 1.), .05, 2, 0, vec3(1.), rayDir, start);
    //addIntersector( Sphere( rayOrigin, rayDir, vec3(-3, 0.2, 0), 1., true), vec3(1.,1., 1.), .05, 2, 0, vec3(1.), rayDir, start);

    addIntersector( Plane( rayOrigin, rayDir, vec3(0, 1, 0), 1. + epsilon), vec3(0.3, 1., 0.3), 1., 0, 1, vec3(0.55), rayDir, start);
    //addIntersector( Plane( rayOrigin, rayDir, vec3(0, 1, 0), 1. + epsilon), vec3(1., 0.6, 0.2), 1., 0, 1, vec3(0.55), rayDir, start);

    addIntersector( Plane2( rayOrigin, rayDir, vec3(0, 1, 0), -.5), vec3(1.,1., 1.), .05, 2, 0, vec3(1.), rayDir, start);

    return nearestDist;
}

vec4 shootRay( vec3 rayDir, vec3 start, float renderDist, bool updateMaterial ) {
    updateMat = updateMaterial;

    nearestDist = renderDist;

    scene(start, rayDir, start);

    vec3 hitPos = rayDir * nearestDist + start;

    if (updateMaterial) {
        if (surfType == 2) {
            surfNormal += bumpStrength * noise(7. * hitPos);
            surfNormal = normalize(surfNormal);
        }
        //surfCol = 1. * fract(noise(1. * hitPos));
    }


    return vec4(nearestDist < renderDist, hitPos);
}

float GGX (float halfDot, float roughness) {
    float r2 = roughness*roughness;
    float a = halfDot * halfDot * (r2 - 1.) + 1.;
    return r2 / (a * a);
}

void directionalLight(vec3 ray, vec3 rayDir, vec3 lightDir, vec3 surfNormal, float intensity, float lightDist) {
    if ( shootRay(lightDir, ray + (surfNormal * epsilon), lightDist, false).x == 0. ) {
        float lambertian = dot(surfNormal, lightDir);
        diffuse += max(intensity * lambertian, 0.);

        vec3 halfway = normalize(lightDir - rayDir);
        float halfDot = dot(surfNormal, halfway);
    	specular += max(lambertian * intensity * GGX(halfDot, surfRoughness), 0.);

    }
}

void pointLight( vec3 ray, vec3 rayDir, vec3 position, float intensity, vec3 surfNormal ) {
    vec3 lightDir = normalize(position - ray);
    directionalLight( ray, rayDir, lightDir, surfNormal, intensity, distance(position, ray) );
}

void sceneLights(vec3 ray, vec3 rayDir, vec3 surfNormal) {
    pointLight( ray, rayDir, lightPos, lightIntensity, surfNormal);
}

// My implementation of the tonemapping curve by milesWaugh
vec3 filmic(vec3 color) {
    return 1. - (1./(5. * pow(color, vec3(2.)) + 1.));
}

float fresnel(float IOR, float roughness, vec3 surfNormal, vec3 incoming) {
    float F0 = (1. - IOR)/(1. + IOR);
    F0 *= F0;
    return F0 + (1. - roughness)*((1. - F0) * pow(1. - dot(surfNormal, incoming),5.));
}

mat3 renderFromForwardRay(vec3 rayDir, vec3 rayOrigin) {
    vec3 remainingCol = vec3(1.);
    float totalDepth = 0.;
    float foga;
    bool newMedium = false;
    bool hitGlass = false;
    for (int i = 0; i < bounces; i++) {
        vec4 rayData = shootRay( rayDir, rayOrigin, renderDist, true );

        if (rayData.x == 1.) {
            totalDepth += distance(rayData.yzw, rayOrigin);
            float f = fresnel(1.5, surfRoughness, surfNormal, -rayDir);
            if (surfType == 2) {
                hitGlass = true;
                remainingCol *= surfCol * (1. - f);

                rayDir = refract(rayDir, surfNormal, mix(1./1.5, 1.5, newMedium));
                newMedium = !newMedium;

                rayOrigin = rayData.yzw - epsilon * surfNormal;
            }
            else {
                return mat3(vec3(rayData.x), remainingCol * dot(-rayDir, surfNormal) * float(hitGlass), rayData.yzw);
            }
        }
        else {
            return mat3(0);
        }
    }
    return mat3(0);
}

