    int num = 0;
    for (int i = 0; i < samples; i++) {
        vec4 c = texelFetch(iChannel0, ivec2(mod(float(i), iResolution.x), float(i) / iResolution.x), 0);
        if (c.w == 1.) {
            float light = c.x;
            vec2 pos = c.yz;
            vec2 diff = (pos / scale) - uv;
            if (length(diff) < radius) {
                total += c.xxx;
                num++;
            }
        }
    }
    // A hand-picked constant
    return 0.0006 * total/(pi * radius * radius);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 prevCol = texture(iChannel1, uv).rgb;
