precision highp float;

vec3 HSLtoRGB(vec3 hsl) {
    //0 <= h <= 360
    //0 <= s <= 1
    //0 <= l <= 1
    float chroma = hsl.y * (1.0 - abs(2.0 * hsl.z - 1.0));
    float h_prime = hsl.x / 60.0;
    float x = chroma * (1.0 - abs(mod(h_prime, 2.0) - 1.0));
    vec3 rgb;
    if (0.0 <= h_prime && h_prime < 1.0) {
        rgb = vec3(chroma, x, 0.0);
    } else if (1.0 <= h_prime && h_prime < 2.0) {
        rgb = vec3(x, chroma, 0.0);
    } else if (2.0 <= h_prime && h_prime < 3.0) {
        rgb = vec3(0.0, chroma, x);
    } else if (3.0 <= h_prime && h_prime < 4.0) {
        rgb = vec3(0.0, x, chroma);
    } else if (4.0 <= h_prime && h_prime < 5.0) {
        rgb = vec3(x, 0.0, chroma);
    } else {
        rgb = vec3(chroma, 0.0, x);
    }
    float m = hsl.z - 0.5 * chroma;
    return rgb + m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float zoom = 3.0;
    vec2 center = vec2(0.0);
    vec2 z = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.x * zoom + center;
    float p = 2.0 + iTime * 0.2;
    float iter = 0.0;
    const float max_iter = 256.0;
    while (iter < max_iter) {
        vec2 oldz = z;
        vec2 f = power(z, p) - vec2(1.0, 0.0); //Function f(z)
        vec2 df = p * power(z, p - 1.0); //f'(z)
        vec2 ddf = p * (p - 1.0) * power(z, p - 2.0); //f"(z)
        z = z - div(2.0 * mul(f, df), 2.0 * sqr(df) - mul(f, ddf));

        float r = mag2(z - oldz);
        if (r < 1.0e-6) {
            break;
        }
        iter += 1.0;
    }

    if (iter >= max_iter) {
        fragColor = vec4(vec3(0.0), 1.0);
    } else {
        float h = arg(z) / pi;
        if (h < 0.0) {
            h = h + 2.0;
        }
        vec3 col = HSLtoRGB(vec3(h * 180.0, 1.0, 0.5));
        fragColor = vec4(col, 1.0);
    }
}

common_code:
/*
Constants

 one = vec2(1., 0.)
 i = vec2(0., 1.
 pi = 3.14159265358979
 e = 2.718281828459045

Complex functions

 mag2(z) = |z|^2 = dot(z, z)
 arg(z) = atan2(z.y, z.x)
 conj(z) = (z.x, -z.y)
 flip(z) = (-z.x, z.y)
 rabs(z) = (abs(z.x), z.y)
 iabs(z) = (z.x, abs(z.y))
 cis(x) = (cos(x), sin(x))
 mul(z, w) = z * w (Complex multiplication)
 mulI(z) = i * z
 div(z, w) = z / w (Complex division)
 recip(z) = 1 / z (Complex reciprocal)
 sqr(z) = z^2
 normalizesqr(z) = normalize(z^2)
 cube(z) = z^3
 power5(z) = z^5
 power7(z) = z^7
 power(z, x) = z^x (Complex exponentiation)
 powervec2(z, w) = z^w (Principal complex exponentiation)
 powerfv(x, z) = x^z (Complex exponentiation)
 sqrtp(z) = sqrt(z) (Principal square root)
 cbrt(z) = z^(1/3) (Principal cube root)
 powexp(z) = e^z (Complex exponential)
 ln(z) = log(z) (Complex logarithm)
 logb(z, w) = log(z) / log(w)
 sine(z) = sin(z) (Trig functions)
 cosine(z) = cos(z)
 tangent(z) = tan(z)
 cot(z) = 1 / tan(z)
 sec(z) = 1 / cos(z)
 csc(z) = 1 / sin(z)
 arcsin(z) = asin(z) (Inverse trig functions)
 arccos(z) = acos(z)
 arctan(z) = atan(z)
 arccot(z) = pi/2 - atan(z)
 arcsec(z) = acos(1 / z)
 arccsc(z) = asin(1 / z)
 sinhyp(z) = sinh(z) (Hyperbolic functions)
 coshyp(z) = cosh(z)
 tanhyp(z) = tanh(z)
 coth(z) = 1 / tanh(z)
 sech(z) = 1 / cosh(z)
 csch(z) = 1 / sinh(z)
 arcsinh(z) = asinh(z) (Inverse hyperbolic functions)
 arccosh(z) = acosh(z)
 arctanh(z) = atanh(z)
 arccoth(z) = acoth(z)
 arcsech(z) = asech(z)
 arccsch(z) = acsch(z)
 linear(z, a, b) = a * z + b
 quadratic(z, a, b, c) = a * z^2 + b * z + c
 cubic(z, a, b, c, d) = a * z^3 + b * z^2 + c * z + d
 gamma(z) (Gamma function)
 productlog(k, z) (Lambert W function (kth branch))
 digamma(z) (Digamma function)
 trigamma(z) (Trigamma function)
*/
#define one vec2(1., 0.)
#define two vec2(2., 0.)
#define i vec2(0., 1.)
#define pi 3.14159265358979
#define e 2.718281828459045
//const float[] B = float[](1., .5, 1./6., 0., -1./30., 0., 1./42., 0., -1./30., 0., 5./66., 0., -691./2730., 0., 7./6.);

float mag2(in vec2 z) {
    return dot(z, z);
}

float arg(in vec2 z) {
    return atan(z.y, z.x);
}

vec2 conj(in vec2 z) {
    return vec2(z.x, -z.y);
}

vec2 flip(in vec2 z) {
    return vec2(-z.x, z.y);
}

vec2 rabs(in vec2 z) {
    return vec2(abs(z.x), z.y);
}

vec2 iabs(in vec2 z) {
    return vec2(z.x, abs(z.y));
}

vec2 cis(in float t) {
    return vec2(cos(t), sin(t));
}

vec2 mul(in vec2 z, in vec2 w) {
    return z.x * w + z.y * vec2(-w.y, w.x);
}

vec2 mulI(in vec2 z) {
    return vec2(-z.y, z.x);
}

vec2 div(in vec2 z, in vec2 w) {
    return (w.x * z + w.y * vec2(z.y, -z.x)) / dot(w, w);
}

vec2 recip(in vec2 z) {
    return conj(z) / dot(z, z);
}

vec2 sqr(in vec2 z) {
    return z.x * z + z.y * vec2(-z.y, z.x);
}

vec2 normalizesqr(in vec2 z) {
    return one - conj(z.yx) * (z.y + z.y) / dot(z, z);
}

vec2 cube(in vec2 z) {
    vec2 z2 = z * z;
    return z * (vec2(z2.x - z2.y) + vec2(-2.0, 2.0) * z2.yx);
}

vec2 power5(in vec2 z) {
    vec2 z2 = z * z;
    vec2 z4 = z2 * z2;
    return z * (z4 + 5. * z4.yx - 10. * z2.x * z2.y);
}

vec2 power7(in vec2 z) {
    vec2 z2 = z * z;
    vec2 z6 = z2 * z2 * z2;
    return conj(z) * (z6 - 7. * z6.yx - z2.x * z2.y * (21. * z2 - 35. * z2.yx));
}

vec2 power(in vec2 z, in float p) {
    return pow(dot(z, z), p * .5) * cis(p * arg(z));
}

vec2 powervec2(in vec2 z, in vec2 w) {
    float argz = arg(z);
    float lnr = .5 * log(dot(z, z));
    return exp(dot(w, vec2(lnr, -argz))) * cis(dot(w, vec2(argz, lnr)));
}

vec2 powerfv(in float x, in vec2 z) {
    float lnr = log(x);
    return exp(z.x * lnr) * cis(z.y * lnr);
}

vec2 sqrtp(in vec2 z) {
    float r = length(z);
    return vec2(1., sign(z.y)) * sqrt(.5 * vec2(r + z.x, r - z.x));
}

vec2 cbrt(in vec2 z) {
    return power(z, .333333333333);
}

vec2 powexp(in vec2 z) {
    return exp(z.x) * cis(z.y);
}

vec2 ln(in vec2 z) {
    return vec2(log(mag2(z)) * .5, arg(z));
}

vec2 logb(in vec2 z, in vec2 b) {
    return div(ln(z), ln(b));
}

vec2 sine(in vec2 z) {
    return vec2(sin(z.x) * cosh(z.y), cos(z.x) * sinh(z.y));
}

vec2 cosine(in vec2 z) {
    return vec2(cos(z.x) * cosh(z.y), -sin(z.x) * sinh(z.y));
}

vec2 sub_tan(in vec2 z) {
    return div(vec2(0.0, 2.0), powexp(mulI(z + z)) + one) - i;
}

vec2 tangent(in vec2 z) {
    if (z.y < -44.) {
        return conj(sub_tan(conj(z)));
    } else {
        return sub_tan(z);
    }
}

vec2 sub_cot(in vec2 z) {
    return div(vec2(0.0, 2.0), powexp(mulI(z + z)) - one) + i;
}

vec2 cot(in vec2 z) {
    if (z.y < -44.) {
        return conj(sub_cot(conj(z)));
    } else {
        return sub_cot(z);
    }
}

vec2 sec(in vec2 z) {
    return recip(cosine(z));
}

vec2 csc(in vec2 z) {
    return recip(sine(z));
}

vec2 arcsin(in vec2 z) {
    return mulI(ln(sqrtp(one - sqr(z)) - mulI(z)));
}

vec2 arccos(in vec2 z) {
    return -mulI(ln(mulI(sqrtp(one - sqr(z))) + z));
}

vec2 arctan(in vec2 z) {
    return -0.5 * mulI(ln(div(i - z, i + z)));
}

vec2 arccot(in vec2 z) {
    return -0.5 * mulI(ln(div(z + i, z - i)));
}

vec2 arcsec(in vec2 z) {
    return arccos(recip(z));
}

vec2 arccsc(in vec2 z) {
    return arcsin(recip(z));
}

vec2 sinhyp(in vec2 z) {
    return vec2(sinh(z.x) * cos(z.y), cosh(z.x) * sin(z.y));
}

vec2 coshyp(in vec2 z) {
    return vec2(cosh(z.x) * cos(z.y), sinh(z.x) * sin(z.y));
}

vec2 sub_tanh(in vec2 z) {
    return one - div(two, powexp(z + z) + one);
}

vec2 tanhyp(in vec2 z) {
    if (z.x > 44.) {
        return flip(sub_tanh(flip(z)));
    } else {
        return sub_tanh(z);
    }
}

vec2 sub_coth(in vec2 z) {
    return one + div(two, powexp(z + z) - one);
}

vec2 coth(in vec2 z) {
    if (z.x > 44.) {
        return flip(sub_coth(flip(z)));
    } else {
        return sub_coth(z);
    }
}

vec2 sech(in vec2 z) {
    return recip(coshyp(z));
}

vec2 csch(in vec2 z) {
    return recip(sinhyp(z));
}

vec2 arcsinh(in vec2 z) {
    return ln(z + sqrtp(sqr(z) + one));
}

vec2 arccosh(in vec2 z) {
    return ln(z + mul(sqrtp(z + one), sqrtp(z - one)));
}

vec2 arctanh(in vec2 z) {
    return .5 * ln(div(one + z, one - z));
}

vec2 arccoth(in vec2 z) {
    return .5 * ln(div(z + one, z - one));
}

vec2 arcsech(in vec2 z) {
    return arccosh(recip(z));
}

vec2 arccsch(in vec2 z) {
    return arcsinh(recip(z));
}

vec2 linear(in vec2 z, in vec2 a, in vec2 b) {
    return mul(z, a) + b;
}

vec2 quadratic(in vec2 z, in vec2 a, in vec2 b, in vec2 c) {
    return mul(z, mul(z, a) + b) + c;
}

vec2 cubic(in vec2 z, in vec2 a, in vec2 b, in vec2 c, in vec2 d) {
    return mul(z, mul(z, mul(z, a) + b) + c) + d;
}

vec2 gamma(in vec2 z) {
    //https://www.shadertoy.com/view/WtlGDN
    const float[6] p = float[] (2.506628275635, 225.525584619175, -268.295973841305, 80.9030806934622, -5.007578639705, 0.0114684895435);
    vec2 zz = z.x > 1. ? z : one - z;
    vec2 m = vec2(p[0], 0.);
    for (int k = 1; k < p.length(); k++) {
        m = m + p[k] * recip(zz + vec2(k, 0.));
    }
    vec2 zh = zz + vec2(5.65, 0.0);
    vec2 w = powexp(ln(m) + mul(zz + vec2(.5, 0.), ln(zh)) - ln(zz) - zh);
    return z.x > 1. ? w : pi * recip(mul(w, sine(pi * z)));
}

vec2 productlog(in int k, in vec2 z) {
    vec2 m, w, mw, w2;
    if (k == 0) {
        //https://www.shadertoy.com/view/3lsGD4
        m = sqrtp(2. * e * z + vec2(2., 0.));
        w = 2. * ln(one + .8842 * m);
        w = div(w - ln(one + .9294 * ln(one + .5106 * m)) - vec2(1.213, 0.), one + recip(w + vec2(4.69483568, 0.)));
    } else {
        w = ln(z) + float(2 * k) * pi * i;
        w -= ln(w);
    }
    for (int k = 0; k < 8; k++) {
        m = powexp(w);
        mw = mul(m, w);
        w2 = w + two;
        w = div(mul(mw, sqr(w)) + mul(sqr(w2) - two, z), mul(mw + z, w2) + (m + m));
    }
    return w;
}

vec2 digamma(in vec2 z) {
    vec2 zz = z.x < .5 ? one - z : z, a = vec2(0.), w = zz;
    while (w.x < 8.) {
        a += recip(w);
        w += one;
    }
    vec2 zinv = recip(w);
    w = ln(w) - 0.5 * zinv;
    zinv = sqr(zinv);
    w -= zinv / 12. - sqr(zinv) / 120. + cube(zinv) / 252. + a;
    return (z.x < .5 ? pi * cot(pi * zz) + w : w);
}

vec2 trigamma(in vec2 z) {
    vec2 zz = z.x < .5 ? one - z : z, a = vec2(0.), w = zz;
    while (w.x < 8.) {
        a += recip(sqr(w));
        w += one;
    }
    w = recip(w) + 0.5 * recip(sqr(w)) + recip(cube(w)) / 6. - recip(power5(w)) / 30. + recip(power7(w)) / 42. + a;
    return z.x < .5 ? sqr(pi * recip(sine(pi * zz))) - w : w;
}

