// Rough version using one axis in 2D-space: https://www.shadertoy.com/view/fdVcR1

void mainImage(out vec4 C, in vec2 O)
{
    initView(iResolution);
    vec2 uv = fromView(O);
    vec2 click = fromView(abs(iMouse.zw));
    vec2 mouse = fromView(abs(iMouse.xy));

    vec2 o = getPoint(0); // zero
    vec2 u = getPoint(1); // u-infinity
    vec2 v = getPoint(2); // v-infinity

    // g = distance(zero, one) / distance(zero, infinity)
    vec2 g  = getPoint(3);
    vec2 gu = mix(o, u, g.x); // u-one
    vec2 gv = mix(o, v, g.y); // v-one

    // gf = distance(zero, one) / distance(zero, infinity)
    vec2 gf = g / (1. - g);

    // Those are homogenous matrices, their scale does not matter:
    //   As long as you keep all signs, you can skip multiplication
    //   with the determinant during inversion
    mat3 T = mat3(vec3(u, 1.) * gf.x, vec3(v, 1) * gf.y, vec3(o, 1));
    mat3 Ti = inverse(T);

    vec2 ij = P(Ti * vec3(uv, 1));

    C = vec4(1);

    // Grid
    vec2 grid = abs(ij - round(ij)) / fwidth(ij);
    if ((T * vec3(ij, 1)).z > 0.)
    {
        C = pow(texture(iChannel1, ij, -1.), vec4(2));
        C = blend(C, vec3(0), min(grid.x, grid.y));
    }

    // Dots at grid junctions
    vec3 z = T * vec3(round(ij), 1);
    if (z.z > 0.)
        C = blend(C, vec3(0), distance(uv, P(z)) / f - 1.5);

    // One unit in u/v directions
    C = blend2(C, colors[1], distance(uv, gu) / f - 3.);
    C = blend2(C, colors[2], distance(uv, gv) / f - 3.);

    // Horizon
    float fuv = clamp(dot(uv-u, v-u) / dot(v-u, v-u), 0., 1.);
    C = blend(C, mix(colors[1], colors[2], fuv), sdLine(uv, u, v, -inf, inf) / f - .5);

    // Handles
    for (int i=0 ; i<3 ; ++i)
        C = blend2(C, colors[i], distance(uv, getPoint(i)) / f - 3.);
    C = blend2(C, colors[3], distance(O, getPoint(3)*iResolution.xy) - 3.);

    C = sqrt(C);
}

