void mainImage(out vec4 C, in vec2 O)
{
    ivec2 I = ivec2(O);
    C = texelFetch(iChannel0, I, 0);

    initView(iResolution);
    vec2 click = fromView(abs(iMouse.zw));
    vec2 mouse = fromView(abs(iMouse.xy));
    vec2 absMouse = abs(iMouse.xy);
    bool down = iMouse.z > 0.;
    bool pressed = iMouse.w > 0.;
    bool init = iFrame == 0;
    float demo = float(iMouse == vec4(0));
    int activeIndex = getActiveIndex();

    vec2 o  = getPoint(0);
    vec2 du = getPoint(1) - o;
    vec2 dv = getPoint(2) - o;

    vec2 g  = getPoint(3);
    vec2 gu = o + du * g.x;
    vec2 gv = o + dv * g.y;

    if (I == ivec2(0, 1))
    {
        if (init)
        {
            activeIndex = -1;
        }
        else if (pressed)
        {
            activeIndex = -1;
            float best = 30. * f;
            for (int i=0 ; i<3 ; ++i)
            { float r = distance(mouse, getPoint(i)); if (r < best) activeIndex = i, best = r; }
            { float r = distance(mouse, fromView(g*iResolution.xy)); if (r < best) activeIndex = 3, best = r; }
            { float r = distance(mouse, gu); if (r < best) activeIndex = -2, best = r; }
            { float r = distance(mouse, gv); if (r < best) activeIndex = -3, best = r; }
        }
        else if (!down)
        {
            activeIndex = -1;
        }
        C = vec4(activeIndex);
    }
    else if (I.y == 0 && I.x < npoints)
    {
        if (init || demo!=0.)
        {
            //C = vec4(.5, .5, 0, 0);
            if (I.x == 0) C = vec4(.5 + 0.3 * demo * sin(iTime), .2, 0, 0);
            if (I.x == 1) C = vec4(.8, .6 + 0.1 * demo * cos(iTime), 0, 0);
            if (I.x == 2) C = vec4(.2, .6 - 0.1 * demo * cos(iTime), 0, 0);
            if (I.x == 3) C = vec4(.15, .2, 0, 0) + 0.1 * demo * vec4(cos(iTime/5.), sin(iTime/5.), 0, 0);
            if (I.x != 3)
            {
                C.xy *= iResolution.xy;
                C.xy = fromView(C.xy);
            }
        }
        else if (down)
        {
            if (activeIndex == I.x)
            {
                if (activeIndex == 3)
                    C = vec4(absMouse / iResolution.xy, 0, 0);
                else
                    C = vec4(mouse, 0, 0);
            }
            else if (activeIndex == -2 && I.x == 3)
                C.x = dot(mouse - o, du) / dot(du, du);
            else if (activeIndex == -3 && I.x == 3)
                C.y = dot(mouse - o, dv) / dot(dv, dv);
        }
    }
}

