// a slight remix of Arc from three points (https://www.shadertoy.com/view/NdycRW)
// using IQ's sdCutDisk (https://www.shadertoy.com/view/ftVXRc)


// r=radius, h=height
float sdCutDisk( in vec2 p, in float r, in float h )
{
    float w = sqrt(r*r-h*h); // constant for a given shape

    p.x = abs(p.x);

    // select circle or segment
    float s = max( (h-r)*p.x*p.x+w*w*(h+r-2.0*p.y), h*p.x-w*p.y );

    return (s<0.0) ? length(p)-r :        // circle
           (p.x<w) ? h - p.y     :        // segment line
                     length(p-vec2(w,h)); // segment corner
}

vec2 project(in vec2 a, in vec2 b)
{
    return b * (dot(a, b) / dot(b, b));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    //arc from three points
    vec2 a = vec2(-0.4,0.0);
    vec2 b = vec2(0.0, 0.5);
    if (iMouse.z>0.5)
        b = m;
    vec2 c = (vec2(0.4, 0.2) + vec2(sin(iTime), cos(iTime)) * 0.3);

    //based on https://mathopenref.com/arcradius.html
    vec2 dir = c - a;
    vec2 mid = mix(a, c, 0.5);
    float w = length(dir);
    vec2 normal = normalize(b - (project(b - a, dir) + a));
    float h = length(mid - b);
    float rad = max(w*0.5, h*0.5 + (w*w) / (8.0*h));

    //re-align b to match the arc top
    b = mid + normal * h;

    // show arc distance (using IQs sdCutDisk)
    dir/=w;
    vec2 pp = b - (normal * (rad));
    pp = mat2(dir.x,-dir.y,dir.y,dir.x) * (p-pp);
    pp.y *= sign(normal.y);
    float d = sdCutDisk(pp,rad,rad-h);

    // coloring
    vec3 col = (d>0.0) ? vec3(0.9,0.6,0.3) : vec3(0.5,0.85,1.0);
    col *= 1.0 - exp(-7.0*abs(d));
    col *= 0.8 + 0.2*cos(128.0*abs(d));
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );

    //draw points A,B,C
    float distToPoint = min(min(length(p - a), length(p - b)), length(p - c));
    col = mix(vec3(1.0,0.0,0.0),col,smoothstep(0.0,0.015,distToPoint-0.025));

    fragColor = vec4(col,1.0);
}

