#define PI 3.141592653589793238

mat2 rotMatrix(vec2 p, float r) { vec2 s = vec2(cos(r), sin(r)); return mat2(s.x, -s.y, s.y,  s.x); }
vec3 rot_XY(vec3 p, float r){ vec2 m = p.xy * rotMatrix(p.xy, r); return vec3(m, p.z); }
vec3 rot_XZ(vec3 p, float r){ vec2 m = p.xz * rotMatrix(p.xz, r); return vec3(m.x, p.y, m.y); }
vec3 rot_YZ(vec3 p, float r){ vec2 m = p.yz * rotMatrix(p.yz, r); return vec3(p.x, m); }
vec3 rot_XZ_YZ(vec3 p, float r1, float r2) { return rot_XZ(rot_YZ(p, r1), r2); }

float func(vec3 o) {
    vec3 l = vec3(0.0);
    float i = 0.0;
    float m = 125.0;
    while(i < m) {
        float r = length(l);
        float t = atan(length(l.xy), l.z);
        float p = atan(l.y, l.x);
        float n = 6.0;
        l = pow(r, n) * vec3(
            sin(t*n)*cos(p*n),
            sin(t*n)*sin(p*n),
            cos(t*n)
        ) + o;
        if(length(l) >= 4.0) {
            return i / m;
        }
        i += 1.0;
    }
    return 1.0;
}

vec3 rot(vec3 p, vec3 r) {
    p = rot_XY(p, r.x);
    p = rot_XZ(p, r.y);
    p = rot_YZ(p, r.z);
    return p;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 pos = fragCoord / iResolution.xy;

    float t = 0.1 * iTime;

    vec3 vp_loc = vec3(2.0, 0.0, 0.0);
    vec2 vp_ang = iMouse.xy==vec2(0.0) ? vec2(0.5*PI, PI) : (PI -  2.0 * PI * ((iMouse.xy / iResolution.xy) - 0.5));
    float FOV = 130.0;
    float cam_dist = 140.0;

    float hFOV = FOV * tan(PI * FOV / 360.0);
    vec3 p_e = vec3(
        hFOV * (pos - 0.5) * (iResolution.xy / iResolution.x),
        -cam_dist);

    vec3 p_cast_s = vp_loc;
    vec3 p_cast_e = vp_loc + rot_XZ_YZ(p_e, -vp_ang.y, vp_ang.x);

    vec3 rots = vec3(t, 0.75 * t, -1.25 * t);
    p_cast_s = rot(p_cast_s, rots);
    p_cast_e = rot(p_cast_e, rots);

    vec3 loc = p_cast_s;
    vec3 delt = 0.001 * normalize(p_cast_e - p_cast_s);
    vec3 col = vec3(0.0);
    float i = -1.0;
    while(i <= 300.0) {
        i += 1.0;
        float q = func(loc);
        loc += max(1.0, 10.0 * (1.0 - q)) * delt;
        if(q < 0.1) {
            continue;
        }else{
            col = clamp(
                2.0 * (0.1 * q + 1.0 - 0.1 * length(p_cast_s - loc)) * vec3(
                mod(0.5 * abs(sin(loc.x - loc.y)), 1.0),
                mod(0.5 * abs(sin(loc.y - loc.z)), 1.0),
                mod(0.5 * abs(sin(loc.z - loc.x)), 1.0)
            ), 0.0, 1.0);
            break;
        }
    }

    fragColor = vec4(col, 1.0);
}

