vec2 GetDist1(vec3 p  )
{	vec2 res= vec2(9999.0, -1.0); 
    float t=iTime;
    float pzm1=2.0*sin(t);
    float pzm2=2.0*cos(t);

    float d1a= yin1(p.xy)-0.05;
    float d1b= sdBox( p, vec3(10.0,6.0,3.0+pzm1) );

    float d2a= yang1(p.xy)-0.05;
    float d2b= sdBox( p, vec3(10.0,6.0,3.0)+pzm2 );

    float d3a= yin2(p.xy);
    float d3b= sdBox( p, vec3(5.0,5.0,4.0)+pzm1 );

    float d4a= yang2(p.xy);
    float d4b= sdBox( p, vec3(5.0,5.0,4.0)+pzm2 );

    float dunia= Intersect(d1a, d1b);
    float dunib= Intersect(d2a, d2b);
    float dunic= Intersect(d3a, d3b);
    float dunid= Intersect(d4a, d4b);

    res =opU2(res, vec2(dunia,32.0));
    res =opU2(res, vec2(dunib,33.0));
    res =opU2(res, vec2(dunic,33.0));
    res =opU2(res, vec2(dunid,32.0));
    return res;
}

vec2  opScaleGetDis( vec3 p,float s)
{   vec2 res =GetDist1(p/s);
    res.x =res.x*s;
    return res;
}


vec2 GetDist(vec3 p  ) 
{	vec2 res= vec2(9999.0, -1.0); 
    vec3 p0=p;
    float escala1=5.0*sin(iTime*0.25);
    float escala2=-5.0*sin(iTime*0.25);

    vec2 gt1;
    if (escala1>0.0)
        gt1= opScaleGetDis(p, escala1);
    else
        gt1= opScaleGetDis(p, escala2);
    res =opU2(res, gt1);
