vec3 pal(float t) {
    vec3 d = 0.5 * vec3(0,1,2)/3.;
    return 0.5 + 0.5 * cos(6.28319 * (0.5 * t + d));
}

#define thc(a,b) tanh(a*cos(b))/tanh(a)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float zm = 1.;
    vec2 res = iResolution.xy;    
    ivec2 px = ivec2(mod(zm * fragCoord + (1.-zm) * res, res));

    vec3 c = cell(px).rgb;
    vec3 col = c;

    vec2 uv2 = col.rg;
    col.rgb += 0.5;
    col *= 0.5 + 0.5 * thc(4., 40. * uv2.x + 2. * cos(10. * (uv2.x + uv2.y - iTime)));
    col *= step(0.001, c.b);
    float d = length(uv2);
    float s = step(d, 0.2);

    col = c.b * c.b * mix(col, pal(iTime * c.b * 2.+ uv2.x * 10. + c.b * 4.), 0.5);
    col += vec3(0.1,0.12,0.2);

    fragColor.rgb = col.rgb;
}

