#define pi 3.14159

#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define mlength(p) max(abs(p.x), abs(p.y))

vec4 cell(in ivec2 p) {
    ivec2 r = ivec2(textureSize(iChannel0, 0));
    p = (p+r) % r;
    return texelFetch(iChannel0, p, 0 );
} 

void mainImage( out vec4 col, in vec2 f )
{
    vec2 res = floor(0.5 * iResolution.xy);

    ivec2 px = ivec2(f);
    f -= res;

    col = vec4(0);

    float t = 2. * iTime; //floor(8. * iTime)/4.;
    vec2 p = 120. * vec2(cos(t), sin(0.825 * t));
    t += 0.01;
    vec2 q = 120. * vec2(cos(t), sin(0.825 * t));

    vec2 dir = p - q;
    float a = atan(dir.x, dir.y);

    f -= p;
    f *= rot(a);
    float sc = 50.;
    col.rgb = vec3(f * 0.5 / sc, 1.);   
    col.rgb *= step(mlength(col.rg), 0.5);

    vec4 c = cell(px);
    if (col.b < c.b) {
        col.rgb += c.rgb;
        col.b -= 0.012;
    }

    col.rg = clamp(col.rg, -0.5, 0.5);
}

