void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    float fontHeight = 30.0;
    float fontWidth = 15.0;
    float fontRatio = fontWidth / fontHeight;
    vec2 uv = (fragCoord - iResolution.xy + vec2(fontWidth, fontHeight)) / fontHeight;

    vec2 offset = vec2(1.0 / 16.0, 1.0 / 16.0);
    vec2 digit = vec2(0.0, -4.0);
    vec2 decimalPoint = vec2(-2.0, -3.0);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec4 font = vec4(0.0);

    // float number = 1234.09999;
    float number = 1.0 / iTimeDelta;
    float decimalPlaces = 2.0;
    number *= pow(10.0, decimalPlaces);
    float i = 0.0;
    while (number > 0.999) {
        vec2 coords = uv + vec2(i * fontRatio + (1.0 - fontRatio), 0.0);
        if (abs(i - decimalPlaces) <= 0.001) {
            if (coords.x >= (1.0 - fontRatio) / 2.0 && coords.x < 1.0 - (1.0 - fontRatio) / 2.0 && coords.y >= 0.0 && coords.y < 1.0) {
                font = texture(iChannel0, (coords + decimalPoint) * offset);
            }
            i += 1.0;
            continue;
        } else {
            float current = mod(floor(number), 10.0);
            if (coords.x >= (1.0 - fontRatio) / 2.0 && coords.x < (1.0 + fontRatio) / 2.0 && coords.y >= 0.0 && coords.y < 1.0) {
            //if (true) {
                font = texture(iChannel0, (coords + digit + current * vec2(1.0, 0.0)) * offset);
            }
            number /= 10.0;
        }
        i += 1.0;
    }
    // Output to screen
    fragColor = vec4(font.x, 0.0, 0.0, 1.0);
}

// this is how much each cone biases the other

#define RR 1.0
#define RG 0.0
#define RB 0.0
#define GR 0.0
#define GG 1.0
#define GB 0.0
#define BR 0.0
#define BG 1.0
#define BB 0.0

// for example, RR is the weight for how much red 
// biases the red chanel

// changing these values results in different 
// color blindess type

// for example, all ones lead to complete color blindness 
// and setting GR to 1.0 leads to a type of red green
// color blindness

// tecnically, there is no actual color blindness that 
// would be created from the BR or RB biases since 
// there is no colorblindness that involves both 
// the red and blue cones 


/*** CODE, DO NOT TOUCH WHILE PRESENTING ***/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // reading image and storing normal color values
    vec4 col = texture(iChannel0,uv); 
    float r = col.x;
    float g = col.y;
    float b = col.z;


    // taking weighted average of the colors
    if(uv.y>0.5) {
        col.x=(r*RR+g*RG+b*RB)/(RR+RG+RB);
        col.y=(r*GR+g*GG+b*GB)/(GR+GG+GB);
        col.z=(r*BR+g*BG+b*BB)/(BR+BG+BB);
    }

    col.x=pow(col.x,2.2);
    col.y=pow(col.y,2.2);
    col.z=pow(col.z,2.2);



    // Output to screen
    fragColor = col;
}

