// -------------------------------------
// Defines
#define EPS 1e-6
#define PI 3.14159265359
#define INFINITY 1000000.0 // Had to change this otherwise I got a division by zero 1.0 / 0.0
#define PLANET_RADIUS 6371000.0
#define PLANET_CENTER vec3(0, -PLANET_RADIUS, 0)
#define ATMOSPHERE_HEIGHT 100000.0
#define RAYLEIGH_HEIGHT (ATMOSPHERE_HEIGHT * 0.08)
#define MIE_HEIGHT (ATMOSPHERE_HEIGHT * 0.012)

// -------------------------------------
// Coefficients
#define C_RAYLEIGH (vec3(5.802, 13.558, 33.100) * 1e-6)
#define C_MIE (vec3(3.996, 3.996, 3.996) * 1e-6)
#define C_OZONE (vec3(0.650, 1.881, 0.085) * 1e-6)

#define ATMOSPHERE_DENSITY 1.0
#define EXPOSURE 20.0

// -------------------------------------
// Math
vec2 SphereIntersection(vec3 rayStart, vec3 rayDir, vec3 sphereCenter, float sphereRadius)
{
    rayStart -= sphereCenter;
    float a = dot(rayDir, rayDir);
    float b = 2.0 * dot(rayStart, rayDir);
    float c = dot(rayStart, rayStart) - (sphereRadius * sphereRadius);
    float d = b * b - 4.0 * a * c;
    if (d < 0.0)
    {
        return vec2(-1, -1);
    }
    else
    {
        d = sqrt(d);
        return vec2(-b - d, -b + d) / (2.0 * a);
    }
}

vec2 PlanetIntersection(vec3 rayStart, vec3 rayDir)
{
    return SphereIntersection(rayStart, rayDir, PLANET_CENTER, PLANET_RADIUS);
}

vec2 AtmosphereIntersection(vec3 rayStart, vec3 rayDir)
{
    return SphereIntersection(rayStart, rayDir, PLANET_CENTER, PLANET_RADIUS + ATMOSPHERE_HEIGHT);
}

// -------------------------------------
// Phase functions
float PhaseRayleigh(float costh)
{
    return 3.0 * (1.0 + costh * costh) / (16.0 * PI);
}
// g = 0.85
float PhaseMie(float costh)
{
    float g = 0.85;
    g = min(g, 0.9381);
    float k = 1.55 * g - 0.55 * g * g * g;
    float kcosth = k * costh;
    return (1.0 - k * k) / ((4.0 * PI) * (1.0 - kcosth) * (1.0 - kcosth));
}

// -------------------------------------
// Atmosphere
float AtmosphereHeight(vec3 positionWS)
{
    return distance(positionWS, PLANET_CENTER) - PLANET_RADIUS;
}
float DensityRayleigh(float h)
{
    return exp(-max(0.0, h / RAYLEIGH_HEIGHT));
}
float DensityMie(float h)
{
    return exp(-max(0.0, h / MIE_HEIGHT));
}
float DensityOzone(float h)
{
    // The ozone layer is represented as a tent function with a width of 30km, centered around an altitude of 25km.
    return max(0.0, 1.0 - abs(h - 25000.0) / 15000.0);
}
vec3 AtmosphereDensity(float h)
{
    return vec3(DensityRayleigh(h), DensityMie(h), DensityOzone(h));
}

// Optical depth is a unitless measurement of the amount of absorption of a participating medium (such as the atmosphere).
// This function calculates just that for our three atmospheric elements:
// R: Rayleigh
// G: Mie
// B: Ozone
// If you find the term "optical depth" confusing, you can think of it as "how much density was found along the ray in total".
vec3 IntegrateOpticalDepth(vec3 rayStart, vec3 rayDir)
{
    vec2 intersection = AtmosphereIntersection(rayStart, rayDir);
    float rayLength = intersection.y;

    int sampleCount = 8;
    float stepSize = rayLength / float(sampleCount);

    vec3 opticalDepth = vec3(0.0, 0.0, 0.0);

    for (int i = 0; i < sampleCount; i++)
    {
        vec3 localPosition = rayStart + rayDir * (float(i) + 0.5) * stepSize;
        float localHeight = AtmosphereHeight(localPosition);
        vec3 localDensity = AtmosphereDensity(localHeight);

        opticalDepth += localDensity * stepSize;
    }

    return opticalDepth;
}

// Calculate a luminance transmittance value from optical depth.
vec3 Absorb(vec3 opticalDepth)
{
    // Note that Mie results in slightly more light absorption than scattering, about 10%
    return exp(-(opticalDepth.x * C_RAYLEIGH + opticalDepth.y * C_MIE * 1.1 + opticalDepth.z * C_OZONE) * ATMOSPHERE_DENSITY);
}

// Integrate scattering over a ray for a single directional light source.
// Also return the transmittance for the same ray as we are already calculating the optical depth anyway.
vec3 IntegrateScattering(vec3 rayStart, vec3 rayDir, float rayLength, vec3 lightDir, vec3 lightColor, out vec3 transmittance)
{
    // We can reduce the number of atmospheric samples required to converge by spacing them exponentially closer to the camera.
    // This breaks space view however, so let's compensate for that with an exponent that "fades" to 1 as we leave the atmosphere.
    float rayHeight = AtmosphereHeight(rayStart);
    float sampleDistributionExponent = 1.0 + clamp(1.0 - rayHeight / ATMOSPHERE_HEIGHT, 0.0, 1.0) * 8.0; // Slightly arbitrary max exponent of 9

    vec2 intersection = AtmosphereIntersection(rayStart, rayDir);
    rayLength = min(rayLength, intersection.y);
    if (intersection.x > 0.0)
    {
        // Advance ray to the atmosphere entry point
        rayStart += rayDir * intersection.x;
        rayLength -= intersection.x;
    }

    float costh = dot(rayDir, lightDir);
    float phaseR = PhaseRayleigh(costh);
    float phaseM = PhaseMie(costh);

    int sampleCount = 64;

    vec3 opticalDepth = vec3(0.0, 0.0, 0.0);
    vec3 rayleigh = vec3(0.0, 0.0, 0.0);
    vec3 mie = vec3(0.0, 0.0, 0.0);

    float prevRayTime = 0.0;

    for (int i = 0; i < sampleCount; i++)
    {
        float rayTime = pow(float(i) / float(sampleCount), sampleDistributionExponent) * rayLength;
        // Because we are distributing the samples exponentially, we have to calculate the step size per sample.
        float stepSize = (rayTime - prevRayTime);

        vec3 localPosition = rayStart + rayDir * rayTime;
        float localHeight = AtmosphereHeight(localPosition);
        vec3 localDensity = AtmosphereDensity(localHeight);

        opticalDepth += localDensity * stepSize;

        // The atmospheric transmittance from rayStart to localPosition
        vec3 viewTransmittance = Absorb(opticalDepth);

        vec3 opticalDepthlight = IntegrateOpticalDepth(localPosition, lightDir);
        // The atmospheric transmittance of light reaching localPosition
        vec3 lightTransmittance = Absorb(opticalDepthlight);

        rayleigh += viewTransmittance * lightTransmittance * phaseR * localDensity.x * stepSize;
        mie += viewTransmittance * lightTransmittance * phaseM * localDensity.y * stepSize;

        prevRayTime = rayTime;
    }

    transmittance = Absorb(opticalDepth);

    return (rayleigh * C_RAYLEIGH + mie * C_MIE) * lightColor * EXPOSURE;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{   
    // Hack this for now because I don't know how to get world position in shaderToy...
    vec3 instanceWorldPos = vec3(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y  * 5.0, 0.0);

    vec3 _WorldSpaceCameraPos = vec3(0.0, 0.0, -10.0);
    vec3 _WorldSpaceLightPos0 = vec3(0.0, 10.0, 0.0);
    vec3 _LightColor0 = vec3(0.5, 0.5, 0.5);

    vec3 rayStart = _WorldSpaceCameraPos;
    vec3 rayDir = normalize(instanceWorldPos - _WorldSpaceCameraPos);
    float rayLength = INFINITY;

    float _DrawPlanet = 0.0;

    if (_DrawPlanet == 1.0)
    {
        vec2 planetIntersection = PlanetIntersection(rayStart, rayDir);
        if (planetIntersection.x > 0.0)
            rayLength = min(rayLength, planetIntersection.x);
    }

    vec3 lightDir = _WorldSpaceLightPos0.xyz;
    vec3 lightColor = _LightColor0.xyz;

    vec3 transmittance;
    vec3 color = IntegrateScattering(rayStart, rayDir, rayLength, lightDir, lightColor, transmittance);

    fragColor = vec4(color, 1.0);
}

vec3 erot(vec3 p,vec3 ax,float t){
    return mix(dot(ax,p)*ax,p,cos(t))+cross(ax,p)*sin(t);
}

float box2(vec2 p, vec2 b){
    vec2 q = abs(p)-b;
    return length(max(vec2(0.),q))+min(0.,max(q.x,q.y));  
}

float box3(vec3 p,vec3 b){
    vec3 q = abs(p)-b;
    return length(max(vec3(0.),q))+min(0.,max(q.x,max(q.y,q.z)));
}
float sdOrientedBox( in vec2 p, in vec2 a, in vec2 b, float th )
{
    float l = length(b-a);
    vec2  d = (b-a)/l;
    vec2  q = (p-(a+b)*0.5);
          q = mat2(d.x,-d.y,d.y,d.x)*q;
          q = abs(q)-vec2(l,th)*0.5;
    return length(max(q,0.0)) + min(max(q.x,q.y),0.0);    
}
float diam(vec3 p,float s){
    p = abs(p);
    return (p.x+p.y+p.z-s)*inversesqrt(3.);
}

vec2 sdf(vec3 p){  

    vec3 tp=p,hp=p;
    hp.y -=abs(asin(sin(iTime)));  
  float gy = dot(sin(p),cos(p.zxy*7.));  

hp = erot(hp,normalize(vec3(1.,2.,3)),iTime);
    vec2 h;
    h.x = length(hp)-1.;
    h.x = .7*mix(diam(hp,1.),box3(hp,vec3(1.)),sin(iTime+gy*.2)*.5-0.2)*.9;




    h.y = 1.;

    vec2 t;
     tp.y +=1.;
      tp.x +=iTime;
     tp.xz = asin(sin(tp.xz));

   float sc=1.;
    for(float i=0.;i<4.;i++){
          tp.x = .5-abs(tp.x);
         tp*=2.5;
         sc*=2.5;

      tp = erot(tp,vec3(0,1,0),.785);
      }
     float dd,c=10./3.141592;

     tp.xz = vec2(log(dd=length(tp.xz)),atan(tp.x,tp.z))*c;
     tp.y /=dd/=c;

    tp.x +=iTime;
    tp.xz = asin(sin(tp.xz));

    tp.y = abs(tp.y)-0.5-abs(gy)*.1;

    gy = dot(sin(tp),cos(tp.zxy*7.)); 
    t.x=  dd*.8*min(box2(tp.yz,vec2(.01)), box2(tp.xy,vec2(.01)))/sc;
    t.y = 2.;
    h = t.x < h.x ? t:h;



    return h;
}
vec3 pal(float t){return .5+.5*cos(6.28*(1.*t+vec3(.0,.3,.7)));}
#define q(s) s*sdf(p+s).x
vec3 norm(vec3 p,float ee){vec2 e=vec2(-ee,ee);return normalize(q(e.xyy)+q(e.yxy)+q(e.yyx)+q(e.xxx));}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;


  vec3 col = vec3(.1,.2,.7)*max(0.,1.+uv.y*7.);
	vec3 ro = vec3(0.,1.+sin(iTime),-5.);

  if(mod(iTime,20.) > 10. ) ro = erot(ro,normalize(vec3(0.,1.,0.)),iTime*.5);

  vec3 rt = vec3(0.);
  vec3 z = normalize(rt-ro);
  vec3 x = normalize(cross(z,vec3(0.,-1.,0.)));
  vec3 y = normalize(cross(z,x));
  vec3 rd = mat3(x,y,z)*normalize(vec3(uv,1.-sin(floor(iTime*.5)*55.)*.8*sqrt(length(uv))));
  vec3 rp = ro;
  vec3 light = vec3(1.,2.,-3);
  float dd = 0.;
  vec3 acc = vec3(0.);


  for(float i=0.;i<128.;i++){
      vec2 d = sdf(rp);
      if(d.y == 2.){
        acc += vec3(1.,1.,0.)*.25*exp(i*-abs(d.x))/(60.+sin(iTime*10.+rp.y*10.)*50.);
        d.x = max(.001,abs(d.x));
      }
      rp+=rd*d.x;

      dd+=d.x;
      if(dd>50.) break;
      if(d.x < .0001){
          vec3 n = norm(rp,.001);
         vec3 n2 = norm(rp,.02+cos(rp.z*10.)*.01);
          float dif = max(0.5,dot(normalize(light-rp),n));
          float spc = pow(max(0.,dot(rd,reflect(normalize(light),n))),32.);
          float fres = pow(1. - dot(-rd, n), 5.);
         float ol = step(.1,.3*length(n-n2));
         if(ol ==0.){
             rd = reflect(rd,n);
             rp+=rd*.1;
           continue;
           }
          col = pal(fres*2.-iTime*.5+dif)*ol+spc+fres;
          break;
      }

  }  


    // Output to screen
    fragColor = vec4(col+acc,1.0);
}

// I first heard of this fractal, which is homeomorphic to the Mandelbrot set, in this lecture by Luna Lomonaco https://www.youtube.com/watch?v=QXQkRjeFCcM&t=3034s
// I wasn't able to find any code for rendering it, despite its relative simplicity, so I wanted to share this first pass. Feel free to reuse it.
// It's not at all optimized for the sake of readability. In fact, it's quite slow.

const bool julia = false;
const float iterations = 100.0;
const float escapeDistance = 60.0;
const float pi = 3.1415926;

vec3 hue(float x)
{
    return vec3(0.6 + 0.6*cos(2.0*pi*(x) + vec4(0,-2.0*pi/3.,2.0*pi/3.0,0)));
}

mat2 rot(float x)
{
    return mat2(cos(x), -sin(x), sin(x), cos(x));
}

vec2 cmul(vec2 za,vec2 zb)
{
    return za*mat2(zb.x, -zb.y, zb.yx);
}

vec2 cinv(vec2 z) 
{
    return z*vec2(1, -1)/dot(z, z);
}

vec2 cexp(vec2 z) 
{
    return exp(z.x)*vec2(cos(z.y), sin(z.y));
}

vec2 clog(vec2 z) 
{
    return vec2(log(length(z)), atan(z.y,z.x));
}

vec2 cpow(vec2 z, float n)
{
    return cexp(n*clog(z));
}

//https://www.shadertoy.com/view/WlfXRN
vec3 magma(float t) 
{
    const vec3 c0 = vec3(-0.002136485053939582, -0.000749655052795221, -0.005386127855323933);
    const vec3 c1 = vec3(0.2516605407371642, 0.6775232436837668, 2.494026599312351);
    const vec3 c2 = vec3(8.353717279216625, -3.577719514958484, 0.3144679030132573);
    const vec3 c3 = vec3(-27.66873308576866, 14.26473078096533, -13.64921318813922);
    const vec3 c4 = vec3(52.17613981234068, -27.94360607168351, 12.94416944238394);
    const vec3 c5 = vec3(-50.76852536473588, 29.04658282127291, 4.23415299384598);
    const vec3 c6 = vec3(18.65570506591883, -11.48977351997711, -5.601961508734096);

    return c0+t*(c1+t*(c2+t*(c3+t*(c4+t*(c5+t*c6)))));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float escaped = iterations;

	vec2 coord = vec2(-2.0,0) + 3.0*(2.0*fragCoord.xy-iResolution.xy)/(iResolution.x);
    vec2 mouse = vec2(-2.0,0) + 3.0*(2.0*iMouse.xy-iResolution.xy)/(iResolution.x);

    vec2 B = julia ? mouse : coord;
    vec2 A = cpow(-B, 0.5);
    // As far as I know, for the general class of functions conformally conjugate to g_A(z) = z +1/z + A, we'd need to check the orbit of both -1 and 1.
    // Just -1 seems to be sufficient for this particular construction.
    vec2 z = julia ? coord : vec2(-1,0);

    vec2 zStop = vec2(0);
    float i = 0.0;
    for (; i < iterations; i++)
    {
        // Note that "escapeDistance" is just a useful fiction for coloring the exterior. It doesn't guarantee divergence.
        if(length(z) > escapeDistance && escaped >= iterations) 
        {
            escaped = i;
            zStop = z;
        }
        z = z + cinv(z) + A;
    }

    // "Smooth iteration count" naively adapted from the standard Mandelbrot. 
    // Looks OK but isn't really correct.
    if(zStop != vec2(0))
    {
        escaped += 1.0-2.0*escapeDistance*log(log(length(zStop))/log(escapeDistance));
        escaped = max(escaped, 0.0);
    }

    float escapeTime = escaped/iterations;
    vec2 gridOrigin = z + 50.0*mouse + vec2(0,15)*rot(iTime/4.0);
    float grid = 0.5*(0.5*sin(0.4*gridOrigin.x)+0.5) + 0.5*(0.5*sin(0.4*gridOrigin.y)+0.5);
    grid = pow(grid+0.4975, 200.0);
    grid = clamp(grid, 0.0, 1.0);
    vec3 col = magma(mix(escapeTime, grid*escapeTime, 0.05*smoothstep(0.0, 10.0, length(z)-5.0)));
    col = mix(col, hue(0.2*iTime+-0.5*mouse.y+4.0/pi*atan(z.y,z.x)).rgb, 0.1);
	fragColor = vec4(col,1.0);
}

////////////////////////////////////////////////////////////////////////////////

// Modular Forms, Matthew Arcus, mla, 2022
// Based entirely on original work by David Lowry-Duda:
// https://davidlowryduda.com/quanta-langlands-viz/

// A Modular Form is a complex valued function, defined on
// the upper half plane, that is invariant (up to a certain
// scaling factor, determined by the "weight" of the form) 
// under the action of the modular group (integer valued
// Mobius transformations with unit determinant).

// Each form can be evaluated by a Fourier series, but this
// can result in large numeric errors. A better way (as
// described by Lowry-Duda) is to pullback or fold the
// half plane into the fundamental region for the
// group (the region with |Re(z)| < 0.5 and |z| > 1) and
// only evaluate the series there, where it can be done
// with less loss of precision.

// The value of the form is shown with color indicating phase
// and shading showing magnitude & the boundary of the
// fundamental region is also shown ('p','m' and 'r' control
// these).

// By default the upper halfplane is mapped into the unit disk
// (press 'h' to see the half plane). Use the mouse to apply
// a Mobius transformation to the disk or plane.

// Controls:
// <mouse>: apply transform to disk or plane
// 'h': Half plane
// 'p': Show phase as color
// 'm': Show (log) magnitude as shading
// 'r': Show fundamental region
// 'f': Use pullback/fold
// 'c': Continuous coloring
// 't': color change over time
// 'l': log contour lines
// 'x': Progressive Fourier expansion

////////////////////////////////////////////////////////////////////////////////

// Select form to display here
//#define FORM JINVARIANT
#define FORM FORM12
//#define FORM E10

#define FORM4 0
#define FORM12 1
#define FORM14 2
#define FORM20 3
#define FORM24 4
#define JINVARIANT 5
#define E2 6
#define E10 7

#if FORM == E10
// E10
float coeffs[] = float[](
  1.0, -264.0, -135432.0, -5196576.0, -69341448.0, -515625264.0, -2665843488.0, -10653352512.0, -35502821640.0
);
int weight = 10;
#elif FORM == FORM20
float coeffs[] = float[](
   0.0,1.0,456.0,50652.0,-316352.0,-2377410.0,23097312.0,-16917544.0,
   -383331840.0,1403363637.0
);
int weight = 20;
#elif FORM == FORM14
float coeffs[] = float[](
  1.0,-24.0,-196632.0,-38263776.0,-1610809368.0,-29296875024.0, 
  -313495116768.0, -2325336249792.0
);
int weight = 14;
#elif FORM == FORM12
// Ramanujan's tau function, giving the modular discriminant
float coeffs[] = float[](
  0.0, 1.0, -24.0, 252.0, -1472.0, 4830.0, -6048.0, -16744.0, 84480.0, -113643.0, -115920.0,
  534612.0, -370944.0, -577738.0, 401856.0, 1217160.0, 987136.0, -6905934.0, 2727432.0,
  10661420.0, -7109760.0, -4219488.0, -12830688.0, 18643272.0, 21288960.0, -25499225.0,
  13865712.0, -73279080.0, 24647168.0, 128406630.0, -29211840.0, -52843168.0, -196706304.0
);
int weight = 12;
#elif FORM == FORM4
float coeffs[] = float[](
  1.0,240.0,2160.0,6720.0,17520.0,30240.0,60480.0,82560.0,140400.0
);
int weight = 4;
#elif FORM == FORM24
float coeffs[] = float[](
  0.0,1.0,0.0,195660.0,12080128.0,44656110.0,-982499328.0, -147247240.0,
  22106234880.0,-8700375483.0,-307849789440.0,407518320132.0,
  1302498570240.0,1503145514150.0,-20178571198464.0,23514204375720.0,
  -1899976392704.0,139716078767730.0,-333538871869440.0,-71967740442820.0
);
int weight = 24;
#elif FORM == JINVARIANT
// Klein's J-Invariant
float coeffs[] = float[](
  744.0,196884.0,21493760.0,864299970.0,20245856256.0,
  333202640600.0, 4252023300096.0, 44656994071935.0, 401490886656000.0,
  3176440229784420.0, 22567393309593600.0, 146211911499519294.0, 
  874313719685775360.0, 4872010111798142520.0, 25497827389410525184.0,
  126142916465781843075.0);
int weight = 0;
#define NEEDINVERSE
#else
#error No FORM defined
#endif

vec2 deltafn(vec2 z) {
  vec2 w = complex(0);
  vec2 q = cexp(TWOPI*cmul(vec2(0,1),z)); // The nome squared
  int N = coeffs.length();
  if (key(CHAR_X)) N = int(iTime)%(N+1); // Progressive
  for (int i = N; i > 0; i--) {
    w = cmul(w,q);
    w += complex(coeffs[i-1]);
  }
#if defined NEEDINVERSE
  w += cinv(q);
#endif
  return w;
}

vec2 act(mat2 gamma, vec2 z) {
  // Action of Mobius transformation matrix on z.
  float a = gamma[0][0], b = gamma[0][1];
  float c = gamma[1][0], d = gamma[1][1];
  return cdiv(a*z+complex(b),c*z+complex(d));
}

vec2 pullback(vec2 z, out mat2 gamma) {
    //Returns gamma, w such that gamma(z) = w and w is
    //(essentially) in the fundamental domain.
    gamma = mat2(1);
    for (int i = 0; i < 40; i++) {
      if (abs(z.x) < 0.5 && dot(z,z) > 1.0) break;
      float xshift = -floor(z.x + 0.5);
      mat2 shiftmatrix = mat2(1, xshift, 0, 1);
      gamma *= shiftmatrix;
      z = act(shiftmatrix, z);
      if (dot(z,z) < 1.0) {
         z = -cinv(z);
         gamma *= mat2(0, -1, 1, 0);
      }
    }
    return z;
}

float clength(vec2 z) {
  // "Careful" length - avoid overflow for very large values
  if (z == vec2(0)) return 0.0;
  float t = max(abs(z.x),abs(z.y));
  z /= t;
  return t*length(z);
}

vec3 getcolor(vec2 z) {
  if (key(CHAR_H)) {
    z.y += 1.0;
    if (iMouse.x > 0.0) {
      vec2 c = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
      z.x -= c.x;
      z *= (c.y+1.0)/dot(z,z);
      z.x += c.x;
      z /= dot(z,z);
    }
  } else {
    if (dot(z,z) > 1.0) return vec3(0);
    if (iMouse.x > 0.0) {
      vec2 c = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
      if (dot(c,c) > 1.0) c /= dot(c,c);
      z -= c;
      z *= (dot(c,c)-1.0)/dot(z,z);
      z += c;
      z /= -dot(z,z);
    }
    z.y += 1.0;
    z *= 2.0/dot(z,z);
    z.y -= 1.0;
    z /= dot(z,z);
  }
  mat2 gamma = mat2(1);
  if (!key(CHAR_F)) z = pullback(z,gamma);
  float a = gamma[0][0], b = gamma[0][1];
  float c = gamma[1][0], d = gamma[1][1];
  vec2 w = deltafn(z);
  if (w == vec2(0)) return vec3(0);
  // Apply modularity - note that gamma is inverse of mapping
  // to fundamental region & and has unit determinant.
  w = cmul(w,cpow(-c*z + complex(a),weight));
  float h = 0.5+atan(w.y,w.x)/TWOPI;
  if (!key(CHAR_C)) h *= 0.5; // Use limited color range
  vec3 col = vec3(1,1,0.5);
  float t = 0.0;
  if (!key(CHAR_T)) t = iTime;
  if (!key(CHAR_P)) col = 0.2+0.8*h2rgb(h+0.0*t);
  if (!key(CHAR_L)) {
    vec2 uv = (vec2(t,0)+4.0*clog(w))/PI;
    float px = fwidth(uv.x);
    uv = fract(uv);
    uv = min(uv,1.0-uv);
    col *= smoothstep(0.0,min(px,0.1),min(uv.x,uv.y));
  }
  if (!key(CHAR_M)) col *= 0.65+0.35*cos(t+4.0*log(clength(w)));
  if (key(CHAR_R)) {
    //  Fundamental region borders
    float d = abs(0.5-abs(z.x));
    d = min(d,abs(length(z)-1.0));
    d = min(d,abs(z.x));
    float px = fwidth(d);
    col *= 0.5+0.5*smoothstep(-px,px,d-0.02);
  }
  return col;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  int AA = 2;
  vec3 col = vec3(0);
  for (int i = 0; i < AA; i++){
      for (int j = 0; j < AA; j++) {
        vec2 z = (2.0*(fragCoord+vec2(i,j)/float(AA))-iResolution.xy)/iResolution.y;
        col += getcolor(z);
      }
  }
  col /= float(AA*AA);
  col = pow(col,vec3(0.4545));
  fragColor = vec4(col,1.0);
}

