bool alert = false;
void assert(bool b) { if (!b) alert = true; }

const float PI = 3.14159265359;
const float TWOPI = 2.0*3.14159265359;

#define complex(x) (vec2((x),0))

vec2 cmul(vec2 z, vec2 w) {
  return mat2(z.x,z.y,-z.y,z.x)*w;
}

vec2 cinv(vec2 z) {
  return vec2(z.x,-z.y)/dot(z,z);
}

vec2 cdiv(vec2 z, vec2 w) {
  return cmul(z,cinv(w));
}

vec2 cexp(vec2 z) {
  // If cos and sin were perfect we wouldn't need normalize
  return exp(z.x)*normalize(vec2(cos(z.y),sin(z.y)));
}

vec2 clog(vec2 z) {
  float r2 = dot(z,z);
  return vec2(0.5*log(r2),atan(z.y,z.x));
}

vec2 cpow(vec2 z, vec2 w) {
  return cexp(cmul(clog(z),w));
}

vec2 cpow(vec2 z, int n) {
  // Integer powers by repeated squaring, more
  // for precision than for performance.
  vec2 res = vec2(1,0);
  vec2 t = z;
  while (true) {
    if ((n&1) == 1) res = cmul(res,t);
    n >>= 1;
    if (n == 0) break;
    t = cmul(t,t);
  }
  return res;
}

vec3 h2rgb(float h) {
  // Function by iq
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  return rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
}

