//Bokeh pass

void mainImage(out vec4 O, vec2 I)
{
    //Resolution, center coordinates and variable for iteration
    vec2 r=iResolution.xy, u=(I+I-r).xx/2e3, i=r/r;
    //Iterate with "i" approximating the sqrt of the number of iterations.
    for(O-=O; i.x<16.; i+=1./i)
        //Rotate sample point with golden angle
        u *= mat2(0,.061,1.413, 0)-.737,
        //Add sample to total (weighted towards brightness)
        O += texture(iChannel0,I/r+u*i/r);

    //Compute average
    O=sqrt(O)*.1;
}

