////////////////////////////////////////////
#define time iTime*1.
#define pi 3.14159265
#define deg pi/180.

mat2 r2d(float a) {
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}

float sdBox(vec3 p, vec3 s) {
    return max(abs(p.z)-s.z,max(abs(p.x)-s.x,abs(p.y)-s.y));
} 

vec3 lpos(float a) {
    vec3 l = vec3(0.,1.,3.);
    l -= 0.3;
    l += a*0.1;
    l.xz *= r2d(time*0.5+a*0.1*time);
    return l;
}

vec4 map(vec3 p) {
    vec3 o = p;
    p.z -= 3.;
    p.yz *= r2d(sin(time/3.)*0.1);
    p.xz *= r2d(time/6.);
    float d = sdBox(p,vec3(0.05));
    float od = d;
    for (int i=0;i<4;i++) {
        p = abs(p)-sin(time/4.+p.y*0.2)*0.1-0.1;
        d = min(d,sdBox(p,vec3(sin(time*0.3+d*0.5+p.y*0.2)*0.5+0.5)*0.1));
    }
    //float d = sdBox(p,vec3(0.05));
    d = min(d,o.y+0.5+0.5-od*0.2);
    //d = max(d,-o.z+0.1);
    return vec4(p,d);
}

vec2 RM(vec3 ro ,vec3 rd, float s) {
    float dO = 0.;
    float ii = 0.;
    for (int i=0;i<99;i++) {
        vec3 p = ro+rd*dO;
        float dS = map(p).w*s;
        dO += dS;
        ii += 0.01;
        if (dO > 100. || dS < 0.001) {break;}
    }
    return vec2(dO,ii);
}

vec3 calcNorm(vec3 p) {
    vec2 h = vec2(0.001,0.);
    return normalize(vec3(
        map(p-h.xyy).w-map(p+h.xyy).w,
        map(p-h.yxy).w-map(p+h.yxy).w,
        map(p-h.yyx).w-map(p+h.yyx).w
    ));
}

vec3 sh(vec3 p, vec3 n, float a) {
    vec3 l = lpos(a);
    vec3 rd = normalize(l-p);
    vec2 d = RM(p-n*0.003,rd,1.);
    float dif = clamp(dot(rd,-n),0.,1.);
    float s = 0.;
    if (length(l-p) > d.x) {
        s = 1.;
    }
    return vec3(s,dif,d.x);
}

float point(vec3 p, vec3 rd, float a) {
    vec3 l = lpos(a);
    float l1 = clamp(length(l-p)*0.5,0.,1.);
    float l2 = pow(1.-length(l.xy-rd.xy*l.z),3.);
    return clamp(pow(l2+0.1,5.),0.,1.);
}

vec3 colo(vec3 p, vec3 n, vec2 d, vec3 rd) {
    vec3 col = 1.-d.xxx/7.;
    vec3 mp = map(p).xyz;
    col += 1.-n;
    col *= 0.05;

    col *= 0.;
    col.rg += sin(mp.z*10.)*0.1;
    vec3 rs = sh(p,n,0.);
    float p1 = point(p,rd,0.);
    col.r += p1;
    col.r -= rs.x*0.5;
    col.r += rs.y*0.4;
    vec3 bs = sh(p,n,1.);
    p1 = point(p,rd,1.);
    col.b += p1;
    col.b -= bs.x*0.5;
    col.b += bs.y*0.4;
    vec3 gs = sh(p,n,2.);
    p1 = point(p,rd,2.);
    col.g += p1;
    col.g -= gs.x*0.5;
    col.g += gs.y*0.4;
    //col += clamp(pow(p1+0.1,5.),0.,1.);
    //col += clamp(s.z*0.01,0.,1.)*0.1;
    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 R = iResolution.xy;
    float ar = R.x/R.y;
    uv -= 0.5;
    uv.x *= ar;
    vec3 col = vec3(0.);
    vec3 ro = vec3(0.);
    vec3 rd = normalize(vec3(uv,1.));
    vec2 d = RM(ro,rd,1.);
    vec3 p = ro+rd*d.x;
    vec3 n = calcNorm(p);
    col = colo(p,n,d,rd);
    vec3 ocol = col;
    vec3 oro = ro;
    vec3 ord = rd;
    vec2 od = d;
    vec3 op = p;
    vec3 on = n;
    float rl = 1.5;
    for (int i=0;i<3;i++) {
        ro = p-n*0.003;
        rd = reflect(rd,n);
        d = RM(ro,rd,1.);
        p = ro+rd*d.x;
        n = calcNorm(p);
        col += colo(p,n,d,rd)*rl;
        rl *= 0.9;
    }
    col = clamp(col,0.,1.);
    col += ocol;

col *= 1.;
    fragColor = vec4(col,1.0);
}

