    btn=min(btn,rbox(dv+vec2(.185,.25),vec2(.065,.030),vec4(.030)) );
    btn=min(btn,rbox(dv+vec2(.46,.25),vec2(.065,.030),vec4(.030)) );
    btn=smoothstep(px,-px,btn);

