const float tx = .5;
const vec2[4] myhood = vec2[4](vec2(1,0),vec2(0,-1),vec2(-1,0),vec2(0,1));
const vec2[4] mypos  = vec2[4](vec2(tx,0),vec2(0,-tx),vec2(-tx,0),vec2(0,tx));

float side(float hs) {
    float s=(hs>.75)?0.:(hs>.5)?2.:(hs>.25)?1.:3.;
    return s;
}

vec3 checkColor(float sd, float fc, vec4 hash) {
    float ch = side(hash[int(fc)]);

    if(sd==0.&&ch==1.||sd==1.&&ch==0.) ch=8.;
    if(sd==0.&&ch==3.||sd==3.&&ch==0.) ch=7.;
    if(sd==0.&&ch==2.||sd==2.&&ch==0.) ch=6.;

    if(sd==1.&&ch==3.||sd==3.&&ch==1.) ch=14.;
    if(sd==1.&&ch==2.||sd==2.&&ch==1.) ch=9.;   

    if(sd==2.&&ch==3.||sd==3.&&ch==2.) ch=5.;

    if(sd==ch) ch=sd;

    return vec3(1./ch);
}

vec4 gethood(vec2 id, inout float hs) {
    vec4 nh = vec4(0);
    hs = fbm3(vec3(id,T*2.5));
    for(int i=0;i<4;i++){
        nh[i]=fbm3(vec3(id-myhood[i],T*2.5));
    }
    return nh;
}

void getScreen(inout vec3 C, vec2 p3) {
    vec2 uv=p3;

    //screen
    float srrn = box(uv-vec2(0.,.115),screenSize*.99)-.015;
    srrn=smoothstep(px,-px,srrn);

    float px =scale/R.x;
    vec3 clr = vec3(1.);
    uv+=vec2(.0,.1)*T;
    uv-=vec2((M.x/R.x*PI)*1.-.5,(M.y/R.y*PI)*1.-.5);

    vec2 id= floor(uv*scale);
    vec2 p = fract(uv*scale)-.5;

    float hs = 0.;
    vec4  gs = gethood(id, hs);

    float d1 = box(p,vec2(hlfsc*.89))-.075;
    if(hs>.25){
        float d2 = length(p)-(hs*.3);
        d2=abs(d2)-.02;
        d2=smoothstep(px,-px,d2);
    }

    float sd = side(hs);
    d1=smoothstep(px,-px,d1);
    C=mix(C,C*hue(sd),min(d1,srrn));

    float sz = .85;

    for(int i=0;i<4;i++){

        vec2 tuv = p+vec2(mypos[i]);
        vec2 bs = i==1||i==3 ? vec2(hlfsc*sz,hlfsc*.25) : vec2(hlfsc*.25,hlfsc*sz);
        d1=box(tuv,bs);

        float s1 = box(tuv-vec2(-.05,.05),bs);
        s1=smoothstep(.15-px,-px,s1);

        float od1 = abs(d1)-.015;
        d1=smoothstep(px,-px,d1);
        clr = checkColor(sd,float(i),gs);

        // only if edges are different draw overlap
        if(side(gs[i])!=sd){
            C=mix(C,C*.5,min(s1,srrn));
            C=mix(C,clr,min(d1,srrn));
            od1=smoothstep(px,-px,od1);
            C=mix(C,vec3(.01),min(od1,srrn));
        }
    }
}

void rainbow( inout vec3 C, vec2 uv) {
    float hs = hash21(uv*sin(uv.x));
    float s1 = length(abs(uv.y+.04))-.02;
    s1=smoothstep(px,-px,s1);
    C=mix(C,vec3(0.576,0.024,0.161)-(hs*.025),s1);

    s1 = length(abs(uv.y))-.02;
    s1=smoothstep(px,-px,s1);
    C=mix(C,vec3(0.576,0.216,0.024)-(hs*.035),s1);

    s1 = length(abs(uv.y-.04))-.02;
    s1=smoothstep(px,-px,s1);
    C=mix(C,vec3(0.678,0.624,0.020)-(hs*.035),s1);

    s1 = length(abs(uv.y-.08))-.02;
    s1=smoothstep(px,-px,s1);
    C=mix(C,vec3(0.184,0.553,0.027)-(hs*.025),s1);

    s1 = length(abs(uv.y-.12))-.02;
    s1=smoothstep(px,-px,s1);
    C=mix(C,vec3(0.027,0.220,0.533)-(hs*.035),s1);
}

void mainImage( out vec4 O, in vec2 F )
{

    vec2 uv = (2.* F.xy-R.xy)/max(R.x,R.y);
    float hs = hash21(uv*sin(uv.x));
    vec3 C = vec3(clamp((uv.y+1.)*.1,.01,1.))-(hs*.015);

    px=2./R.x;

    rainbow(C, uv);
    nanoBody(C, uv);
    getScreen(C,uv);

    if(hs<.65) C = clamp(C-(hs*.005),C,vec3(1));
    C = pow(C, vec3(.4545));
    O = vec4(C,1.);
}

// Texturing
// Zidane Zinedine


const float View = 2800.0;  // View distance
const float Epsilon = 0.01; // Marching epsilon
const int Steps = 400;      // Number of steps

// Lipschitz constant
const float K=1.0;

// Terrain -----------------------------------------------------------------------------------------------

//PLATEAU

float plateau(float h, float e, float a)
{
    if ((h<e)||(h>e+a)){ return h;}
   float u = (h-e)/((e+a)-e);
    float c = u*u;
    h = e + a*c;
    return h;
}

// Intersection, preserve the index of first object
// a, b : field function of left and right sub-trees
float Intersection(float a,float b)
{
    return max(a,b);
}

float Union(float a, float b)
{
    return min(a,b);
}

// Terrain heightfield
// x : Position in the plane
float Terrain( in vec2 p )
{
	return 50.0*sin(p.x/120.0)*cos(p.y/90.0);
} 

float Terre (in vec2 p)
{
    return FbmRidge(p,200.0, 140.0, 9);
}

float mer(in vec2 p)
{
    return FbmRidge(p,20.0, 10.0, 2);
}

// Implicit surface defining the terrain
// p : Point
float ImplicitTerrain(in vec3 p)
{
    float z = Terre( p.xy );
    float h = (p.z - z)/2.0; 
    return h;
}

float ImplicitTerrain1(in vec3 p)
{
    float z = mer( p.xy );
    float h = (p.z - z)/2.0; 
    return h;
}



// Primitive functions -----------------------------------------------------------------------------------

// Sphere 
// p : Point
// c : Center 
// r : Radius
float Sphere(vec3 p, vec3 c,float r)
{
  return length(p-c)-r;
}



// Scene --------------------------------------------------------------------------

// Potential field of the object
// p : point
float Object(vec3 p)
{
  float a = ImplicitTerrain1(p+iTime*vec3(27,27,0));
  float u = Union(ImplicitTerrain(p),a);

  return u;
}

// Analysis of the scalar field --------------------------------------------------------------------------

// Calculate object normal
// p : point
vec3 ObjectNormal(in vec3 p )
{
  float eps = 0.001;
  vec3 n;
    float v = Object(p);
  n.x = Object( vec3(p.x+eps, p.y, p.z) ) - v;
  n.y = Object( vec3(p.x, p.y+eps, p.z) ) - v;
  n.z = Object( vec3(p.x, p.y, p.z+eps) ) - v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// e : Maximum distance 
// h : hit
// s : Number of steps
float SphereTrace(vec3 o, vec3 u, float e,out bool h,out int s)
{
  h = false;

    // Start at the origin
    float t=0.0;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = Object(p);
    // Hit object
      if (v < 0.0)
      {
          s=i;
          h = true;
          break;
      }
      // Move along ray
      t += max(Epsilon,v/K);
      // Escape marched too far away
      if (t>e)
      {
          break;
      }
  }
  return t;
}



// Shading and lighting ---------------------------------------------------------------------------


vec3 TextureWater(in vec3 p)
{
  return vec3(0.52,0.75,0.91);   
}

vec3 TextureRock(in vec3 p)
{
  return mix(vec3(0.92,0.91,0.90),vec3(0.74,0.72,0.72),Fbm(p/50.0));   
}


vec3 TextureBorder(in vec3 p)
{
    return vec3(0.25,0.24,0.24);   
}

// Compute color 
// p : Point
// n : Normal
vec3 Color(in vec3 p,in vec3 n)
{
    return TextureBorder(p);
}

// Rendering 
// ro, rd : Ray origin and direction
// pip : Picture in picture boolean
vec4 Render( in vec3 ro, in vec3 rd, bool pip )
{
    // Light direction
    vec3 light1 = normalize( vec3(-0.8,-0.3,0.4) );


	vec3 col;
    int it;
    bool b;
    float t = SphereTrace( ro, rd, View,b , it);
	vec3 p = ro + t*rd;
    float vp= Object(p);
    vec3 n = ObjectNormal( p );


    col = Color(p,n);
    // Sky
    if( b==false )
    {
        col = vec3(0.35,0.65,0.95) - rd.z*rd.z*2.5;
    }
	else 
	{
        vec3 ref = reflect( rd, n );
        float fre = clamp( 1.0+dot(rd,n), 0.0, 1.0 );
        vec3 hal = normalize(light1-rd);


         // Pseudo diffuse lighting
		float dif = 0.5*(1.0+dot( light1, n ));
        dif*=dif;

        col = 0.45*col+0.55*dif*vec3(0.35,0.35,0.35);
	}

    // Shading for iterations
    if (pip==true)
    {
    	float s=float(it)/float(Steps);
		return vec4(0.112+0.824*s,0.418+0.556*s,0.611-0.515*s,t);
    }
    else
    {
    	// Gamma with square root
    	return vec4( sqrt(col), t );
    }
}

vec2 RayDirection(in vec2 pixel, out bool pip)
{
    // Pixel coordinates
    vec2 p = (-iResolution.xy + 2.0*pixel)/iResolution.y;
   if (pip==true)
   {    
    const float fraction=1.0/4.0;
    // Picture in picture
    if ((pixel.x<iResolution.x*fraction) && (pixel.y<iResolution.y*fraction))
    {
        p=(-iResolution.xy*fraction + 2.0*pixel)/(iResolution.y*fraction);
        pip=true;
    }
       else
       {
           pip=false;
       }
   }
   return p;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   // Time as function of the position of the mouse
    float a = 3.14*2.0*1.5*iMouse.x/iResolution.x;

    // Camera transform
    vec3 ro; 
    mat3 cam = Camera( a, ro);   

    // Picture in picture
    bool pip=true;

    // Pixel coordinates
    vec2 p = RayDirection(fragCoord, pip);

    // Camera ray    
    vec3 rd = cam * normalize(vec3(p,5.0));

    // Render
    vec4 res = Render( ro, rd, pip );

    fragColor = vec4( res.xyz, 0.0 );

}

