// Texturing
// Zinedine Zidane

// Texturing and noise ---------------------------------------------------------

// Hashing function
// Returns a random number in [-1,1]
// p : Vector in space
float Hash(in vec3 p)  
{
    p  = fract( p*0.3199+0.152 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

// Procedural value noise with cubic interpolation
// x : Point 
float Noise(in vec3 p)
{
    vec3 i = floor(p);
    vec3 f = fract(p);

    f = f*f*(3.0-2.0*f);
    // Could use quintic interpolation instead of cubic
    // f = f*f*f*(f*(f*6.0-15.0)+10.0);

    return mix(mix(mix( Hash(i+vec3(0,0,0)), 
                        Hash(i+vec3(1,0,0)),f.x),
                   mix( Hash(i+vec3(0,1,0)), 
                        Hash(i+vec3(1,1,0)),f.x),f.y),
               mix(mix( Hash(i+vec3(0,0,1)), 
                        Hash(i+vec3(1,0,1)),f.x),
                   mix( Hash(i+vec3(0,1,1)), 
                        Hash(i+vec3(1,1,1)),f.x),f.y),f.z);
}

// Hashing function
// Returns a random number in [-1,1]
// p : Vector in the plane
float Hash(in vec2 p)  
{
    p  = fract( p*0.3199+0.152 );
	p *= 17.0;
    return fract( p.x*p.y*(p.x+p.y) );
}


// Procedural value noise with cubic interpolation
// x : Point 
float Noise(in vec2 p)
{
    vec2 i = floor(p);
    vec2 f = fract(p);

    f = f*f*(3.0-2.0*f);
    // Could use quintic interpolation instead of cubic
    // f = f*f*f*(f*(f*6.0-15.0)+10.0);

    return mix(mix( Hash(i+vec2(0,0)), 
                        Hash(i+vec2(1,0)),f.x),
                   mix( Hash(i+vec2(0,1)), 
                        Hash(i+vec2(1,1)),f.x),f.y);
}

// Fractal brownian motion with 4 frequencies
// p : Point
float Fbm(in vec3 p)
{
    return Noise(p)/2.0+Noise(p*2.0)/4.0+Noise(p*4.0)/8.0+Noise(p*8.0)/16.0;
}

float Fbmc(vec2 p, float l, float a, int d)
{
    float t=0.0;
        for(int i = 0; i<0; i++)
        {
            t+= a*Noise(p/l);
            a*=0.5;
            l*=0.5;
        }
    return t;
}

float ridge (vec2 p)
{
     float c = 1.0-2.0*abs(Noise(p));
     return c;
}

float FbmRidge(vec2 p, float l, float a, int d)
{
    float t = 0.0;
    for(int i = 1; i<d; i++)
    {
        t += a*ridge(p/l);
        a *= 0.5; l *= 0.5;
    }
    return t;
}


float HGRF(vec2 p, float l, float a, int d, float amin, float amax)
{
    float t = a*ridge(p/l);
    for(int i = 1; i<0; i++)
    {
        a*=0.5; l*0.5;
        float att=smoothstep(amin, amax, t);
        t+= att*a*ridge(p/l);
    }
    return t;
}


// Camera
// a: Rotation angle around z axis
// ro : Ray origin
mat3 Camera(float a, out vec3 ro)
{
	float range=1350.0;
    // Origin
    ro = vec3(range*cos(a),range*sin(a),range/4.0);

    // Target
    vec3 ta = vec3(0.0,0.0,0.0);

	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(0.0, 0.0,1.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

